// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'
import DatePicker from 'react-datepicker'
import 'moment/locale/es'
import moment from 'moment'
import _isEqual from 'lodash/isEqual'
import _isNil from 'lodash/isNil'
import _isString from 'lodash/isString'

// Components
import { MONTHS } from 'shared/catalogs'

const customLocale = {
  monthsShort: MONTHS.map(m => m.label.substr(0, 3)),
  months: MONTHS.map(m => m.label)
}

moment.locale('es')
moment.updateLocale('es', customLocale)

// Error messages
const ERROR_TXT = {
  REQUIRED: 'Este campo es requerido'
}

class Datepicker extends Component {
  constructor (props) {
    super(props)
    this.data = {
      value: moment(props.value),
      isValid: true,
      errorText: '',
      validateOnChange: false
    }
    this.state = Object.assign({}, this.data)
    this.onChange = this.onChange.bind(this)
    this.onBlur = this.onBlur.bind(this)
    this._handleKeyDown = this._handleKeyDown.bind(this)
    this.validate = this.validate.bind(this)
  }

  componentDidUpdate () {
    this.changeControlledDate()
  }

  changeControlledDate = () => {
    if (!_isEqual(this.props.value, this.state.value)) {
      this.setState({ value: this.props.value })
    }
  }

  onBlur () {
    this.validate()
    if (!this.state.validateOnChange) this.setState({ validateOnChange: true })
    this.props.onBlur()
  }

  // This doesn't really work, react datepicker takes ownership of the keyDown event
  _handleKeyDown (e) {
    if (e.key === 'Enter') {
      this.props.onEnter()
    }
  }

  // When the field changes, it updates the value locally and calls the passed callback function
  // so it can also update values on the parent component
  onChange (e) {
    if (!e) return
    this.setState({ value: e }, () => {
      if (this.state.validateOnChange) this.validate()
    })
    this.props.onChange(e)
  }

  // Validates if selected value matches the requirements.
  // Sets on state the validation result and error message
  // Returns boolean with the validation result.
  validate () {
    const { required } = this.props
    const { value } = this.state
    let isValid = !required
    let errorText = ''
    if (required) {
      isValid = moment(value).isValid()
      errorText = isValid ? '' : ERROR_TXT.REQUIRED
    }
    this.setState({ isValid, errorText })
    return isValid
  }

  render () {
    const {
      label,
      includeDates,
      required,
      className,
      placeholder,
      disabled,
      startDate,
      endDate,
      onClick,
      maxDate,
      minDate
    } = this.props
    const { isValid, errorText } = this.state
    let { value } = this.state
    if (_isNil(value)) value = moment()

    return (
      <div>
        {label && (
          <label className='caption'>{`${label} ${required ? '*' : ''}`}</label>
        )}
        <div className='control has-icons-left'>
          <DatePicker
            className={classNames('input ' + className, {
              'is-danger': !isValid
            })}
            placeholderText={placeholder}
            disabled={disabled}
            selected={_isString(value) ? moment(value) : value}
            includeDates={includeDates}
            onChange={this.onChange}
            onBlur={this.onBlur}
            onKeyDown={this._handleKeyDown}
            startDate={startDate}
            endDate={endDate}
            dateFormat='DD-MMM-YYYY'
            fixedHeight
            locale='es'
            onFocus={e => {
              onClick(e)
            }}
            maxDate={maxDate}
            minDate={minDate}
          />
          <span className='icon is-left'>
            <i className='mdi mdi-calendar' />
          </span>
          <span
            className={classNames('help is-danger', { 'is-hidden': isValid })}
          >
            {errorText || ''}
          </span>
        </div>
      </div>
    )
  }
}

export default Datepicker

Datepicker.propTypes = {
  value: PropTypes.oneOfType([PropTypes.string, PropTypes.object]),
  includeDates: PropTypes.array,
  onChange: PropTypes.func,
  label: PropTypes.string,
  className: PropTypes.string,
  disabled: PropTypes.bool,
  required: PropTypes.bool,
  onBlur: PropTypes.func,
  onEnter: PropTypes.func,
  onClick: PropTypes.func,
  placeholder: PropTypes.string,
  startDate: PropTypes.object,
  endDate: PropTypes.object,
  maxDate: PropTypes.string,
  minDate: PropTypes.string
}

Datepicker.defaultProps = {
  value: moment(),
  includeDates: null,
  className: '',
  disabled: false,
  required: false,
  onChange: () => {},
  onBlur: () => {},
  onEnter: () => {},
  onClick: () => {},
  startDate: moment(),
  endDate: moment(),
  minDate: ''
}
