// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { Link, browserHistory } from 'react-router'
import DocumentTitle from 'react-document-title'
import AppSignal from 'appsignal'

// Componets
import Logo from 'shared/logo'
import TextField from 'shared/textField/index'
import Button from 'shared/button'
import API from 'api'
import SessionActions from 'actions/session'

// Keys
const EMAIL_KEY = 'email'
const PASSWORD_KEY = 'password'
const TOKEN_KEY = 'token'

const requiredFields = new Set()

class FirstPassword extends Component {
  constructor (props) {
    super(props)
    this.data = {
      requestInProgress: false,
      [EMAIL_KEY]: '',
      [PASSWORD_KEY]: ''
    }
    this.state = Object.assign({}, this.data)
  }

  componentWillUnmount () {
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  // Update state fields
  onChange (field, e) {
    this.setState({ [field]: e.target.value })
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validateAll () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate.call(this)) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  // Send your new password
  async changePassword (token) {
    if (this.validateAll()) {
      this.setState({ requestInProgress: true })

      try {
        await API.User.FirstPassword({
          [TOKEN_KEY]: token,
          [PASSWORD_KEY]: this.state[PASSWORD_KEY]
        })

        if (this.props.location.query[EMAIL_KEY]) {
          await SessionActions.login({
            email: this.props.location.query[EMAIL_KEY],
            password: this.state[PASSWORD_KEY]
          })
        }

        this.setState({ requestInProgress: false })
        browserHistory.push('/bienvenida')
      } catch (error) {
        AppSignal.sendError(error)
        this.setState({ requestInProgress: false })
      }
    }
  }

  // Render the recover password inputs
  renderInvalidToken () {
    return (
      <div className='content'>
        <div>
          <p className='unselectable text-align-center'>
            <strong>Token inválido</strong>
          </p>
          <p className='control'>
            <Link to='/'>
              <Button buttonStyle='link' className='is-fullwidth'>
                Ir al inicio
              </Button>
            </Link>
          </p>
        </div>
      </div>
    )
  }

  // Render form for wrigin your new password
  renderNewPassword () {
    let renderNewPassword = null
    if (typeof this.props.location.query[TOKEN_KEY] !== 'undefined') {
      renderNewPassword = (
        <div className='content'>
          <div className='espacio-medium'>
            <p>Ingresa tu nueva contraseña</p>
            <TextField
              type='password'
              placeholder='Contraseña'
              value={this.state[PASSWORD_KEY]}
              onChange={this.onChange.bind(this, [PASSWORD_KEY])}
              required
              ref={r => requiredFields.add(r)}
            />
          </div>
          <p className='control'>
            <Button
              buttonStyle='primary'
              className='is-fullwidth'
              onClick={this.changePassword.bind(
                this,
                this.props.location.query[TOKEN_KEY]
              )}
              loading={this.state.requestInProgress}
            >
              Guardar
            </Button>
          </p>
        </div>
      )
    }
    return renderNewPassword
  }

  // Main render
  render () {
    let render =
      typeof this.props.location.query[TOKEN_KEY] !== 'undefined'
        ? this.renderNewPassword()
        : this.renderInvalidToken()
    return (
      <DocumentTitle title='Restablecer tu contraseña - Enconta'>
        <div className='login-page'>
          <div className='login-box__background-vectors' />
          <div className='login-box'>
            <Logo textColour='dark' />
            {render}
          </div>
        </div>
      </DocumentTitle>
    )
  }
}

FirstPassword.propTypes = {
  location: PropTypes.shape({
    query: PropTypes.object
  })
}

export default FirstPassword
