// Libraries
import React, { Component, Fragment } from 'react'
import { Link, browserHistory } from 'react-router'
import { formatFile } from 'utils'
import classNames from 'classnames'

// Components
import EmptyState from 'shared/emptyState'
import DropZoneFile from 'shared/credentials/components/DropzoneFile'
import Button from 'shared/button'

// Actions
import NotificationsActions from 'actions/notifications'
import InventoriesActions from 'actions/inventories'

// Stores
import InventoriesStore from 'stores/inventories'

export const createProductImport = ({ file }) => ({
  product_import: {
    preview: true,
    attached_file: file.file64
  }
})

const getCurrentState = () => {
  return {
    hasProducts: InventoriesStore.getHasProducts()
  }
}
export default class MassiveUploadProducts extends Component {
  state = {
    file: {
      name: '',
      file64: '',
      contentType: ''
    },
    requestInProgress: false,
    hasProducts: false,
    ...getCurrentState()
  }
  /**
   * @param {File[] | undefined} file
   */
  onDropFile = async file => {
    if (!file) return
    this.setState({ file: await formatFile(file) })
  }

  sendInventoryFile = () => {
    this.setState({ requestInProgress: true })
    InventoriesActions.analyzeProducts(createProductImport(this.state))
      .then(() => {
        NotificationsActions.success('Archivo analizado correctamente')
        this.setState({ requestInProgress: false })
        browserHistory.push('/inventarios/vista-previa')
      })
      .catch(error => {
        NotificationsActions.error(error)
        this.setState({ requestInProgress: false })
      })
  }

  render () {
    const { name } = this.state.file
    const { requestInProgress, hasProducts } = this.state
    return (
      <Fragment>
        {!hasProducts && (
          <EmptyState
            icon={'cube-scan'}
            mainText={'No contamos con productos registrados'}
          />
        )}

        <div className='columns is-centered'>
          <div className='column is-6'>
            <form className='massive-products-form'>
              <p className='body body--dark-greay-hover'>
                Para registrar varios productos a la vez sigue las siguientes
                indicaciones:
              </p>
              <ol className='massive-products-form__instructions'>
                <li>
                  <Link
                    to={
                      'https://s3-us-west-2.amazonaws.com/enconta-public/environment/templates/productos.xlsx'
                    }
                    className='is-uppercase link--underline link--underline--color-blue'
                    download
                    target='_blank'
                  >
                    <i
                      className='mdi mdi-file-table icon-aligned--margin-right'
                      aria-hidden='true'
                    />
                    Descargar excel
                  </Link>
                </li>
                <li>
                  Registra la informaci√≥n de tus productos de acuerdo a las
                  indicaciones.
                </li>
                <li>
                  <p>Carga el archivo actualizado.</p>
                  <DropZoneFile
                    className={classNames(
                      'is-fullwidth dropzone--has-margin-bottom',
                      {
                        approved: name !== ''
                      }
                    )}
                    accept='.xlsx'
                    onDrop={file => this.onDropFile(file)}
                    file={name}
                  />
                </li>
                <li>
                  Guarda los productos que fueron procesados correctamente.
                </li>
              </ol>
              <Button
                type='button'
                buttonStyle='primary'
                className='massive-products-form__send-button'
                icon='send'
                disabled={requestInProgress || name === ''}
                loading={requestInProgress}
                onClick={this.sendInventoryFile}
              >
                Enviar
              </Button>
            </form>
          </div>
        </div>
      </Fragment>
    )
  }
}
