// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import uuid from 'uuid'
import { Link } from 'react-router'
import AppSignal from 'appsignal'

// Components
import API from 'api'
import Button from 'shared/button'
import Card from 'shared/card'
import NotificationsActions from 'actions/notifications'

// Keys
const EMAIL_CREATED_AT_KEY = 'created_at'
const EMAIL_EMAILED_INVOICES_KEY = 'emailed_invoices'
const EMAIL_ERROR_MESSAGE_KEY = 'error_message'
const EMAIL_ID_KEY = 'id'
const EMAIL_MESSAGE_KEY = 'message'
const EMAIL_TO_KEY = 'to'

// Labels
const EMAIL_LABELS = {
  [EMAIL_CREATED_AT_KEY]: 'Fecha de envÃ­o',
  [EMAIL_EMAILED_INVOICES_KEY]: 'Facturas enviadas',
  [EMAIL_ERROR_MESSAGE_KEY]: 'Mensaje de error',
  [EMAIL_ID_KEY]: 'ID del correo',
  [EMAIL_MESSAGE_KEY]: 'ID del mensaje',
  [EMAIL_TO_KEY]: 'Enviado a'
}

// Render sigle item
const renderDetail = (name, parent, item) => {
  return typeof parent[item] !== 'undefined' && parent[item] !== null ? (
    <p key={uuid()}>
      <span className='caption'>{name}</span>
      <br />
      <span className='body'>{parent[item]}</span>
    </p>
  ) : null
}

class FacturaEnviada extends Component {
  constructor (props) {
    super(props)
    this.data = {
      email: {
        [EMAIL_CREATED_AT_KEY]: '',
        [EMAIL_EMAILED_INVOICES_KEY]: [],
        [EMAIL_ERROR_MESSAGE_KEY]: '',
        [EMAIL_ID_KEY]: 0,
        [EMAIL_MESSAGE_KEY]: '',
        [EMAIL_TO_KEY]: ''
      }
    }
    this.state = Object.assign({}, this.data)
  }

  // Load data on component's mount
  componentDidMount () {
    this.loadData()
  }

  // Load email data
  loadData () {
    API.SentEmails.Show(this.props.params.id)
      .then(r => {
        this.setState({
          requestInProgress: false,
          email: r.email
        })
      })
      .catch(error => {
        AppSignal.sendError(error)
        NotificationsActions.error(error)
      })
  }

  // Render simple details
  renderDetails () {
    const details = [
      EMAIL_CREATED_AT_KEY,
      EMAIL_TO_KEY,
      EMAIL_ID_KEY,
      EMAIL_ERROR_MESSAGE_KEY,
      EMAIL_MESSAGE_KEY
    ]
    return (
      <div className='content'>
        {details.map(d => renderDetail(EMAIL_LABELS[d], this.state.email, d))}
      </div>
    )
  }

  // Render single invoice
  renderInvoice (invoice) {
    return (
      <div key={uuid()} className='content'>
        <span className='caption'>Emitido a</span>
        <br />
        <p className='body'>
          <Link to={`/facturas/ver/${invoice['invoice_id']}`}>
            {invoice['company_name']}
          </Link>
        </p>
      </div>
    )
  }

  // Render list of invoices sent
  renderInvoices () {
    let invoices =
      this.state.email[EMAIL_EMAILED_INVOICES_KEY].length > 0 &&
      this.state.email[EMAIL_EMAILED_INVOICES_KEY].map(invoice =>
        this.renderInvoice(invoice)
      )
    return <div className='espacio-medium'>{invoices}</div>
  }

  // Main render
  render () {
    return (
      <div>
        <h1 className='headline'>Factura</h1>
        <Card>
          <h2 className='subheader-green'>Detalles del envÃ­o</h2>
          {this.renderDetails()}
          <h2 className='subheader-green'>Facturas enviadas</h2>
          {this.renderInvoices()}
          <Link to='/emails/'>
            <Button icon='arrow-left' buttonStyle='white'>
              Regresar
            </Button>
          </Link>
        </Card>
      </div>
    )
  }
}

FacturaEnviada.propTypes = {
  params: PropTypes.shape({
    id: PropTypes.string
  })
}

export default FacturaEnviada
