import React, { Component } from 'react'
import { Link } from 'react-router'
import classNames from 'classnames'

import Button from 'shared/button'

/*
 *
 */

class InvoiceConcepts extends Component {
  constructor (props) {
    super(props)
    this.data = {
      invoiceConcepts: []
    }
    this.state = Object.assign({}, this.data)
  }

  componentDidMount () {
    this.loadData()
  }

  loadData () {
    console.log('LOG: Load Data Invoice Concepts')
    this.setState({
      invoiceConcepts: [
        {
          id: 0,
          name: 'Hola q23saiofiasd asdoifjasdf',
          kind: 'income',
          category: '100.01 Activo a corto plazo'
        },
        {
          id: 1,
          name: 'Hola q23saiofiasd asdoifjasdf',
          kind: 'income',
          category: '100.01 Activo a corto plazo'
        }
      ]
    })
  }

  renderNewButton (right) {
    return (
      <p className={classNames('control', { 'is-pulled-right': right })}>
        <Link to='/admin/rubros/nuevo'>
          <Button buttonStyle='primary' icon='plus'>
            Nuevo rubro
          </Button>
        </Link>
      </p>
    )
  }

  renderTable () {
    let renderTable = (
      <div className='content'>
        <div className='block unselectable'>
          <p>No hay rubros</p>
        </div>
        {this.renderNewButton()}
      </div>
    )
    if (this.state.invoiceConcepts.length > 0) {
      let invoiceConcepts = this.state.invoiceConcepts.map(function (
        concept,
        index
      ) {
        return (
          <tr key={concept.id}>
            <th>{concept.category}</th>
            <th>{concept.name}</th>
            <td>
              <p className='control table--actions'>
                <Link to={'/admin/rubros/editar/' + concept.id}>
                  <span className='with-tooltip'>
                    <span className='tooltip'>Editar</span>
                    <i className='mdi mdi-pencil' aria-hidden='true' />
                  </span>
                </Link>
              </p>
            </td>
          </tr>
        )
      })
      renderTable = (
        <div>
          <table className='table'>
            <thead>
              <tr>
                <th className='is-unselectable cursor-default'>
                  Categorías SAT
                </th>
                <th className='is-unselectable cursor-default'>Rubros</th>
                <th className='is-unselectable cursor-default'>Acciones</th>
              </tr>
            </thead>
            <tbody>{invoiceConcepts}</tbody>
          </table>
          {this.renderNewButton()}
        </div>
      )
    }
    return renderTable
  }

  render () {
    return (
      <div>
        <h1 className='headline'>Conceptos</h1>
        {this.renderNewButton('right')}
        <hr />
        {this.renderTable()}
      </div>
    )
  }
}

export default InvoiceConcepts
