// Components
import { TAX_KIND } from 'shared/catalogs'

// Tax Detail keys
const TAX_RETAINED_KEY = 'li_retained_tax_mgs'
const TAX_TRANSFERRED_KEY = 'li_transferred_tax_mgs'
const TAX_TAX_NAME_KEY = 'tax_name'
const TAX_AMOUNT_CENTS_KEY = 'amount_cents'
const TAX_RATE_KEY = 'rate'
const TAX_FACTORY_TYPE_KEY = 'factory_type'
const TAX_BASE_KEY = 'base'

const RECIPE_DEFAULT_TAX = TAX_KIND[0]
const TAXES_BY_NAME = {
  ISR: { id: '001', retained: true, translated: false, reach: 'federal' },
  IVA: { id: '002', retained: true, translated: true, reach: 'federal' },
  IEPS: { id: '003', retained: true, translated: true, reach: 'federal' }
}

// Tax Recipes
class TaxRecipes {
  // Single taxes (taxes inside retained or transferred objects)
  static SingleTax (taxBase) {
    const taxRate = 16
    return {
      [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.IVA['id'],
      [TAX_AMOUNT_CENTS_KEY]: taxRate * 10,
      [TAX_RATE_KEY]: taxRate,
      [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['label'],
      [TAX_BASE_KEY]: taxBase
    }
  }
  // Venta más IVA
  static SaleTax (base) {
    const taxRate = 16
    const taxRateCents = 0.16
    return {
      [TAX_RETAINED_KEY]: [],
      [TAX_TRANSFERRED_KEY]: [
        {
          [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.IVA['id'],
          [TAX_AMOUNT_CENTS_KEY]: base * taxRateCents,
          [TAX_RATE_KEY]: taxRate,
          [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['id'],
          [TAX_BASE_KEY]: base
        }
      ]
    }
  }
  // Venta IVA incluído
  static SaleWithTaxTax (base) {
    const taxRate = 16
    const taxRateCents = 0.16
    return {
      [TAX_RETAINED_KEY]: [],
      [TAX_TRANSFERRED_KEY]: [
        {
          [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.IVA['id'],
          [TAX_AMOUNT_CENTS_KEY]: base - base / (1 + taxRateCents),
          [TAX_RATE_KEY]: taxRate,
          [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['id'],
          [TAX_BASE_KEY]: base
        }
      ]
    }
  }
  // Honorarios
  static FeesTax (base) {
    const taxRate = 16
    const taxRateCents = 0.16
    return {
      [TAX_RETAINED_KEY]: [
        {
          [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.IVA['id'],
          [TAX_AMOUNT_CENTS_KEY]: base * 0.106666,
          [TAX_RATE_KEY]: 10.6666,
          [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['id'],
          [TAX_BASE_KEY]: base
        },
        {
          [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.ISR['id'],
          [TAX_AMOUNT_CENTS_KEY]: base * 0.1,
          [TAX_RATE_KEY]: 10,
          [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['id'],
          [TAX_BASE_KEY]: base
        }
      ],
      [TAX_TRANSFERRED_KEY]: [
        {
          [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.IVA['id'],
          [TAX_AMOUNT_CENTS_KEY]: base * taxRateCents,
          [TAX_RATE_KEY]: taxRate,
          [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['id'],
          [TAX_BASE_KEY]: base
        }
      ]
    }
  }
  // Arrendamiento
  static LeaseTax (base) {
    const taxRate = 16
    const taxRateCents = 0.16
    return {
      [TAX_RETAINED_KEY]: [
        {
          [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.IVA['id'],
          [TAX_AMOUNT_CENTS_KEY]: base * 0.106666,
          [TAX_RATE_KEY]: 10.6666,
          [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['id'],
          [TAX_BASE_KEY]: base
        },
        {
          [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.ISR['id'],
          [TAX_AMOUNT_CENTS_KEY]: base * 0.1,
          [TAX_RATE_KEY]: 10,
          [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['id'],
          [TAX_BASE_KEY]: base
        }
      ],
      [TAX_TRANSFERRED_KEY]: [
        {
          [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.IVA['id'],
          [TAX_AMOUNT_CENTS_KEY]: base * taxRateCents,
          [TAX_RATE_KEY]: taxRate,
          [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['id'],
          [TAX_BASE_KEY]: base
        }
      ]
    }
  }
  // Comisiones
  static AffiliatesTax (base) {
    const taxRate = 16
    const taxRateCents = 0.16
    return {
      [TAX_RETAINED_KEY]: [
        {
          [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.IVA['id'],
          [TAX_AMOUNT_CENTS_KEY]: base * 0.106666,
          [TAX_RATE_KEY]: 10.6666,
          [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['id'],
          [TAX_BASE_KEY]: base
        }
      ],
      [TAX_TRANSFERRED_KEY]: [
        {
          [TAX_TAX_NAME_KEY]: TAXES_BY_NAME.IVA['id'],
          [TAX_AMOUNT_CENTS_KEY]: base * taxRateCents,
          [TAX_RATE_KEY]: taxRate,
          [TAX_FACTORY_TYPE_KEY]: RECIPE_DEFAULT_TAX['id'],
          [TAX_BASE_KEY]: base
        }
      ]
    }
  }
  // Personalizada
  static OtherTax () {
    return {
      [TAX_RETAINED_KEY]: [],
      [TAX_TRANSFERRED_KEY]: []
    }
  }
}

export default TaxRecipes
