// Libraries
import React, { Component } from 'react'
import { browserHistory, Link } from 'react-router'
import accounting from 'accounting'
import classNames from 'classnames'
import { resetScroll } from 'utils'
// Components
import Card from 'shared/card'
import { STEPS_PERIOD, STEPS_PERIOD_NUMBER } from 'shared/catalogs'
import Paginator from 'shared/paginator'
import Modal from 'shared/modal'

// Stores
import AccountingPeriodsStore from 'stores/accountingPeriods'
// Acctions
import AccountingPeriodsActions from 'actions/accountingPeriods'

import moment from 'moment'
import pluralize from 'pluralize'

const STEPS = [
  'received_account_statement',
  'accounting_done',
  'accounting_accepted',
  'tax_statement_done'
]

/**
 *
 * @param {AccountingPeriod} period
 */
const formatPeriod = ({ month, year, term_type: type }) => {
  switch (type) {
    case 'monthly':
      return moment({ month: month - 1, year }).format('MMMM YYYY')
    case 'bimonthly':
      const initMonth = moment({ month: month - 1, year }).format('MMMM')
      const lastMonth = moment({ month: month, year }).format('MMMM YYYY')
      return `${initMonth}-${lastMonth}`
    default:
      break
  }
}

function getCurrentState () {
  const accountingPeriods = AccountingPeriodsStore.getAccountingPeriods()
  const totalPages = AccountingPeriodsStore.getTotalPages()
  const totalItems = AccountingPeriodsStore.getTotalItems()
  return {
    accountingPeriods,
    totalItems,
    totalPages
  }
}
/**
 * @typedef {import('api').AccountingPeriod} AccountingPeriod
 * @typedef {import('api').Pagination} Pagination
 * @typedef {{}} Props
 * @typedef {{accountingPeriods: AccountingPeriod[], pagination: Pagination | null}} State

 * @extends {Component<Props, State>}
 */
export default class AccountingPeriods extends Component {
  /**
   * @type {State}
   */
  state = { ...getCurrentState(), referralPromoActive: true }
  /**
   *  Call accountiong periods list
   *  And evaluate if needs to show the referral promo
   */

  componentDidMount () {
    // Adds listener, when the Store changes, trigger the _onChange callback func
    AccountingPeriodsStore.addChangeListener(this._onChange)
    this.loadData()
    this.handleReferralPromo()
    resetScroll()
  }

  componentDidUpdate (prevProps) {
    if (this.props.location.search !== prevProps.location.search) {
      this.loadData()
    }
  }

  componentWillUnmount () {
    // Removes the listener to avoid memory leaks
    AccountingPeriodsStore.removeChangeListener(this._onChange)
  }

  _onChange = () => {
    // When the store changes, the state goes to it and pulls the updated data
    // then sets it so the component rerender
    const state = getCurrentState()
    this.setState(state)
  }

  /**
   * It shows the promotion only the first 15 days of month
   */
  handleReferralPromo = () => {
    const currentDay = moment().date()
    if (currentDay > 15) {
      this.setState({ referralPromoActive: false })
    }
  }
  // Request email list page to the API
  loadData (props = this.props) {
    const params = Object.assign({}, props.location.query)

    AccountingPeriodsActions.getAccoutingPeriods(params)
  }

  goPeriod (periodID) {
    browserHistory.push(`/mi-contabilidad/${periodID}/ver`)
  }

  toggleReferralPromo = () => {
    this.setState(state => ({ referralPromoActive: !state.referralPromoActive }))
  }

  renderReferralPromo () {
    const { referralPromoActive } = this.state
    return (
      <Modal
        isActive={referralPromoActive}
        toggleModal={this.toggleReferralPromo}
        noFooter
        isAlert
      >
        <div className='columns referral-promo'>
          <div className='column is-12'>
            <p className='referral-promo__title brake-line'>
              <span>Ahorra una mensualidad con nuestra </span>
              <span className='referral-promo__title--highlight'>
                Promoción de referidos
              </span>
            </p>
            <div className='columns is-centered'>
              <div className='column is-6'>
                <img
                  src={`img/referralPromo.svg`}
                  alt={'Promoción de referidos'}
                  className='referral-promo__img'
                />
              </div>
            </div>
            <p className='referral-promo__subtitle'>¡Es rápido y sencillo!</p>
            <p className='referral-promo__content'>
              Refiere a un conocido y si se vuelve cliente de Enconta te
              regalamos una mensualidad de tu servicio. *
            </p>

            <Link
              to='https://docs.google.com/forms/d/e/1FAIpQLSewxoMXBGAV_rfppnVeUgr5zsW_Oj4aG4LRFEMUrVUnkUl_Lw/viewform?usp=sf_link'
              className='referral-promo__link'
              target={'_blank'}
            >
              Registrar referido
            </Link>
            <p className='caption'>*Aplican restricciones.</p>
          </div>
        </div>
      </Modal>
    )
  }

  // Render table
  renderTable () {
    const { accountingPeriods, totalPages } = this.state
    const { location } = this.props
    if (!accountingPeriods || accountingPeriods.length === 0) {
      return (
        <div className='block unselectable'>
          <p>No hay periodos</p>
        </div>
      )
    }

    return (
      <div>
        <table className='table is-fullwidth'>
          <thead>
            <tr>
              <th className='align-left'>Periodo</th>
              <th className='align-left'>Paso Actual</th>
              <th className='has-text-left'> Estatus</th>
              <th />
            </tr>
          </thead>
          <tbody>
            {accountingPeriods.map(period => {
              const { id, actual_step: currentStep } = period
              return (
                <tr
                  key={id}
                  className='current-month'
                  onClick={() => this.goPeriod(id)}
                >
                  <td className='align-left'>
                    <p className='monthPeriod'>{formatPeriod(period)}</p>
                  </td>
                  <td className='align-left'>
                    <p className='infoPeriods'>
                      {STEPS_PERIOD_NUMBER[currentStep]}
                    </p>
                    <div>
                      {STEPS.map((step, index, array) => {
                        if (step === currentStep) return true

                        return index < array.indexOf(currentStep)
                      }).map((step, i) => (
                        <div
                          key={i}
                          className={classNames(
                            'accountabilityPeriods--periodStep',
                            {
                              'accountabilityPeriods--periodStep-completed': step
                            }
                          )}
                        />
                      ))}
                    </div>
                  </td>
                  <td>
                    <p className='has-text-left'>{STEPS_PERIOD[currentStep]}</p>
                  </td>
                  <td className='align-right'>
                    <p className='accounting-periods-go flex-center'>
                      <i className='mdi mdi-arrow-right mdi-18px' />
                      IR
                    </p>
                  </td>
                </tr>
              )
            })}
          </tbody>
        </table>
        <Paginator totalPages={totalPages} location={location} />
      </div>
    )
  }
  // Main render
  render () {
    const { totalItems } = this.state
    return (
      <div>
        <h1 className='headline'>Mi contabilidad</h1>
        <Card>
          {totalItems && (
            <p className='infoPeriods'>
              <span className='infoPeriods infoPeriods--semibold'>
                {accounting.formatNumber(totalItems)}
              </span>{' '}
              {`${pluralize('periodo', totalItems)} ${pluralize(
                'activo',
                totalItems
              )}`}
            </p>
          )}
          <br />
          {this.renderTable()}
        </Card>
        {this.renderReferralPromo()}
      </div>
    )
  }
}
