// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { browserHistory } from 'react-router'

// Components
import TextField from 'shared/textField'
import Datepicker from 'shared/datepicker'
import SelectField from 'shared/selectField'
import Button from 'shared/button'
import Alert from 'shared/alert'
import moment from 'moment'

// Actions
import EditClientActions from 'actions/editClient'
import NotificationActions from 'actions/notifications'
import PaymentAccountsActions from 'actions/paymentAccounts'

// Stores
import PaymentAccountsStore from 'stores/paymentAccounts'
import EmployeesStore from 'stores/employees'

import {
  COUNTRY_LIST,
  STATE_LIST,
  PAYMENT_SCHEDULES,
  TAX_CLASSIFICATIONS_EMPLOYEE,
  CONTRACT_TYPE,
  WORKDAY_TYPE,
  RISK_CATEGORY,
  PAYROLL_TYPE,
  RESOURCE_ORIGIN
} from 'shared/catalogs'
import { avoidBlankSpace } from 'utils'

// Set where all the required field refs are going to be
// stored so we can validate them all when submit
const requiredFields = new Set()

function dateToString (d) {
  return moment(d).format('YYYY-MM-DD')
}

function getCurrentState () {
  return {
    banksList: PaymentAccountsStore.getBanksList(),
    ...EmployeesStore.getCurrentEmployee()
  }
}

class EditEmployee extends Component {
  constructor () {
    super()
    this.data = {
      requestInProgress: false,
      alertOpen: false,
      banksList: PaymentAccountsStore.getBanksList()
    }
    this.state = Object.assign({}, this.data, getCurrentState())
    this._onChange = this._onChange.bind(this)
  }

  componentDidMount () {
    // Adds listener, when the Store changes, trigger the _onChange callback func
    EmployeesStore.addChangeListener(this._onChange)
    PaymentAccountsStore.addChangeListener(this._onChange)
    // If we are supposed to be editing a document, ask for document info
    if (
      !this.props.location.pathname.includes('/empleados/nuevo') &&
      typeof this.props.params.id !== 'undefined'
    ) {
      this.setState({ isUpdate: true })
      EditClientActions.getEmployeeData(this.props.params.id)
    }

    PaymentAccountsActions.getBanksList().catch(error => {
      NotificationActions.error(error)
    })
  }

  componentWillUnmount () {
    // Removes the listener to avoid memory leaks
    EmployeesStore.removeChangeListener(this._onChange)
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  _onChange () {
    // When the store changes, the state goes to it and pulls the updated data
    // then sets it so the component rerenders
    let state = getCurrentState()
    this.setState(state)
  }

  toggleAlert () {
    this.setState(prevState => ({
      alertOpen: !prevState.alertOpen
    }))
  }

  toggleCashPayment = () => {
    this.setState(
      state => ({ cashPayment: !state.cashPayment }),
      () => {
        requiredFields.delete(null)
      }
    )
  }

  onChangeField (field, e) {
    const value = e._isAMomentObject ? e : e.target.value
    this.setState({ [field]: value })
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validateAll () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate()) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  // Clears the current Document and redirects to the documents listing
  returnToListing () {
    this.setState({ requestInProgress: false })
    EditClientActions.clearEmployee()
    browserHistory.push('/empleados')
  }

  deleteEmployee () {
    const { id } = this.props.params
    EditClientActions.deleteEmployee(id)
      .then(() => {
        NotificationActions.success('El empleado ha sido borrado correctamente')
        this.returnToListing()
      })
      .catch(err => NotificationActions.error(err))
  }

  sendData (e) {
    e.preventDefault()
    // Validates all required fields, then builds the data object using the values stored in the state
    if (this.validateAll() && !this.state.requestInProgress) {
      let data = {
        employee: {
          full_name: this.state.employeeFullName,
          curp: this.state.employeeCurp,
          country: this.state.employeeCountry,
          state: this.state.employeeState,
          employee_number: this.state.employeeEmployeeNumber,
          social_security_number: this.state.employeeSsn,
          tax_classification: this.state.employeeTaxClassification,
          department: this.state.employeeDepartment,
          position: this.state.employeePosition,
          email: this.state.employeeEmail,
          clabe: this.state.employeeClabe,
          rfc: this.state.employeeRfc,
          payment_interval: this.state.employeePaymentInterval,
          hiring_date: dateToString(this.state.employeeHiringDate),
          contract_type: this.state.employeeContractType,
          work_day_type: this.state.employeeWorkdayType,
          risk_category: this.state.employeeRiskCategory,
          payroll_type: this.state.employeePayrollType,
          resource_source: this.state.employeeResourceSource,
          base_salary: this.state.employeeBaseSalary,
          daily_wage: this.state.employeeDailyWage,
          bank_code: this.state.employeeBankCode
        }
      }
      // If the request is for update an existing employee, sets the id and calls the corresponding Action
      // Otherwise, calls the action to create a new one.
      this.setState({ requestInProgress: true }, () => {
        if (this.state.isUpdate) {
          let id = this.props.params.id
          EditClientActions.setEmployeeData(id, data)
            .then(() => {
              NotificationActions.success(
                'El empleado ha sido editado correctamente'
              )
              this.returnToListing()
            })
            .catch(error => {
              if (Array.isArray(error)) {
                NotificationActions.error(error.join('\n'))
              } else {
                NotificationActions.error(error.error)
              }
              this.setState({ requestInProgress: false })
            })
        } else {
          EditClientActions.createEmployee(data)
            .then(() => {
              NotificationActions.success(
                'El empleado ha sido creado correctamente'
              )
              this.returnToListing()
            })
            .catch(error => {
              if (Array.isArray(error)) {
                NotificationActions.error(error.join('\n'))
              } else {
                NotificationActions.error(error.error)
              }
              this.setState({ requestInProgress: false })
            })
        }
      })
    }
  }

  render () {
    const {
      isUpdate,
      cashPayment,
      banksList,
      employeeFullName,
      employeeEmployeeNumber,
      employeeRfc,
      employeeCurp,
      employeeSsn,
      employeeDepartment,
      employeePosition,
      employeeEmail,
      employeeClabe,
      employeeBaseSalary,
      employeeDailyWage
    } = this.state

    return (
      <div>
        <Button
          buttonStyle='link'
          icon='chevron-double-left'
          onClick={this.returnToListing.bind(this)}
        >
          Volver
        </Button>
        <br />
        <br />
        <h4 className='subheader-green'>{this.props.route.title}</h4>
        <div className='columns'>
          <div className='column is-8'>
            <TextField
              label='Nombre Completo'
              value={avoidBlankSpace(employeeFullName)}
              onChange={this.onChangeField.bind(this, 'employeeFullName')}
              required
              ref={r => requiredFields.add(r)}
            />
          </div>
          <div className='column is-4'>
            <TextField
              label='No. de empleado'
              value={avoidBlankSpace(employeeEmployeeNumber)}
              onChange={this.onChangeField.bind(this, 'employeeEmployeeNumber')}
              required
              ref={r => requiredFields.add(r)}
            />
          </div>
        </div>
        <div className='columns'>
          <div className='column'>
            <TextField
              label='RFC'
              value={avoidBlankSpace(employeeRfc)}
              onChange={this.onChangeField.bind(this, 'employeeRfc')}
              required
              ref={r => requiredFields.add(r)}
            />
          </div>
          <div className='column'>
            <TextField
              label='CURP'
              value={avoidBlankSpace(employeeCurp)}
              onChange={this.onChangeField.bind(this, 'employeeCurp')}
              required
              ref={r => requiredFields.add(r)}
            />
          </div>
          <div className='column'>
            <TextField
              label='No. de Seguro Social'
              value={avoidBlankSpace(employeeSsn)}
              onChange={this.onChangeField.bind(this, 'employeeSsn')}
              required
              ref={r => requiredFields.add(r)}
            />
          </div>
        </div>

        <div className='columns'>
          <div className='column'>
            <TextField
              label='Departamento'
              value={avoidBlankSpace(employeeDepartment)}
              onChange={this.onChangeField.bind(this, 'employeeDepartment')}
            />
          </div>
          <div className='column'>
            <TextField
              label='Puesto'
              value={avoidBlankSpace(employeePosition)}
              onChange={this.onChangeField.bind(this, 'employeePosition')}
            />
          </div>
          <div className='column'>
            <TextField
              label='Email'
              type='email'
              value={avoidBlankSpace(employeeEmail)}
              onChange={this.onChangeField.bind(this, 'employeeEmail')}
            />
          </div>
        </div>
        <div className='columns'>
          <div className='column'>
            <SelectField
              label='Estado'
              placeholder='Selecciona'
              options={STATE_LIST}
              optionsConfig={{ id: 'state', label: 'label' }}
              value={this.state.employeeState}
              onChange={this.onChangeField.bind(this, 'employeeState')}
              required
              ref={r => requiredFields.add(r)}
              showId
            />
          </div>
          <div className='column'>
            <SelectField
              label='Pas'
              placeholder='Selecciona'
              options={COUNTRY_LIST}
              value={this.state.employeeCountry}
              onChange={this.onChangeField.bind(this, 'employeeCountry')}
              required
              ref={r => requiredFields.add(r)}
              showId
            />
          </div>
          <div className='column'>
            <Datepicker
              label='Fecha de Ingreso'
              value={this.state.employeeHiringDate}
              onChange={this.onChangeField.bind(this, 'employeeHiringDate')}
              required
              ref={r => requiredFields.add(r)}
            />
          </div>
        </div>
        <hr className='visual-rest visual-rest--margin-16px' />
        <h5 className='body-bold'>Datos de pago</h5>

        <div className='columns'>
          <div className='column'>
            <SelectField
              label='Periodicidad pago'
              placeholder='Selecciona Periodicidad del pago'
              options={PAYMENT_SCHEDULES}
              value={this.state.employeePaymentInterval}
              onChange={this.onChangeField.bind(
                this,
                'employeePaymentInterval'
              )}
              required
              ref={r => requiredFields.add(r)}
              showId
            />
          </div>
          <div className='column'>
            <SelectField
              label='Rgimen'
              placeholder='Selecciona Rgimen Fiscal'
              options={TAX_CLASSIFICATIONS_EMPLOYEE}
              value={this.state.employeeTaxClassification}
              onChange={this.onChangeField.bind(
                this,
                'employeeTaxClassification'
              )}
              required
              ref={r => requiredFields.add(r)}
              showId
            />
          </div>
          <div className='column'>
            <SelectField
              label='Tipo Contrato'
              placeholder='Selecciona Tipo de Contrato'
              options={CONTRACT_TYPE}
              value={this.state.employeeContractType}
              onChange={this.onChangeField.bind(this, 'employeeContractType')}
              required
              ref={r => requiredFields.add(r)}
              showId
            />
          </div>
        </div>
        <div className='columns'>
          <div className='column'>
            <SelectField
              label='Tipo Jornada'
              placeholder='Selecciona Jornada'
              options={WORKDAY_TYPE}
              value={this.state.employeeWorkdayType}
              onChange={this.onChangeField.bind(this, 'employeeWorkdayType')}
              required
              ref={r => requiredFields.add(r)}
              showId
            />
          </div>
          <div className='column'>
            <SelectField
              label='Clase de Riesgo'
              placeholder='Selecciona Clase de Riesgo'
              options={RISK_CATEGORY}
              value={this.state.employeeRiskCategory}
              onChange={this.onChangeField.bind(this, 'employeeRiskCategory')}
              required
              ref={r => requiredFields.add(r)}
              showId
            />
          </div>
          <div className='column'>
            <SelectField
              label='Tipo de nmina'
              placeholder='Tipo de nmina'
              options={PAYROLL_TYPE}
              value={this.state.employeePayrollType}
              onChange={this.onChangeField.bind(this, 'employeePayrollType')}
              required
              ref={r => requiredFields.add(r)}
              showId
            />
          </div>
          <div className='column'>
            <SelectField
              label='Origen del recurso'
              placeholder='Origen del recurso'
              options={RESOURCE_ORIGIN}
              value={this.state.employeeResourceSource}
              onChange={this.onChangeField.bind(this, 'employeeResourceSource')}
              required
              ref={r => requiredFields.add(r)}
              showId
            />
          </div>
        </div>
        <div className='columns'>
          <div className='column'>
            <SelectField
              label='Cdigo del banco'
              placeholder='Selecciona un banco'
              options={banksList}
              value={this.state.employeeBankCode}
              onChange={this.onChangeField.bind(this, 'employeeBankCode')}
              required={!cashPayment}
              disabled={cashPayment}
              ref={r =>
                cashPayment ? requiredFields.delete(r) : requiredFields.add(r)
              }
            />
            <label className='checkbox'>
              <input
                type='checkbox'
                onChange={this.toggleCashPayment}
                checked={cashPayment}
              />
              &nbsp;Pago en efectivo
            </label>
          </div>
          <div className='column'>
            <TextField
              label='CLABE'
              value={avoidBlankSpace(employeeClabe)}
              onChange={this.onChangeField.bind(this, 'employeeClabe')}
              minLength={18}
              type='number'
              required={!cashPayment}
              disabled={cashPayment}
              ref={r =>
                cashPayment ? requiredFields.delete(r) : requiredFields.add(r)
              }
            />
          </div>
          <div className='column'>
            <TextField
              label='Salario base cotizacin'
              type='number'
              value={avoidBlankSpace(employeeBaseSalary)}
              onChange={this.onChangeField.bind(this, 'employeeBaseSalary')}
              required
              ref={r => requiredFields.add(r)}
            />
          </div>
          <div className='column'>
            <TextField
              label='Salario diario integrado'
              type='number'
              value={avoidBlankSpace(employeeDailyWage)}
              onChange={this.onChangeField.bind(this, 'employeeDailyWage')}
              required
              ref={r => requiredFields.add(r)}
            />
          </div>
        </div>
        <br />
        <div className='caption required'>* Campos obligatorios</div>
        <br />
        <div className='flex-end'>
          <Button icon='cancel' onClick={this.returnToListing.bind(this)}>
            Cancelar
          </Button>
          {isUpdate ? (
            <Button
              buttonStyle='danger'
              icon='delete'
              type='button'
              onClick={this.toggleAlert.bind(this)}
            >
              Eliminar Empleado
            </Button>
          ) : null}
          <Button
            buttonStyle='primary'
            icon='content-save'
            onClick={this.sendData.bind(this)}
            loading={this.state.requestInProgress}
          >
            Guardar
          </Button>
        </div>
        <Alert
          isActive={this.state.alertOpen}
          toggleAlert={this.toggleAlert.bind(this)}
          onSubmit={this.deleteEmployee.bind(this)}
        />
      </div>
    )
  }
}

EditEmployee.propTypes = {
  location: PropTypes.shape({
    pathname: PropTypes.string
  }),
  params: PropTypes.shape({
    id: PropTypes.string
  }),
  route: PropTypes.shape({
    title: PropTypes.string
  })
}
export default EditEmployee
