// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import update from 'immutability-helper'
import { browserHistory } from 'react-router'
import validator from 'validator'

// Components
import EditClientActions from 'actions/editClient'
import NotificationActions from 'actions/notifications'
import EditClientStore from 'stores/editClient'
import SelectFile from 'shared/selectFile'
import Button from 'shared/button'

import SatCredentials from 'shared/satCredentials'

// Set where all the required field refs are going to be
// stored so we can validate them all when submit
const requiredFields = new Set()

function getCurrentState () {
  let entity = EditClientStore.getEditingEntity()
  return { entity }
}

class EditClientRFC extends Component {
  constructor () {
    super()
    this.data = {
      requestInProgress: false,
      modalOpen: false,
      clickedButton: '',
      logo: {
        data: '',
        filename: '',
        contentType: ''
      },
      certificate: {
        data: '',
        filename: '',
        contentType: ''
      },
      key: {
        data: '',
        filename: '',
        contentType: ''
      }
    }
    this.state = Object.assign({}, this.data, getCurrentState())
    this._handleEscape = this._handleEscape.bind(this)
    this._onChange = this._onChange.bind(this)
  }

  componentDidMount () {
    // Adds listener, when the Store changes, trigger the _onChange callback func
    EditClientStore.addChangeListener(this._onChange)
    // If we are supposed to be editing an entity, ask for entity data
    if (typeof this.props.params.id !== 'undefined') {
      EditClientActions.getEntityData(this.props.params.id)
    }
  }

  componentWillUnmount () {
    // Removes the listener to avoid memory leaks
    EditClientStore.removeChangeListener(this._onChange)
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  _onChange () {
    // When the store changes, the state goes to it and pulls the updated data
    // then sets it so the component rerenders
    let state = getCurrentState()
    this.setState(state)
  }

  // Listen for key presses, close the dialog if escape is true
  _handleEscape (key, b) {
    if (key.keyCode === 27) {
      window.removeEventListener('keydown', this._handleEscape, true)
      this.setState({ modalOpen: false })
    }
  }

  // Toggles SelectFile component open/closed
  toggleFileDialog () {
    if (!this.state.modalOpen) {
      window.addEventListener('keydown', this._handleEscape, true)
    }
    this.setState({ modalOpen: !this.state.modalOpen })
  }

  // Validates if a doc is uploaded
  isValidDocument (file) {
    return !validator.isEmpty(this.state[file].data)
  }

  // Sets logo data and filename on the state
  setLogo (data, filename, contentType) {
    this.setState({
      logo: update(this.state.logo, {
        data: { $set: data },
        filename: { $set: filename },
        contentType: { $set: contentType }
      })
    })
  }

  // Sets certificate data and filename on the state
  setCertificate (data, filename, contentType) {
    this.setState({
      cetificate: update(this.state.certificate, {
        data: { $set: data },
        filename: { $set: filename },
        contentType: { $set: contentType }
      })
    })
    console.log('Cer: ', this.state.certificate)
  }

  // Sets key data and filename on the state
  setKey (data, filename, contentType) {
    this.setState({
      key: update(this.state.key, {
        data: { $set: data },
        filename: { $set: filename },
        contentType: { $set: contentType }
      })
    })
    console.log('Key: ', this.state.key)
  }

  // Sets which of the upload buttons was clicked and opens the SelectFile modal
  selectFilefunction (func) {
    this.setState({ clickedButton: func }, this.toggleFileDialog)
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validateAll () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate.call(this)) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  // Clears the current Document and redirects to the documents listing
  returnToListing () {
    this.setState({ requestInProgress: false })
    EditClientActions.clearEditingEntity()
    browserHistory.push('/entidades')
  }

  sendData () {
    let { entity, logo, requestInProgress } = this.state
    if (this.isValidDocument('logo') && !requestInProgress) {
      let data = {
        taxable_entity: {
          logo: {
            attached_file: logo.data,
            original_filename: logo.filename,
            content_type: logo.contentType
          }
        }
      }
      this.setState({ requestInProgress: true }, () => {
        EditClientActions.setEntityData(entity.id, data)
          .then(() => {
            NotificationActions.success(
              'La entidad ha sido editada correctamente'
            )
            this.returnToListing()
          })
          .catch(e => {
            NotificationActions.error(e)
            this.setState({ requestInProgress: false })
          })
      })
    }
  }

  render () {
    let { clickedButton, modalOpen, entity, logo } = this.state
    // Depending on the button clicked, passes a different callback function to the SelectFile modal
    let setFileFunc = () => {}
    switch (clickedButton) {
      case 'logo':
        setFileFunc = this.setLogo
        break
      case 'cer':
        setFileFunc = this.setCertificate
        break
      case 'key':
        setFileFunc = this.setKey
        break
    }

    return (
      <div>
        <Button
          buttonStyle='link'
          icon='chevron-double-left'
          onClick={this.returnToListing.bind(this)}
        >
          Volver
        </Button>
        <br />
        <br />
        <h1 className='subheader-green'>Editar Entidad Fiscal</h1>
        <div>
          <div className='title is-marginless'>{entity.legalName}</div>
          <div className='body'>RFC: {entity.rfc}</div>
          <br />
          <div style={{ width: '100px' }}>
            <img src={entity.logo} />
          </div>
          <Button
            icon='upload'
            onClick={this.selectFilefunction.bind(this, 'logo')}
          >
            Subir Archivo
          </Button>
          <Button
            buttonStyle='primary'
            icon='content-save'
            onClick={this.sendData.bind(this)}
            disabled={!logo.data}
            loading={this.state.requestInProgress}
          >
            Guardar cambios
          </Button>
          <br />
          <br />
          <h5 className='subheader-green'>Direcci√≥n</h5>
          <div className='columns'>
            <div className='column'>
              <textarea className='textarea' disabled value={entity.address} />
            </div>
          </div>
        </div>
        <br />
        <SatCredentials />
        <SelectFile
          isActive={modalOpen}
          toggleModal={this.toggleFileDialog.bind(this)}
          title='Subir Archivo'
          fileTypePlaceholder={clickedButton}
          accept={
            clickedButton === 'cer' || clickedButton === 'key'
              ? `.${clickedButton}`
              : 'image/*'
          }
          setFile={setFileFunc.bind(this)}
        />
      </div>
    )
  }
}

EditClientRFC.propTypes = {
  params: PropTypes.shape({
    id: PropTypes.string
  })
}
export default EditClientRFC
