// import libraries
import React, { Component } from 'react'
import { Link, browserHistory } from 'react-router'
import Select from 'react-select'
import AppSignal from 'appsignal'

// import components
import Card from 'shared/card'
import Button from 'shared/button'
import NotificationsActions from 'actions/notifications'

import API from 'api'
import { SERVICE_UNSUBSCRIBE } from 'shared/catalogs'

class RequestLeave extends Component {
  state = {
    requestInProgress: false,
    service: '',
    reasons: [],
    comments: '',
    reasonsValue: '' // the reasonsValue are a string separated by commas
  }

  // Call CancellationReasons and set data in  the state
  componentDidMount () {
    this.getCancellationReasons()
  }

  // send form data
  sendData = () => {
    this.sendCancellationRequest()
  }

  // update services checked/unchecked
  onChangeServices = serviceId => {
    this.setState({ service: serviceId })
  }

  // update message
  onChangeComments = e => {
    this.setState({
      comments: e.target.value
    })
  }

  // update reason
  onChangeReason = value => {
    this.setState({
      reasonsValue: value
    })
  }

  // Get Cancellation Reasons from api
  getCancellationReasons () {
    API.User.CancellationReasons()
      .then(response => {
        this.setState({ reasons: response.cancellation_reasons })
      })
      .catch(error => {
        AppSignal.sendError(error)
        NotificationsActions.error(error)
      })
  }

  // Request endpoint
  sendCancellationRequest = () => {
    const { service, reasonsValue, comments } = this.state
    const data = {
      cancellation_request: {
        service: service.toString(),
        cancellation_reasons: reasonsValue.split(','),
        comments
      }
    }
    this.setState({ requestInProgress: true }, () => {
      API.User.CreateCancelRequest(data)
        .then(r => {
          NotificationsActions.success('Solicitud enviada')
        })
        .catch(error => {
          AppSignal.sendError(error)
          NotificationsActions.error(error)
        })
        .finally(() => {
          this.setState({ requestInProgress: false })
          browserHistory.push('/dashboard')
        })
    })
  }

  // render main content
  render () {
    const { reasons, comments, service, reasonsValue } = this.state
    const NEW_REASONS = reasons.map(reason => {
      const newReasons = { value: reason.id, label: reason.name }
      return newReasons
    })
    return (
      <div>
        <h2 className='headline'>Solicitar Baja</h2>
        <Card>
          <div className='requestLeave'>
            <h2 className='title' style={{ marginBottom: '1em' }}>
              ¿Qué servicio deseas dar de baja? *
            </h2>
            <form onSubmit={this.sendData}>
              <div className='flex-column requestLeave-label'>
                {SERVICE_UNSUBSCRIBE.map(service => {
                  return (
                    <label className='checkbox' key={service.id}>
                      <input
                        type='checkbox'
                        value={service.id}
                        name={service.id}
                        checked={this.state.service === service.id}
                        onChange={() => this.onChangeServices(service.id)}
                      />
                      {service.label}
                    </label>
                  )
                })}
              </div>
              <div className='flex-column requestLeave-label'>
                <h2 className='title' style={{ marginBottom: '1em' }}>
                  Por favor, indícanos el motivo de la solicitud de baja *
                </h2>
                <Select
                  placeholder='Selecciona una opción'
                  value={reasonsValue}
                  onChange={this.onChangeReason}
                  options={NEW_REASONS}
                  simpleValue
                  multi
                />
              </div>
              <div>
                <label className='title'>
                  Puedes proporcionar mas información si deseas.
                </label>
                <textarea
                  className='textarea'
                  style={{ margin: '1em 0' }}
                  value={comments}
                  onChange={this.onChangeComments}
                />
              </div>
            </form>
          </div>
          <Button
            type='submit'
            buttonStyle='primary'
            icon='telegram'
            disabled={service === '' || !reasons}
            loading={this.state.requestInProgress}
            onClick={this.sendData}
          >
            Enviar
          </Button>
          <Link to={'/dashboard'}>
            <Button icon='cancel'>Cancelar</Button>
          </Link>
          <p style={{ marginTop: '1em', color: '#B00A85' }}>
            * Campos requeridos
          </p>
        </Card>
      </div>
    )
  }
}

export default RequestLeave
