import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types'
import { Link, browserHistory } from 'react-router'
import DocumentTitle from 'react-document-title'
import AppSignal from 'appsignal'

import NotificationsContainer from 'shared/notifications'
import Logo from 'shared/logo'
import TextField from 'shared/textField/index'
import Button from 'shared/button'
import API from 'api'

import NotificationsActions from 'actions/notifications'
import SessionActions from 'actions/session'

// Keys
const EMAIL_KEY = 'email'
const PASSWORD_KEY = 'password'
const TOKEN_KEY = 'token'

const requiredFields = new Set()

class RecoverPassword extends Component {
  constructor (props) {
    super(props)
    this.data = {
      requestSuccessful: false,
      requestInProgress: false,
      [EMAIL_KEY]: '',
      [PASSWORD_KEY]: ''
    }
    this.state = Object.assign({}, this.data)
  }

  componentWillUnmount () {
    requiredFields.clear()
  }

  onChange (field, e) {
    this.setState({ [field]: e.target.value })
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validateAll () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate.call(this)) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  sendData (e) {
    e.preventDefault()
    if (this.validateAll()) {
      this.setState({ requestInProgress: true }, () => {
        SessionActions.resetPassword({ [EMAIL_KEY]: this.state[EMAIL_KEY] })
          .then(r => {
            this.setState({
              requestSuccessful: true,
              requestInProgress: false
            })
          })
          .catch(r => {
            this.setState({ requestInProgress: false })
          })
      })
    }
  }

  changePassword (token) {
    if (this.validateAll()) {
      this.setState({ requestInProgress: true }, () => {
        API.User.ChangePassword({
          [TOKEN_KEY]: token,
          [PASSWORD_KEY]: this.state[PASSWORD_KEY]
        })
          .then(r => {
            NotificationsActions.success('Contraseña cambiada exitosamente')
            this.setState({ requestInProgress: false })
            browserHistory.push('/')
          })
          .catch(error => {
            NotificationsActions.error(error)
            this.setState({ requestInProgress: false })
            AppSignal.sendError(error)
          })
      })
    }
  }

  // Render the recover password inputs
  requestNewPassword () {
    let requestNewPassword = null
    if (!this.state.requestSuccessful) {
      requestNewPassword = (
        <Fragment>
          <form onSubmit={this.sendData.bind(this)}>
            <p className='login-box__text'>
              Para restablecer tu contraseña te enviaremos un link, por favor
              ingresa tu correo:
            </p>
            <TextField
              className='login-box__input'
              type='email'
              placeholder='Correo electrónico'
              value={this.state[EMAIL_KEY]}
              onChange={this.onChange.bind(this, EMAIL_KEY)}
              required
              ref={r => requiredFields.add(r)}
            />
            <Button
              buttonStyle='primary'
              className='is-fullwidth login-top-2rem login-box__input'
              type='submit'
              onClick={this.sendData.bind(this)}
              loading={this.state.requestInProgress}
            >
              Recuperar contraseña
            </Button>
            <Link to='/' className='is-fullwidth login-box__button-start'>
              Ir al inicio
            </Link>
          </form>
        </Fragment>
      )
    } else {
      requestNewPassword = (
        <Fragment>
          <p className='login-box__text'>
            Se ha enviado un correo a {this.state[EMAIL_KEY]} para poder
            restablecer tu contraseña.
          </p>
          <Link
            to='/'
            className='encontaButton encontaButton__login primary is-fullwidth'
          >
            Ir al inicio
          </Link>
        </Fragment>
      )
    }
    return requestNewPassword
  }

  // Render form for wrigin your new password
  renderNewPassword () {
    let renderNewPassword = null
    if (typeof this.props.location.query[TOKEN_KEY] !== 'undefined') {
      renderNewPassword = (
        <div className='content'>
          <p>Ingresa tu nueva contraseña</p>
          <TextField
            className='login-box__input'
            type='password'
            placeholder='Contraseña'
            value={this.state[PASSWORD_KEY]}
            onChange={this.onChange.bind(this, [PASSWORD_KEY])}
            required
            ref={r => requiredFields.add(r)}
          />
          <Button
            buttonStyle='primary'
            className='is-fullwidth'
            onClick={this.changePassword.bind(
              this,
              this.props.location.query[TOKEN_KEY]
            )}
            loading={this.state.requestInProgress}
          >
            Guardar
          </Button>
        </div>
      )
    }
    return renderNewPassword
  }

  // Main render
  render () {
    let render =
      typeof this.props.location.query[TOKEN_KEY] !== 'undefined'
        ? this.renderNewPassword()
        : this.requestNewPassword()
    return (
      <DocumentTitle title='Restablecer tu contraseña - Enconta'>
        <div className='login-page'>
          <div className='login-box__background-vectors' />
          <div className='login-box'>
            <Logo logoSize={'normal'} />
            {render}
          </div>
          <NotificationsContainer />
        </div>
      </DocumentTitle>
    )
  }
}

RecoverPassword.propTypes = {
  location: PropTypes.shape({
    query: PropTypes.object
  })
}

export default RecoverPassword
