import React, { Component, Fragment } from 'react'
import { withRouter } from 'react-router'
import classNames from 'classnames'
import { handleBack, handleURL } from 'utils'
import { checkFeatureFlag } from './utils'

import Button from 'shared/button'
import Terms from 'shared/terms'
import BankList from './components/bankList'

import SmartSyncActions from 'actions/smartsync'
import PaymentAccountsStore from 'stores/paymentAccounts'

function getCurrentState () {
  return {
    hasSynchronizedBank: PaymentAccountsStore.getHasSynchronizedBank()
  }
}
export class SmartSync extends Component {
  defaultState = {
    requestInProgress: false,
    termsChecked: false
  }

  state = { ...this.defaultState, ...getCurrentState() }

  componentDidMount () {
    checkFeatureFlag()
    PaymentAccountsStore.addChangeListener(this._onChange)
    SmartSyncActions.getBankList()
  }

  componentWillUnmount () {
    PaymentAccountsStore.removeChangeListener(this._onChange)
  }
  _onChange = () => {
    this.setState(getCurrentState())
  }

  setInfoLegals = () => {
    const windowGlobal = typeof window !== 'undefined' && window

    windowGlobal.localStorage.setItem('termsAccepted', true)
    handleURL('/nuevo/banco')
  }

  toggleCheckTerms = () => {
    this.setState(state => ({ termsChecked: !state.termsChecked }))
  }

  explainSmartSync = () => {
    const { hasSynchronizedBank } = this.state

    return (
      <Fragment>
        {!hasSynchronizedBank && (
          <h4 className='subheader-green margin-top'>
            {this.props.route.title}
          </h4>
        )}
        <div className='columns'>
          <div
            className={classNames('column', {
              'is-7': !hasSynchronizedBank,
              'is-6': hasSynchronizedBank
            })}
          >
            {hasSynchronizedBank && (
              <h4 className='subheader-green margin-top'>
                Sincronizar nueva cuenta
              </h4>
            )}
            <p className='synchronization__title'>¿Por que usar SmartSync?</p>
            <p className='synchronization__text-gray'>
              Al tener tu cuenta sincronizada no tendrás que enviar el resumen
              de movimientos de cada mes, ya que nuestro sistema automatizado lo
              hace por ti.
            </p>

            <p className='synchronization__title'>¿Cómo funciona?</p>

            <ol className='synchronization__list synchronization__text-gray'>
              <li className='synchronization__list-item'>
                Selecciona tu banco
              </li>
              <li className='synchronization__list-item'>
                Ingresa el usuario y contraseña que usas para tu banco en línea
              </li>
              <li className='synchronization__list-item'>
                Selecciona la(s) cuenta(s) que se sincronizaran cada mes
              </li>
            </ol>

            <p className='synchronization__title'>¿Con que bancos funciona?</p>
            <ul className='synchronization__text-gray'>
              <li>American Express</li>
              <li>Banamex</li>
              <li>HSBC</li>
              <li>Santander</li>
              <li>Scotiabank</li>
              <li>Bancomer</li>
            </ul>
            {hasSynchronizedBank &&
              this.renderButton('Sincronizar nueva cuenta')}
          </div>

          <div
            className={classNames('column', {
              'is-4 flex-center': !hasSynchronizedBank,
              'is-6': hasSynchronizedBank
            })}
          >
            {hasSynchronizedBank ? (
              <BankList />
            ) : (
              <img
                alt='Ingresa tu fiel'
                src='/img/smart-sync.png'
                className='synchronization__img'
              />
            )}
          </div>
        </div>

        {!hasSynchronizedBank && this.renderButton('Comenzar')}
      </Fragment>
    )
  }
  renderButton (text) {
    const { termsChecked } = this.state
    const windowGlobal = typeof window !== 'undefined' && window
    const { termsAccepted } = windowGlobal.localStorage
    const buttonDisabled = !termsAccepted && !termsChecked

    return (
      <Fragment>
        {!termsAccepted && (
          <Terms
            terms={termsChecked}
            toggleCheckTerms={() => this.toggleCheckTerms()}
            grayText
          />
        )}
        <div className='columns flex-center synchronization--margin-top'>
          <Button
            buttonStyle='primary'
            onClick={() => this.setInfoLegals()}
            disabled={buttonDisabled}
          >
            {text}
          </Button>
        </div>
      </Fragment>
    )
  }
  renderEditAccounts () {
    return (
      <h4 className='subheader-green margin-top'>Sincronizar nueva cuenta</h4>
    )
  }

  render () {
    return this.explainSmartSync()
  }
}

const Bank = props => (
  <Fragment>
    <Button buttonStyle='link' icon='chevron-double-left' onClick={handleBack}>
      Volver
    </Button>

    <SmartSync {...props} />
  </Fragment>
)

export default withRouter(Bank)
