// Libraries
import React from 'react'
import accounting from 'accounting'

// Components
import TextField from 'shared/textField'
import SelectField from 'shared/selectField'
import { TRADE_INTERNATIONAL_TERMS } from 'shared/catalogs'
import { absolutify } from 'utils'

const OPERATION_TYPE_KEY = 'operation_type'
const REQUEST_KEY_KEY = 'request_key'
const ORIGIN_CERTIFICATE_KEY = 'origin_certificate'
const ORIGIN_CERTIFICATE_FOLIO_KEY = 'origin_certificate_folio'
const INCOTERM_KEY = 'incoterm'
const SUBDIVISION_KEY = 'subdivision'
const COMMENTS_KEY = 'comments'
const USD_EXCHANGE_RATE_KEY = 'usd_exchange_rate'
const USD_TOTAL_KEY = 'usd_total'

const ORIGIN_CERTIFICATE = [
  { id: '0', label: 'No funge como certificado de origen' },
  { id: '1', label: 'Funge como certificado de origen' }
]

const requiredFields = new Set()

class GeneralInfo extends React.Component {
  constructor (props) {
    super(props)
    this.data = {
      [OPERATION_TYPE_KEY]: '2',
      [REQUEST_KEY_KEY]: 'A1',
      [ORIGIN_CERTIFICATE_KEY]: '1',
      [ORIGIN_CERTIFICATE_FOLIO_KEY]: '',
      [INCOTERM_KEY]: 'FCA',
      [SUBDIVISION_KEY]: '0',
      [COMMENTS_KEY]: '',
      [USD_EXCHANGE_RATE_KEY]: '0',
      [USD_TOTAL_KEY]: '0'
    }
    this.state = Object.assign({}, this.data)
  }

  componentWillUnmount () {
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validate () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate()) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  // Exports state data
  export () {
    return Object.assign({}, this.state)
  }

  // Updates an input's value when it changes
  onChange (field, e) {
    this.setState({
      [field]: e.target.value
    })
  }

  // Sets the correct format to the content field when it losses the focus
  formatField (field, kind = 'string') {
    const formatted = this.setFormat(this.state[field], kind)
    this.setState({
      [field]: formatted
    })
  }

  // Formats the current field as number when it gets the focus
  unformatField (field, kind = 'string') {
    const unformatted =
      kind === 'string'
        ? this.state[field]
        : accounting.unformat(this.state[field])
    this.setState({
      [field]: unformatted
    })
  }

  // Sets a specific format depending of the kind
  setFormat (value, kind = 'string') {
    switch (kind) {
      case 'money':
        return accounting.formatMoney(absolutify(value), '$ ')
      case 'integer':
        return absolutify(value).toString()
      case 'percentage':
        return accounting.formatMoney(absolutify(value), {
          symbol: '%',
          format: '%v %s'
        })
      case 'string':
        return value
      default:
        return value
    }
  }

  // Renders origin certificate folio if required
  rendeOriginCertificateFolio () {
    if (this.shouldRenderOriginCertificateFolio()) {
      return (
        <TextField // origin_certificate_folio    6 a 40 caracteres alfanuméricos. Si CertificadoOrigen === 0, este dato no debe existir
          label='Folio del certificado de origen'
          placeholder='Folio del certificado de origen'
          value={this.state[ORIGIN_CERTIFICATE_FOLIO_KEY]} // 6 a 40 caracteres alfanumericos
          onChange={this.onChange.bind(this, ORIGIN_CERTIFICATE_FOLIO_KEY)}
          required={this.shouldRenderOriginCertificateFolio()}
          ref={r => requiredFields.add(r)}
        />
      )
    } else {
      requiredFields.clear()
    }
  }

  // Checks if origin certificate folio text field should be rendered
  shouldRenderOriginCertificateFolio () {
    return this.state[ORIGIN_CERTIFICATE_KEY] === '1'
  }

  // Renders general info component
  render () {
    return (
      <div>
        <SelectField // Select origin_certificate: No funge como certificado de origen, val=0 | Funge como certificado de origen, val=1
          label='Certificado de origen'
          key='ORIGIN_CERTIFICATE'
          placeholder='Selecciona un tipo'
          options={ORIGIN_CERTIFICATE}
          value={this.state[ORIGIN_CERTIFICATE_KEY]}
          onChange={this.onChange.bind(this, ORIGIN_CERTIFICATE_KEY)}
        />
        <br />
        {this.rendeOriginCertificateFolio()}
        <br />
        <SelectField // select c_INCOTERM
          label='Términos Internacionales de Comercio'
          key='TRADE_INTERNATIONAL_TERMS'
          placeholder='Selecciona uno'
          options={TRADE_INTERNATIONAL_TERMS}
          value={this.state[INCOTERM_KEY]}
          onChange={this.onChange.bind(this, INCOTERM_KEY)}
        />
        <br />
        <div className='columns'>
          <div className='column'>
            <label className='caption'>Comentarios</label>
            <div className='control'>
              <textarea // libre comments
                value={this.state[COMMENTS_KEY]}
                onChange={this.onChange.bind(this, COMMENTS_KEY)}
                className='textarea'
              />
            </div>
          </div>
          <div className='column'>
            <TextField // libre usd_exchange_rate
              label='Tipo de cambio USD'
              placeholder='Tipo de cambio USD'
              value={this.state[USD_EXCHANGE_RATE_KEY]}
              onChange={this.onChange.bind(this, USD_EXCHANGE_RATE_KEY)}
              onBlur={this.formatField.bind(
                this,
                USD_EXCHANGE_RATE_KEY,
                'money'
              )}
              onFocus={this.unformatField.bind(
                this,
                USD_EXCHANGE_RATE_KEY,
                'money'
              )}
              required
              ref={r => requiredFields.add(r)}
            />
            <br />
            <TextField // calculado usd_total
              label='Importe total USD'
              placeholder='Importe total USD'
              value={this.state[USD_TOTAL_KEY]}
              onChange={this.onChange.bind(this, USD_TOTAL_KEY)}
              disabled
            />
          </div>
        </div>
      </div>
    )
  }
}

export default GeneralInfo
