// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { browserHistory } from 'react-router'

// Components
import Tabs, { TabList, Tab, TabPanels, TabPanel } from 'shared/tabs'
import Isr from './isr'
import Client from './client'
import IsrArrend from './isrArrend'
import IsrRent from './isrRent'
import Iva from './iva'
import InvoiceCalculation from './invoiceCalculation'
import Deposits from './deposits'

class TableCalculations extends Component {
  static propTypes = {
    calculation: PropTypes.shape({
      id: PropTypes.number.isRequired
    }).isRequired,
    period: PropTypes.shape({
      id: PropTypes.number.isRequired
    }).isRequired
  }

  onTabChange = periodId => {
    browserHistory.push(`/mi-contabilidad/${periodId}/ver`)
  }

  render () {
    const {
      period,
      calculation,
      sendCalculation,
      showButtons,
      oldestPeriod
    } = this.props
    return (
      <div className='box box--no-lateral-padding'>
        <Tabs onTabChange={() => this.onTabChange(period.id)}>
          <TabList className='justify-between tabs--tabs-caculation'>
            <Tab>GENERAL</Tab>
            <Tab>ISR</Tab>
            <Tab isHidden={!calculation.lease_isr}>ISR ARRENDAMIENTO</Tab>
            <Tab isHidden={!calculation.salaries_wages_isr}>
              ISR RETENCIONES
            </Tab>
            <Tab>IVA</Tab>
            <Tab>INGRESOS</Tab>
            <Tab>GASTOS</Tab>
            <Tab>DEPÓSITOS NO FACTURADOS</Tab>
          </TabList>
          <TabPanels>
            <TabPanel>
              <Client
                period={period}
                calculation={calculation}
                sendCalculation={sendCalculation}
                showButtons={showButtons}
                oldestPeriod={oldestPeriod}
              />
            </TabPanel>
            <TabPanel>
              <Isr
                calculation={calculation}
                id={calculation.id}
                periodId={period.id}
                month={period.month}
              />
            </TabPanel>
            <TabPanel>
              <IsrArrend
                calculation={calculation}
                id={calculation.id}
                periodId={period.id}
                month={period.month}
              />
            </TabPanel>
            <TabPanel>
              <IsrRent
                calculation={calculation}
                id={calculation.id}
                periodId={period.id}
                month={period.month}
              />
            </TabPanel>
            <TabPanel>
              <Iva
                calculation={calculation}
                id={calculation.id}
                periodId={period.id}
              />
            </TabPanel>
            <TabPanel>
              <InvoiceCalculation
                periodId={period.id}
                calculationId={calculation.id}
                invoicesIds={calculation.invoices}
                type='incomes'
                key='incomes'
              />
            </TabPanel>
            <TabPanel>
              <InvoiceCalculation
                periodId={period.id}
                calculationId={calculation.id}
                invoicesIds={calculation.invoices}
                type='all_expenses'
                key='expenses'
              />
            </TabPanel>
            <TabPanel>
              <Deposits calculation={calculation} id={calculation.id} />
            </TabPanel>
          </TabPanels>
        </Tabs>
      </div>
    )
  }
}

export default TableCalculations
