// Libraries
import React from 'react'

/**
 * @typedef {'AMERICAN EXPRESS' | 'MASTERCARD' | 'Other'} Brand
 * @param {{brand: string, className: string}} props
 */
const getThumbnail = brand => {
  let alt = ''
  let src = ''

  switch (brand) {
    case 'AMERICAN EXPRESS':
      alt = 'American Express'
      src = '/img/amex_logo.svg'
      break
    case 'VISA':
      alt = 'Visa'
      src = '/img/visa_logo.svg'
      break
    case 'MASTERCARD':
      alt = 'Masterd Card'
      src = '/img/masterdcard_logo.svg'
      break
    default:
      alt = 'Other'
      src = '/img/logo_generico.svg'
  }
  return { alt, src }
}

const CardThumbnail = ({ brand, className = 'img-paymentcard' }) => {
  return (
    <figure>
      <img className={className} {...getThumbnail(brand)} />
    </figure>
  )
}

export default CardThumbnail
