import React, { Component } from 'react'
import PropTypes from 'prop-types'
import validator from 'validator'
import classNames from 'classnames'
import _isNil from 'lodash/isNil'
import _kebabCase from 'lodash/kebabCase'

const ERROR_TXT = {
  REQUIRED: 'Este campo es requerido',
  EMAIL: 'Ingrese un e-mail v치lido'
}

const currency = (n = 0) => {
  n = n === '' ? 0 : n
  n = parseFloat(n)
  return isNaN(n) ? false : n.toFixed(2)
}

class TextField extends Component {
  static getDerivedStateFromProps (props, state) {
    if (props.value !== state.value) {
      return { ...state, value: props.value }
    }

    return null
  }

  constructor (props) {
    super(props)
    this.data = {
      value: props.isMoney ? currency(props.value) : props.value,
      isValid: true,
      errorText: '',
      validateOnChange: false
    }
    this.state = Object.assign({}, this.data)
    this.onChange = this.onChange.bind(this)
    this.onFocus = this.onFocus.bind(this)
    this.onBlur = this.onBlur.bind(this)
    this._handleKeyDown = this._handleKeyDown.bind(this)
    this.validate = this.validate.bind(this)
  }

  componentDidMount () {
    this.mounted = true
  }

  componentWillUnmount () {
    this.mounted = false
  }

  onFocus (e) {
    let { selectAllOnFocus, onFocus } = this.props
    onFocus()
    if (selectAllOnFocus) e.target.select()
  }

  onBlur () {
    this.validate()
    if (!this.state.validateOnChange) {
      this.setState({ validateOnChange: true })
    }
    if (this.props.isMoney) {
      this.setState(prevState => ({ value: currency(prevState.value) }))
    }
    this.props.onBlur()
  }

  _handleKeyDown (e) {
    if (e.key === 'Enter') {
      this.props.onEnter()
    }
  }

  // When the field changes, it updates the value locally and calls the passed callback function
  // so it can also update values on the parent component
  onChange (e) {
    this.setState({ value: e.target.value }, () => {
      if (this.state.validateOnChange) this.validate()
    })
    this.props.onChange(e)
  }

  // Validates if selected value matches the requirements.
  // Sets on state the validation result and error message
  // Returns boolean with the validation result.
  validate () {
    let {
      type,
      required,
      minLength,
      maxLength,
      name,
      onValidationError
    } = this.props
    let { value } = this.state
    let isValid = !(required || minLength)
    let errorText = ''
    if (value === null && required) {
      isValid = validator.isLength(String(value), { min: minLength })
      errorText = ERROR_TXT.REQUIRED
    }
    if (required) {
      if (type === 'number') {
        isValid = !isNaN(value) && value !== ''
      } else {
        isValid = !validator.isEmpty(value)
      }
      errorText = isValid ? '' : ERROR_TXT.REQUIRED
    }
    // Check also for validity in the next validations
    // Prevents from passing validation if a previous condition decided it was invalid
    if (isValid) {
      if (type === 'email' || type === 'email-no-icon') {
        if (!value) return
        isValid = validator.isEmail(value)
        errorText = errorText || ERROR_TXT.EMAIL
      }
      if (type === 'number') {
        if (minLength) {
          isValid = validator.isLength(String(value), { min: minLength })
          errorText =
            errorText ||
            `El campo debe contener al menos ${minLength} caracteres`
        }
        if (maxLength) {
          isValid = validator.isLength(String(value), { max: maxLength })
          errorText =
            errorText || `El campo debe contener m치ximo ${maxLength} caracteres`
        }
      } else {
        if (minLength) {
          isValid = validator.isLength(value, { min: minLength })
          errorText =
            errorText ||
            `El campo debe contener al menos ${minLength} caracteres`
        }
        if (maxLength) {
          isValid = validator.isLength(value, { max: maxLength })
          errorText =
            errorText || `El campo debe contener m치ximo ${maxLength} caracteres`
        }
      }
    }
    if (!isValid) onValidationError(`${name} - ${errorText}`)
    if (this.mounted) this.setState({ isValid, errorText })
    return isValid
  }

  // Render an icon on the right side of the text field
  // If the icon has a label, a tooltip will be shown
  // If the icon has an onClick action, it will be called
  // If it has a long text tooltip, it will be shown with a define width
  // Example of the icon prop
  // icon={{icon: 'close-circle', label: 'Eliminar', onClick: this.renderDeleteButton(pos)}}
  renderIcon () {
    let {
      icon = '',
      label = '',
      onClick,
      disabled = false,
      tooltipText = '',
      tooltipSize = ''
    } = this.props
    let renderLabel = label ? <span className='tooltip'>{label}</span> : null
    return (
      <span
        className={classNames('icon info-icon', {
          clickable: onClick,
          disabled: disabled
        })}
      >
        {renderLabel}
        {tooltipText && (
          <span
            className={classNames('tooltip', {
              'tooltip--width-280px': tooltipSize === 'width-280px'
            })}
          >
            {tooltipText}
          </span>
        )}
        <i className={`mdi mdi-${icon}`} onClick={!disabled && onClick} />
      </span>
    )
  }

  render () {
    let {
      label,
      type,
      className,
      placeholder,
      disabled,
      required,
      info,
      icon,
      minLength,
      maxLength,
      onClick,
      isSearchBar,
      floatLabel,
      hideAndShowPassword,
      onChangeType
    } = this.props
    let { value, isValid, errorText } = this.state
    if (_isNil(value)) value = ''
    if (required && label) label = `${label} *`

    return (
      <div
        className={classNames({
          'filter__search-input': isSearchBar,
          'control float-label': floatLabel
        })}
      >
        {label && (
          <label className='caption' htmlFor={_kebabCase(label)}>
            {label}
          </label>
        )}
        <p
          className={classNames('control', {
            'has-icons-left': type === 'email' || type === 'password',
            'has-info': info || icon
          })}
        >
          <input
            className={classNames('input ' + className, {
              'is-danger': !isValid,
              'has-text-right': type === 'number',
              required: required && !value
            })}
            type={type}
            placeholder={placeholder}
            disabled={disabled}
            value={value}
            onChange={this.onChange}
            onFocus={this.onFocus}
            onBlur={this.onBlur}
            onKeyDown={this._handleKeyDown}
            required={required}
            minLength={minLength}
            maxLength={maxLength}
            onClick={onClick}
            {...(label || floatLabel
              ? { id: _kebabCase(label || floatLabel) }
              : {})}
          />

          {floatLabel && (
            <label className='caption' htmlFor={_kebabCase(floatLabel)}>
              {floatLabel}
            </label>
          )}
          {hideAndShowPassword && (
            <p onClick={onChangeType} className='float-label-icon'>
              <i className='mdi mdi-eye' />
            </p>
          )}
          {info && (
            <span className='icon info-icon'>
              <span className='tooltip'>{info}</span>
              <i className='mdi mdi-information-outline' />
            </span>
          )}
          {icon && this.renderIcon()}
          {type === 'email' && (
            <span className='icon is-left'>
              <i
                className={classNames({
                  'mdi mdi-email-outline': type === 'email',
                  'mdi mdi-key': type === 'password'
                })}
              />
            </span>
          )}
        </p>
        <span className={classNames('help is-danger', { 'is-hidden': isValid })}>
          {errorText || ''}
        </span>
      </div>
    )
  }
}

export default TextField

TextField.propTypes = {
  value: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  onChange: PropTypes.func,
  label: PropTypes.string,
  name: PropTypes.string,
  className: PropTypes.string,
  placeholder: PropTypes.string,
  disabled: PropTypes.bool,
  type: PropTypes.string,
  minLength: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  maxLength: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
  required: PropTypes.bool,
  selectAllOnFocus: PropTypes.bool,
  isMoney: PropTypes.bool,
  onFocus: PropTypes.func,
  onBlur: PropTypes.func,
  onEnter: PropTypes.func,
  info: PropTypes.string,
  icon: PropTypes.object,
  onClick: PropTypes.func,
  onValidationError: PropTypes.func,
  floatLabel: PropTypes.string,
  hideAndShowPassword: PropTypes.bool,
  onChangeType: PropTypes.func
}

TextField.defaultProps = {
  value: '',
  className: '',
  placeholder: '',
  label: '',
  name: '',
  disabled: false,
  type: 'text',
  required: false,
  selectAllOnFocus: false,
  isMoney: false,
  onChange: () => {},
  onFocus: () => {},
  onBlur: () => {},
  onEnter: () => {},
  onClick: () => {},
