// Libraries
import React, { Component } from 'react'
import accounting from 'accounting'
import { scaleOrdinal } from 'd3'
import update from 'immutability-helper'
import classNames from 'classnames'
import moment from 'moment'
import AppSignal from 'appsignal'

// Chart
import Chart from './chart'
import SelectField from 'shared/selectField'
import { MONTHS, YEARS } from 'shared/catalogs'
import { prettyPeriod, DATE_FORMAT, PERIOD_FORMAT } from 'utils'
import API from 'api'
import {
  TYPE_KEY,
  PERIOD_KEY,
  MONTH_KEY,
  YEAR_KEY,
  PERIODS,
  TYPES,
  COLORS,
  LABEL_KEY,
  AMOUNT_KEY,
  PERCENTAGE_KEY,
  PERIOD_START_KEY,
  PERIOD_END_KEY,
  PERCENTAGE_THRESHOLD_AMOUNT,
  SMALL_ITEMS_SUMARIZED_LABEL
} from '../constants'

YEARS.reverse()

// Color palette
const getColor = scaleOrdinal().range(COLORS)

// Assign a percentage for every amount in item from total
const parseData = (chartData, total) => {
  let chartDataPercents = chartData.map((item, i) =>
    Object.assign({}, item, {
      [PERCENTAGE_KEY]: (item[AMOUNT_KEY] * 100) / total
    })
  )
  return chartDataPercents
}

// Return all items that are big enough
const itemsAboveThreshold = items =>
  items.filter(i => i[PERCENTAGE_KEY] >= PERCENTAGE_THRESHOLD_AMOUNT)

// Return all items that aren't big enough
const itemsBelowThreshold = items =>
  items.filter(i => i[PERCENTAGE_KEY] < PERCENTAGE_THRESHOLD_AMOUNT)

// Sumarized items that aren't big enough into a single item
const itemsBelowThresholdSummarized = items => {
  return {
    [AMOUNT_KEY]: itemsBelowThreshold(items).reduce(
      (acc, i) => acc + i[AMOUNT_KEY],
      0
    ),
    [LABEL_KEY]: SMALL_ITEMS_SUMARIZED_LABEL,
    [PERCENTAGE_KEY]: itemsBelowThreshold(items).reduce(
      (acc, i) => acc + i[PERCENTAGE_KEY],
      0
    )
  }
}

// Component
class Rubros extends Component {
  constructor (props) {
    super(props)
    this.data = {
      chartData: [],
      showOthers: false,
      filters: {
        [TYPE_KEY]: TYPES[1].id,
        [PERIOD_KEY]: PERIODS[0].id,
        [MONTH_KEY]: moment().format('MM'),
        [YEAR_KEY]: moment().format('YYYY')
      }
    }
    this.state = Object.assign({}, this.data)
  }

  // Load data on component's mount
  componentDidMount () {
    this.loadData()
  }

  // Get start and end period
  getPeriod () {
    return {
      [PERIOD_START_KEY]: moment(
        `${this.state.filters[YEAR_KEY]}-${this.state.filters[MONTH_KEY]}-01`,
        DATE_FORMAT
      ).format(PERIOD_FORMAT),
      [PERIOD_END_KEY]: moment(
        `${this.state.filters[YEAR_KEY]}-${this.state.filters[MONTH_KEY]}-01`,
        DATE_FORMAT
      )
        .add(
          PERIODS.find(period => this.state.filters[PERIOD_KEY] === period.id)
            .months - 1,
          'month'
        )
        .format(PERIOD_FORMAT)
    }
  }

  // Load data from API
  loadData () {
    API.Reports.Categories(
      Object.assign(
        {
          [TYPE_KEY]: this.state.filters[TYPE_KEY]
        },
        this.getPeriod()
      )
    )
      .then(r => {
        this.setState({
          chartData: r.report
        })
      })
      .catch(error => {
        AppSignal.sendError(error)
      })
  }

  // Toggle showOthers
  toggleShowOthers () {
    this.setState(prevState => ({ showOthers: !prevState.showOthers }))
  }

  // Handle changes for filters
  onChangeFilter (field, e) {
    this.setState(
      {
        filters: update(this.state.filters, {
          [field]: { $set: e.target.value }
        })
      },
      () => {
        this.loadData()
      }
    )
  }

  // Return a list of items with label, amount and percentage from total
  renderItems (items, status) {
    if (status === 'below' && !this.state.showOthers) return null
    return items.map((item, i) => {
      let icon =
        item[LABEL_KEY] === SMALL_ITEMS_SUMARIZED_LABEL ? (
          <i
            className={classNames('mdi mdi-triangle icon mdi-rotate-90', {
              'mdi-rotate-180': this.state.showOthers
            })}
            onClick={this.toggleShowOthers.bind(this)}
          />
        ) : null
      return (
        <tr
          key={i}
          className={classNames({
            below: status === 'below',
            'cursor-pointer': item[LABEL_KEY] === SMALL_ITEMS_SUMARIZED_LABEL
          })}
        >
          <td
            className='colorIcon'
            style={{
              backgroundColor: status === 'above' ? getColor(i) : 'transparent'
            }}
          />
          <td>
            {item[LABEL_KEY]}
            {icon}
          </td>
          <td className='amount'>
            {accounting.formatMoney(item[AMOUNT_KEY], '$ ', 2)}
          </td>
          <td className='percentage'>
            {accounting.formatNumber(item[PERCENTAGE_KEY], 2)} %
          </td>
        </tr>
      )
    })
  }

  // Render rubros component, get data from parent component, this only shows parent info
  renderChart ({ chartData, filters } = this.state) {
    const total = chartData.reduce((acc, i) => acc + i[AMOUNT_KEY], 0)
    const items = parseData(chartData, total)
    const {
      [PERIOD_START_KEY]: startPeriod,
      [PERIOD_END_KEY]: endPeriod
    } = this.getPeriod()
    const start = `${prettyPeriod(startPeriod, DATE_FORMAT, true, '/')}`
    const end = prettyPeriod(endPeriod, DATE_FORMAT, true, '/')
    const period = startPeriod === endPeriod ? start : `${start} a ${end}`
    return (
      <div className='rubrosChart rubrosChart__columns'>
        <div className='rubrosChart rubrosChart__columns-columnChart'>
          <Chart
            items={items}
            total={total}
            colors={COLORS}
            itemsAboveThreshold={itemsAboveThreshold}
            itemsBelowThreshold={itemsBelowThreshold}
            itemsBelowThresholdSummarized={itemsBelowThresholdSummarized}
          />
        </div>
        <div className='rubrosChart rubrosChart__columns-columnTable'>
          <div className='rubrosChart rubrosChart__table-title'>
            {TYPES.find(t => t.id === filters[TYPE_KEY]).label} de {period}
          </div>
          <div className='rubrosChart rubrosChart__table-amount'>
            {accounting.formatMoney(total, '$ ', 2)}
          </div>
          <table className='rubrosChart rubrosChart__table-table'>
            <tbody>
              {this.renderItems(
                [
                  ...itemsAboveThreshold(items),
                  itemsBelowThresholdSummarized(items)
                ].filter(i => i[AMOUNT_KEY] !== 0),
                'above'
              )}
              {this.renderItems(itemsBelowThreshold(items), 'below')}
            </tbody>
          </table>
        </div>
      </div>
    )
  }

  // Render filters for select
  renderFilters () {
    return (
      <div className='rubrosChart__filters rubrosChart__filters-wrap'>
        <div className='rubrosChart__filters rubrosChart__filters-filter'>
          <SelectField
            label='Tipo'
            options={TYPES}
            value={this.state.filters[TYPE_KEY]}
            onChange={this.onChangeFilter.bind(this, TYPE_KEY)}
          />
        </div>
        <div className='rubrosChart__filters rubrosChart__filters-filter'>
          <SelectField
            label='Periodo'
            options={PERIODS}
            value={this.state.filters[PERIOD_KEY]}
            onChange={this.onChangeFilter.bind(this, PERIOD_KEY)}
          />
        </div>
        <div className='rubrosChart__filters rubrosChart__filters-filter'>
          <SelectField
            label='Mes de inicio'
            options={MONTHS}
            value={this.state.filters[MONTH_KEY]}
            onChange={this.onChangeFilter.bind(this, MONTH_KEY)}
          />
        </div>
        <div className='rubrosChart__filters rubrosChart__filters-filter'>
          <SelectField
            label='AÃ±o'
            options={YEARS}
            value={this.state.filters[YEAR_KEY]}
            onChange={this.onChangeFilter.bind(this, YEAR_KEY)}
          />
        </div>
      </div>
    )
  }

  // Main render
  render () {
    return (
      <div
        className='rubrosChart rubrosChart__wrap'
        ref={node => (this.node = node)} // eslint-disable-line
      >
        {this.renderFilters()}
        {this.renderChart()}
      </div>
    )
  }
}

export default Rubros
