// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import moment from 'moment'
import accounting from 'accounting'
import classNames from 'classnames'

// Components
import NotificationsActions from 'actions/notifications'
import DownloadsActions from 'actions/downloads'
import DownloadsStore from 'stores/downloads'
import TextField from 'shared/textField'
import SelectField from 'shared/selectField'
import Card from 'shared/card'
import IconButton from 'shared/iconButton'
import Paginator from 'shared/paginator'
import Datepicker from 'shared/datepicker'
import Alert from 'shared/alert'
import TableFilter from 'shared/tableFilter'
import { prettyDate, resetScroll } from 'utils'
import { ALL_ITEMS } from 'shared/catalogs'
// Constants
const EXCEL_FILE_TYPE =
  'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
const ZIP_FILE_TYPE = 'application/zip'

const FILE_TYPES = []
FILE_TYPES[EXCEL_FILE_TYPE] = 'Excel'
FILE_TYPES[ZIP_FILE_TYPE] = 'Zip'

const FILE_ICONS = []
FILE_ICONS[EXCEL_FILE_TYPE] = 'mdi mdi-file-excel'
FILE_ICONS[ZIP_FILE_TYPE] = 'mdi mdi-folder-outline'

const FILE_TYPE_OPTIONS = [
  { id: 'xlsx', label: 'Excel' },
  { id: 'pdf', label: 'PDF' },
  { id: 'zip', label: 'Zip' },
  { id: 'txt', label: 'Texto plano' }
]

function getCurrentState () {
  const filesList = DownloadsStore.getFilesList()
  const totalPages = DownloadsStore.getTotalPages()
  const totalItems = DownloadsStore.getTotalItems()
  return {
    filesList,
    totalPages,
    totalItems
  }
}

class Downloads extends Component {
  constructor () {
    super()
    this.data = {
      filters: {
        file_name: '',
        file_type: '',
        startDate: moment().subtract(1, 'M'),
        endDate: moment()
      },
      datesOpen: false,
      alertOpen: false
    }
    this.state = Object.assign({}, this.data, getCurrentState())
    this._onChange = this._onChange.bind(this)
  }

  componentDidMount () {
    // Adds listener, when the Store changes, trigger the _onChange callback func
    DownloadsStore.addChangeListener(this._onChange)
    this.loadData()
    resetScroll()
  }

  componentDidUpdate (prevProps) {
    if (this.props.location.search !== prevProps.location.search) {
      this.loadData(this.props)
    }
  }

  componentWillUnmount () {
    // Removes the listener to avoid memory leaks
    DownloadsStore.removeChangeListener(this._onChange)
  }

  _onChange () {
    // When the store changes, the state goes to it and pulls the updated data
    // then sets it so the component rerenders
    const state = getCurrentState()
    this.setState(state)
  }

  loadData (props = this.props) {
    const params = Object.assign({}, props.location.query)
    DownloadsActions.getFilesList(params)
    this.setState(prevState => ({
      filters: Object.assign({}, prevState.filters, params)
    }))
  }

  deleteFile (id) {
    DownloadsActions.deleteFile(this.state.itemToDelete)
      .then(() => {
        this.loadData()
        NotificationsActions.success('El archivo ha sido borrado con Ã©xito')
        this.setState({ itemToDelete: '' })
      })
      .catch(err => NotificationsActions.error(err))
  }

  onChange (field, e) {
    const filter = { [field]: e.target.value }
    this.setState(prevState => ({
      filters: Object.assign({}, prevState.filters, filter)
    }))
  }

  onChangeDate (field, e) {
    const filter = { [field]: e }
    this.setState(prevState => ({
      filters: Object.assign({}, prevState.filters, filter)
    }))
  }

  toggleDatesOpen () {
    this.setState(prevState => ({
      datesOpen: !prevState.datesOpen
    }))
  }

  toggleAlert (id = '') {
    this.setState(prevState => ({
      alertOpen: !prevState.alertOpen,
      itemToDelete: id
    }))
  }

  prepareFilters (filters) {
    const { datesOpen } = this.state
    const _filters = {
      file_name: filters.file_name,
      file_type: filters.file_type
    }
    if (datesOpen) {
      Object.assign(_filters, {
        'date_range[start]': moment(filters.startDate).format('YYYY-MM-DD'),
        'date_range[end]': moment(filters.endDate).format('YYYY-MM-DD')
      })
    }
    return _filters
  }

  // Reset all filters and data to default
  clearFilters () {
    this.setState({
      filters: this.data.filters,
      datesOpen: false
    })
  }
  // Format file size to a human friendly number
  formatBytes (bytes, decimals = 2) {
    if (bytes === 0) return '0 Bytes'
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB']
    const i = Math.floor(Math.log(bytes) / Math.log(1000))
    return `${parseFloat((bytes / Math.pow(1000, i)).toFixed(decimals))} ${
      sizes[i]
    }`
  }

  renderFilters () {
    const { datesOpen, filters } = this.state
    return (
      <TableFilter
        baseUrl='/descargas'
        filters={filters}
        prepareFilters={this.prepareFilters.bind(this)}
        onClearFilters={this.clearFilters.bind(this)}
      >
        <TextField
          placeholder='Nombre'
          value={filters.file_name}
          onChange={this.onChange.bind(this, 'file_name')}
        />
        <SelectField
          options={[...ALL_ITEMS, ...FILE_TYPE_OPTIONS]}
          value={filters.file_type}
          placeholder='Tipo de archivo'
          onChange={this.onChange.bind(this, 'file_type')}
        />
        <div className='flex-column'>
          <div
            onClick={this.toggleDatesOpen.bind(this)}
            style={{ cursor: 'pointer' }}
            className='subtitle-body flex-between'
          >
            Periodo
            <i
              className={classNames('mdi', {
                'mdi-plus': !datesOpen,
                'mdi-minus': datesOpen
              })}
            />
          </div>
          <div className={classNames({ 'is-hidden': !datesOpen })}>
            <Datepicker
              label='Del'
              placeholder='Fecha inicio'
              value={filters.startDate}
              onChange={this.onChangeDate.bind(this, 'startDate')}
            />
            <Datepicker
              label='Al'
              placeholder='Fecha fin'
              value={filters.endDate}
              onChange={this.onChangeDate.bind(this, 'endDate')}
            />
          </div>
        </div>
      </TableFilter>
    )
  }

  // Render downloads table
  renderTable () {
    const { filesList, totalPages } = this.state
    let renderTable = (
      <div className='block unselectable'>
        <p>No tienes archivos generados</p>
      </div>
    )

    if (filesList.length > 0) {
      const downloads = filesList.map(file => {
        const size = this.formatBytes(file.fileFileSize, 1)
        return (
          <tr key={file.id}>
            <td className='flexCell align-left'>{file.fileFileName}</td>
            <td>
              <i className={FILE_ICONS[file.fileContentType]} />{' '}
              {FILE_TYPES[file.fileContentType]}
            </td>
            <td className='align-left'>{size}</td>
            <td>
              {prettyDate(file.createdAt, 'YYYY-MM-DD', true, false, '/')}
            </td>
            <td>
              <a style={{ marginRight: '14px' }} href={file.url} download>
                <IconButton icon='download' tooltip='Descargar' />
              </a>
              <IconButton
                icon='delete'
                onClick={this.toggleAlert.bind(this, file.id)}
                tooltip='Borrar'
              />
            </td>
          </tr>
        )
      })

      renderTable = (
        <div>
          <table className='table fixedTable'>
            <thead>
              <tr>
                <th className='align-left'>Nombre del archivo</th>
                <th style={{ width: '10rem' }}>Tipo de archivo</th>
                <th style={{ width: '5rem' }} className='align-left'>
                  TamaÃ±o
                </th>
                <th style={{ width: '8rem' }}>Creado</th>
                <th style={{ width: '8rem' }} />
              </tr>
            </thead>
            <tbody>{downloads}</tbody>
          </table>
          <Paginator totalPages={totalPages} location={this.props.location} />
        </div>
      )
    }

    return renderTable
  }

  // Main render
  render () {
    const { totalItems, alertOpen } = this.state
    return (
      <div>
        <h1 className='headline'>Descargas</h1>
        <Card>
          {this.renderFilters()}
          <div className='caption'>
            {accounting.formatNumber(totalItems)} archivos visibles
          </div>
          <br />
          {this.renderTable()}
        </Card>
        <Alert
          isActive={alertOpen}
          toggleAlert={this.toggleAlert.bind(this)}
          onSubmit={this.deleteFile.bind(this)}
        />
      </div>
    )
  }
}

Downloads.propTypes = {
  location: PropTypes.object
}

export default Downloads
