import React, { Fragment } from 'react'
import { INTEGRAL_SERVICE } from 'shared/plans/catalogsPlan'
import Tooltip from 'shared/tooltip'
import IconButton from 'shared/iconButton'
import classNames from 'classnames'
import Button from 'shared/button'
import { handleURL, formatCurrency, prettyDate } from 'utils'

const IntegralService = props => {
  const { services, nextBillingDate } = props
  let totalIVA = 0
  totalIVA = services.reduce((accumulator, service) => {
    return Number(accumulator) + Number(service.amount)
  }, 0)
  return (
    <Fragment>
      <div className='premium__row'>
        <div className='premium__row-left'>
          <p className='premium__actual-plan'>Tu plan actual es</p>
          <p className='premium__title premium__title-in-row'>
            Servicio integral
          </p>
        </div>
        <div className='premium__row-right'>
          <Button onClick={() => handleURL('/cancelar-servicio')}>
            Cancelar plan
          </Button>
        </div>
      </div>
      <p className='premium__specifications'>
        Tú próxima fecha de cobro es el:{' '}
        {prettyDate(nextBillingDate, null, false, false, ' de ')}
      </p>
      {services.map(({ amount, description }) => {
        return (
          <p className='premium__specifications'>{`${description} : ${formatCurrency(
            amount
          )}`}</p>
        )
      })}
      <p className='premium__specifications'>
        Total: {formatCurrency(totalIVA)}
      </p>
      <div className='premium__container'>
        {INTEGRAL_SERVICE.map(({ specifications }, index) => {
          return (
            <div key={index}>
              {specifications.map(({ specification, tooltip }, index) => {
                return (
                  <div key={index} className='plans__container'>
                    <i className='mdi mdi-check plans__icon' />
                    <Tooltip tooltip={tooltip} className='plans__tooltip'>
                      <p
                        className={classNames({
                          plans__specification: tooltip
                        })}
                      >
                        {specification}
                        {tooltip && (
                          <IconButton
                            icon='comment-question'
                            className='plans__tooltip-icon'
                          />
                        )}
                      </p>
                    </Tooltip>
                  </div>
                )
              })}
            </div>
          )
        })}
      </div>
    </Fragment>
  )
}

export default IntegralService
