import React from 'react'
import PropTypes from 'prop-types'
import _isArray from 'lodash/isArray'
import classNames from 'classnames'
import { Link } from 'react-router'

import TextField from 'shared/textField'

const SearchBar = ({ value, placeholder, onChange, onEnter }) => (
  <TextField
    value={value}
    placeholder={placeholder}
    onChange={onChange}
    onEnter={onEnter}
    className='searchbar'
  />
)

SearchBar.propTypes = {
  value: PropTypes.string,
  placeholder: PropTypes.string,
  onChange: PropTypes.func,
  onEnter: PropTypes.func
}

SearchBar.defaultProps = {
  value: '',
  placeholder: '',
  onEnter: () => {},
  onChange: () => {}
}

const Tabs = props => (
  <div className='tabs'>
    <ul>
      {props.title.map((tab, index) => {
        return (
          <li
            key={`cardTab${index}`}
            className={classNames({
              'is-active': tab.id === props.activeTab
            })}
          >
            <Link
              to={props.redirect ? `${props.baseUrl}/${tab.id}` : ''}
              onClick={() => {
                props.onTabSwitch(tab.id)
              }}
            >
              {tab.label}
            </Link>
          </li>
        )
      })}
    </ul>
  </div>
)

Tabs.propTypes = {
  title: PropTypes.oneOfType([PropTypes.string, PropTypes.array]),
  activeTab: PropTypes.string,
  baseUrl: PropTypes.string,
  redirect: PropTypes.bool
}

const Title = props => {
  return (
    <div
      className={classNames('card-title', {
        'has-searchbar': props.searchbar,
        'is-tabs': _isArray(props.title)
      })}
    >
      {_isArray(props.title) ? <Tabs {...props} /> : <span>{props.title}</span>}
      {props.searchbar && <SearchBar {...props.searchbar} />}
    </div>
  )
}

Title.propTypes = {
  searchbar: PropTypes.object,
  title: PropTypes.oneOfType([PropTypes.string, PropTypes.array])
}

const Card = props => {
  return (
    <div className='card-container card-container--space'>
      {props.title || props.searchbar ? <Title {...props} /> : null}
      <div className={`card-body ${props.className}`}>{props.children}</div>
    </div>
  )
}

export default Card

Card.propTypes = {
  title: PropTypes.oneOfType([PropTypes.string, PropTypes.array]),
  className: PropTypes.string,
  activeTab: PropTypes.string,
  baseUrl: PropTypes.string,
  onTabSwitch: PropTypes.func,
  redirect: PropTypes.bool,
  searchbar: PropTypes.object,
  children: PropTypes.oneOfType([PropTypes.element, PropTypes.array])
}

Card.defaultProps = {
  title: '',
  className: '',
  activeTab: '',
  baseUrl: '',
  onTabSwitch: () => {},
  redirect: true,
  searchbar: null
}

// Format for tabs array:
// title: {[
//   {
//     label: 'El titulo a mostrar',
//     id: 'el-titulo' <- este será casi siempre lo que venga en la ruta
//  },
// ]}

// Format for searchbar:
// searchbar: {
//   value: this.state.searchbarValue,
//   placeholder: 'Buscar por nombre',
//   onEnter: this.onEnter, <- la funcion que será llamada al dar enter en el searchbar
//   onChange: this.onChangeSearchbar <- la funcion que será llamada al escribir searchbar
// }
