import _includes from 'lodash/includes'
import { camelCase } from 'utils/index'

import BaseStore from './baseStore'

class EditUserStore extends BaseStore {
  constructor () {
    super()
    this.userData = {
      name: '',
      lastname: '',
      email: ''
    }
    this.userNotifications = {
      newInvoices: false,
      downloadedInvoices: false
    }

    this.actions = {
      USER_DATA_RECEIVED: action => {
        this.setUserData(action.data)
      },
      USER_NOTIFICATIONS_RECEIVED: action => {
        this.setUserNotifications(action.data)
      },
      USER_DATA_SAVED: action => {
        this.setUserData(action.data)
      },
      USER_NOTIFICATIONS_SAVED: action => {
        this.setUserNotifications(action.data)
      }
    }
  }

  // Returns stored UserData, this is used when explicitly you want to know it
  getUserData () {
    return this.userData
  }

  // Returns stored UserData, this is used when explicitly you want to know it
  getUserNotifications () {
    return this.userNotifications
  }

  // If data is received from server, stores it as UserData, otherwise stores the defaults
  setUserData (data) {
    this.userData = camelCase(data.profile)
  }

  // If data is received from server, stores it as UserNotifications, otherwise stores the defaults
  setUserNotifications (data) {
    this.userNotifications = {
      newInvoices: _includes(data.notifications, 'new_invoices'),
      downloadedInvoices: _includes(data.notifications, 'downloaded_invoices')
    }
  }
}

export default EditUserStore.getInstance()
