// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router'
import update from 'immutability-helper'

import Button from 'shared/button'

class NotifyTaxableEntities extends Component {
  constructor (props) {
    super(props)
    this.data = {
      entityId: this.props.params.entityId,
      notifications: [
        {
          selected: false,
          id: 0,
          label: 'Nuevas Facturas'
        },
        {
          selected: false,
          id: 1,
          label: 'Facturas Descargadas'
        }
      ]
    }
    this.state = Object.assign({}, this.data)
  }

  renderNewButton (right) {
    return (
      <div className='field is-grouped'>
        <span className='control'>
          <Button onClick={this.sendData.bind(this)}>Guardar</Button>
        </span>
        <span className='control'>
          <Link to='/admin/entidades_fiscales/'>
            <Button>Cancelar</Button>
          </Link>
        </span>
      </div>
    )
  }

  sendData () {
    console.table(this.state.notifications)
  }

  toggleNotification (catId) {
    let notificationPosition = 0
    // Get TRUE position of category, currently maps to 1:1, 2:2, 3:3, ..., 123:123 (zero index)
    for (let i = 0; i < this.state.notifications.length; i++) {
      if (catId === this.state.notifications[i].id) notificationPosition = i
    }
    this.setState({
      notifications: update(this.state.notifications, {
        [notificationPosition]: {
          selected: {
            $set: !this.state.notifications[notificationPosition].selected
          }
        }
      })
    })
  }

  renderNotifications () {
    let renderNotifications = (
      <article className='message'>
        <div className='message-body'>No hay notificaciones.</div>
      </article>
    )
    if (this.state.notifications.length > 0) {
      renderNotifications = this.state.notifications.map(
        function (notification, index) {
          return (
            <div key={notification.id}>
              <div className='field'>
                <p className='control'>
                  <label className='checkbox' htmlFor={notification.id}>
                    <input
                      id={notification.id}
                      type='checkbox'
                      onChange={this.toggleNotification.bind(
                        this,
                        notification.id
                      )}
                    />
                    {notification.label}
                  </label>
                </p>
              </div>
            </div>
          )
        }.bind(this)
      )
    }
    return renderNotifications
  }

  render () {
    return (
      <div>
        <h1 className='title is-4'>Notificaciones por correo electr√≥nico</h1>
        {this.renderNotifications()}
        {this.renderNewButton()}
      </div>
    )
  }
}

NotifyTaxableEntities.propTypes = {
  params: PropTypes.shape({
    entityId: PropTypes.string
  })
}

export default NotifyTaxableEntities
