import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Button from 'shared/button'
import classNames from 'classnames'
import { browserHistory } from 'react-router'

class CsdModal extends Component {
  state = {
    active: this.props.active
  }

  componentDidUpdate = prevProps => {
    if (prevProps.active !== this.props.active) {
      this.setState({ active: this.props.active })
    }
  }

  hideModal = () => {
    this.setState({ active: false })
  }
  render () {
    const { entity } = this.props
    const { active } = this.state

    return (
      <div className={classNames('modal', { 'is-active': active })}>
        <div className='modal-background' onClick={this.hideModal} />
        <div className='modal-card'>
          <header className='modal-card-head'>
            <p className='modal-card-title'>
              Falta tu certificado de Sello Digital (CSD)
            </p>
            <button
              style={{
                position: 'absolute',
                right: '0px',
                top: '6px'
              }}
              onClick={this.hideModal}
            >
              <i className='mdi mdi-close-circle-outline is-clickable' />
            </button>
          </header>
          <section className='modal-card-body'>
            Recuerda que es indispensable para emitir facturas, Â¿quieres
            configurarlo ahora?
          </section>
          <footer className='modal-card-foot'>
            <Button onClick={this.hideModal} icon='cancel'>
              Omitir
            </Button>
            <Button
              buttonStyle='secondary'
              onClick={() => {
                browserHistory.push(`/entidades/${entity}`)
              }}
              icon='check-circle-outline'
            >
              Configurar CSD
            </Button>
          </footer>
        </div>
      </div>
    )
  }
}

CsdModal.propTypes = {
  entity: PropTypes.number.isRequired
}

export default CsdModal
