// Libraries
import React from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

// Components
import TextField from 'shared/textField'
import Button from 'shared/button'

const BRAND_KEY = 'brand'
const MODEL_KEY = 'model'
const SUB_MODEL_KEY = 'sub_model'
const SERIAL_NUMBER_KEY = 'serial_number'

const requiredFields = new Set()

class SpecificDescription extends React.Component {
  constructor () {
    super()
    this.data = {
      [BRAND_KEY]: '',
      [MODEL_KEY]: '',
      [SUB_MODEL_KEY]: '',
      [SERIAL_NUMBER_KEY]: ''
    }
    this.state = { ...this.data }
  }

  componentWillUnmount () {
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validate () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate()) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  // Exports specific description object
  export () {
    return this.state
  }

  // Updates an input's value when it changes
  onChange (field, e) {
    this.setState({ [field]: e.target.value })
  }

  // Updates an async input value
  onChangeAsync (field, value) {
    this.setState({ [field]: value })
  }

  renderSpecificDescription () {
    return (
      <div className='columns'>
        <div className='column'>
          <TextField
            label='Marca'
            placeholder='Marca'
            value={this.state[BRAND_KEY]}
            onChange={this.onChange.bind(this, BRAND_KEY)}
            required
            ref={r => requiredFields.add(r)}
          />
          <br />
          <TextField
            label='Modelo'
            placeholder='Modelo'
            value={this.state[MODEL_KEY]}
            onChange={this.onChange.bind(this, MODEL_KEY)}
          />
          <br />
          <TextField
            label='Sub modelo'
            placeholder='Sub modelo'
            value={this.state[SUB_MODEL_KEY]}
            onChange={this.onChange.bind(this, SUB_MODEL_KEY)}
          />
          <br />
          <TextField
            label='Número de serie'
            placeholder='Valor total (USD)'
            value={this.state[SERIAL_NUMBER_KEY]}
            onChange={this.onChange.bind(this, SERIAL_NUMBER_KEY)}
          />
        </div>
      </div>
    )
  }

  // Renders pecific description component
  render () {
    return (
      <div>
        {this.renderSpecificDescription()}
        <div className={classNames({ 'is-hidden': !this.props.deletable })}>
          <Button
            buttonStyle='dangerLink'
            icon='delete'
            onClick={() => {
              this.props.deleteSpecificDescription(this.props.uuid)
            }}
          >
            Eliminar Descripción
          </Button>
        </div>
      </div>
    )
  }
}

SpecificDescription.propTypes = {
  uuid: PropTypes.string,
  deletable: PropTypes.bool,
  deleteSpecificDescription: PropTypes.func
}
export default SpecificDescription
