import AppDispatcher from '../dispatcher'
import AppSignal from 'appsignal'
import API from '../api'

export default {
  getRestrictions (id) {
    return API.Restrictions.GetRestrictions()
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'RESTRICTIONS_RECEIVED',
          data: response
        })
      })
      .catch(error => {
        AppSignal.sendError(new Error(error))
        throw error
      })
  },

  downloadPDF () {
    AppDispatcher.handleAction({
      actionType: 'PDF_DOWNLOADED'
    })
  }
}
