import AppDispatcher from '../dispatcher'
import API from '../api'
import AppSignal from 'appsignal'

// This action creator sends the type of action and the params (if any) to the dispatcher
export default {
  getDocumentsList (params) {
    // Request the documents list to the API, then triggers the change on the store if success
    API.Documents.List(params)
      .then(response => {
        AppDispatcher.handleAction({
          actionType: 'DOCUMENTS_LIST_RECEIVED',
          data: response
        })
      })
      .catch(error => {
        AppSignal.sendError(error)
      })
  }
}
