// Libraries
import React, { Component } from 'react'
import update from 'immutability-helper'

// Components
import { STATE_LIST } from 'shared/catalogs'
import TextField from 'shared/textField'
import SelectField from 'shared/selectField'
import { removeEmpty } from 'utils'

const STATE_OPTIONS = STATE_LIST.filter(i => i.country === 'MEX')

// Keys
const COMMITTEE_KEY = 'committee_type'
const STATE_KEY = 'state'
const STATE_ID_KEY = 'entity_key'
const STATE_TAX_ID_KEY = 'accounting_id'
const TAX_ID_WRAPPER_KEY = 'ine_accounting_mgs'

// Constants
const COMMITTEE_NATION_EXECUTIVE = 'Ejecutivo Nacional'
const COMMITTEE_STATE_EXECUTIVE = 'Ejecutivo Estatal'
const COMMITTEE_STATE_DIRECTIVE = 'Directivo Estatal'

const COMMITTEE_KINDS = [
  {
    id: COMMITTEE_NATION_EXECUTIVE,
    label: COMMITTEE_NATION_EXECUTIVE,
    stateDefault: {
      [STATE_ID_KEY]: null,
      [STATE_TAX_ID_KEY]: 0
    }
  },
  {
    id: COMMITTEE_STATE_EXECUTIVE,
    label: COMMITTEE_STATE_EXECUTIVE,
    stateDefault: {
      [STATE_ID_KEY]: STATE_OPTIONS[0].state,
      [STATE_TAX_ID_KEY]: 0
    }
  },
  {
    id: COMMITTEE_STATE_DIRECTIVE,
    label: COMMITTEE_STATE_DIRECTIVE,
    stateDefault: {
      [STATE_ID_KEY]: null,
      [STATE_TAX_ID_KEY]: null
    }
  }
]

const requiredFields = new Set()

class Ordinary extends Component {
  constructor (props) {
    super(props)
    this.data = {
      [COMMITTEE_KEY]: COMMITTEE_KINDS[0].id,
      [STATE_KEY]: COMMITTEE_KINDS[0].stateDefault
    }
    this.state = Object.assign({}, this.data)
  }

  componentWillUnmount () {
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validateRequiredFields () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate()) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  // Set target value to this.state[field]
  onChange (field, e) {
    this.setState(
      {
        [field]: update(this.state[field], { $set: e.target.value })
      },
      () => {
        // If there's a change in COMMITTEE_KEY clean all other fields
        if (field === COMMITTEE_KEY) {
          requiredFields.clear()
          const KEY = COMMITTEE_KINDS.findIndex(
            c => c.id === this.state[COMMITTEE_KEY]
          )

          this.setState({
            [STATE_KEY]: COMMITTEE_KINDS[KEY].stateDefault
          })
        }
      }
    )
  }

  // Set target value to this.state[parent][field]
  onChangeInner (parent, field, e) {
    this.setState({
      [parent]: update(this.state[parent], {
        [field]: { $set: e.target.value }
      })
    })
  }

  // Make inner data available to parent
  exportData () {
    if (this.validateRequiredFields()) {
      let data = {
        topData: {
          [COMMITTEE_KEY]: this.state[COMMITTEE_KEY]
        },
        lowData: [
          {
            [STATE_ID_KEY]: this.state[STATE_KEY][STATE_ID_KEY],
            [TAX_ID_WRAPPER_KEY]: [
              {
                [STATE_TAX_ID_KEY]: this.state[STATE_KEY][STATE_TAX_ID_KEY]
              }
            ]
          }
        ]
      }
      let cleanData = Object.assign({}, data)
      cleanData = removeEmpty(cleanData)
      return cleanData
    } else {
      return { topData: null, lowData: null }
    }
  }

  // Render ID de contabilidad
  renderTaxId () {
    return (
      <TextField
        label='ID de contabilidad'
        value={this.state[STATE_KEY][STATE_TAX_ID_KEY]}
        onChange={this.onChangeInner.bind(this, STATE_KEY, STATE_TAX_ID_KEY)}
        type='number'
        maxLength={5}
        required
        ref={r => requiredFields.add(r)}
      />
    )
  }

  // Render Clave Entidad
  renderState () {
    return (
      <div>
        <SelectField
          label='Estado: Clave Entidad'
          placeholder='Selecciona una opción'
          options={STATE_OPTIONS}
          value={this.state[STATE_KEY][STATE_ID_KEY]}
          onChange={this.onChangeInner.bind(this, STATE_KEY, STATE_ID_KEY)}
          optionsConfig={{ id: 'state', label: 'label' }}
          showId
          required
          ref={r => requiredFields.add(r)}
        />
        {this.renderTaxId()}
      </div>
    )
  }

  // Main render
  render () {
    let committee = this.state[COMMITTEE_KEY]
    let committeeOptions = {
      [COMMITTEE_NATION_EXECUTIVE]: this.renderTaxId(),
      [COMMITTEE_STATE_EXECUTIVE]: this.renderState()
    }
    return (
      <div>
        <SelectField
          label='Tipo comité'
          placeholder='Selecciona una opción'
          options={COMMITTEE_KINDS}
          value={this.state[COMMITTEE_KEY]}
          onChange={this.onChange.bind(this, COMMITTEE_KEY)}
          required
          ref={r => requiredFields.add(r)}
        />
        {committeeOptions[committee]}
      </div>
    )
  }
}

export default Ordinary
