  }
  timer = null

  componentDidMount () {
    this.getInvoice()
    RestrictionsStore.addChangeListener(this._onChange)
    RestrictionsActions.getRestrictions().catch(error => {
      NotificationsActions.error(error)
    })
  }

  componentWillUnmount () {
    clearTimeout(this.timer)
    RestrictionsStore.removeChangeListener(this._onChange)
  }

  _onChange = () => {
    this.setState(getCurrentState())
  }

  componentDidUpdate (prevProps) {
    if (prevProps.params.id !== this.props.params.id) {
      this.getInvoice()
    }
  }

  getInvoice () {
    this.setState({ requestInProgress: true }, () => {
      API.Invoice.Show(this.props.params.id)
        .then(response => {
          this.setState({
            requestInProgress: false,
            paymentDetails: response.invoice.payment_detail,
            [INVOICE_KEY]: response[INVOICE_KEY]
          })
        })
        .catch(error => {
          this.setState({ requestInProgress: false })
          AppSignal.sendError(error)
        })
    })
  }

  toggleMailModal = () => {
    this.setState(prevState => ({ mailModalOpen: !prevState.mailModalOpen }))
  }

  togglePaymentModal (paymentId = '') {
    this.setState(prevState => ({
      paymentModalOpen: !prevState.paymentModalOpen,
      paymentId: paymentId.toString()
    }))
  }

  cancelInvoice = reemit => {
    this.setState({ cancelInProgress: true }, async () => {
      const { id: invoiceID } = this.props.params
      await InvoiceActions.cancelInvoice(invoiceID)

      NotificationsActions.info(
        'Se ha comenzado el proceso de cancelaci칩n, en un momento estar치 listo',
        'infinite'
      )

      this.askingCancelStatus(reemit)
    })
  }

  askingCancelStatus = reemit => {
    const { id: invoiceID } = this.props.params
    this.timer = setTimeout(async () => {
      const cancelationStatus = await InvoiceActions.askCancelationStatus(
        invoiceID
      )
      const { inProgress, cancelled } = cancelationStatus
      switch (inProgress) {
        case true:
          this.askingCancelStatus()
          break
        case 'failed':
          this.setState({ cancelInProgress: false })
          NotificationsActions.clearAll()
          NotificationsActions.error('Ocurri칩 un error al cancelar la factura')
          break
        default:
          NotificationsActions.clearAll()

          if (cancelled) {
            NotificationsActions.success('Factura cancelada')
            if (reemit) {
              browserHistory.push(`/facturas/nueva?invoice=${invoiceID}`)
            } else {
              this.setState({ cancelInProgress: false })
            }
          } else {
            NotificationsActions.error(
              'Ocurri칩 un error al cancelar la factura'
            )
            this.setState({ cancelInProgress: false })
          }

          break
      }
    }, INTERVAL)
  }

  downloadFile (format) {
    const { id } = this.props.params
    let apiCall
    switch (format) {
      case 'xml':
        apiCall = API.Invoice.Xml
        break
      case 'pdf':
        apiCall = API.Invoice.Pdf
        break
    }
    this.setState({ requestInProgress: true }, () => {
      apiCall(id)
        .then(r => {
          if (typeof r[format] !== 'undefined') {
            fileDownload(r[format], `factura-${r.uuid || 'enconta'}.${format}`)
            NotificationsActions.success('Archivo guardado')
          }
          this.setState({ requestInProgress: false })
        })
        .catch(error => {
          AppSignal.sendError(error)
          NotificationsActions.error('El archivo no pudo descargarse')
          this.setState({ requestInProgress: false })
        })
    })
  }

  renderCancelButton () {
    let renderCancelButton = null
    const { version } = this.state[INVOICE_KEY]
    if (version === '3.2' || version === '3.3') {
      const disabledButton =
        this.state[INVOICE_KEY][PAC_REFERENCE] === null ||
        this.state[INVOICE_KEY]['cancelled_at'] !== null

      const isInvoice = this.state[INVOICE_KEY][TYPE_KEY] === 0
      const isExpensePayroll =
        this.state[INVOICE_KEY][TYPE_KEY] === 1 &&
        this.state[INVOICE_KEY].payroll

      renderCancelButton = (
        <React.Fragment>
          {(isInvoice || isExpensePayroll) && (
            <Button
              icon='repeat'
              buttonStyle={disabledButton ? '' : 'danger'}
              loading={this.state.cancelInProgress}
              onClick={() => this.cancelInvoice(true)}
              disabled={disabledButton}
            >
              Cancelar Factura y Reemitir
            </Button>
          )}
          <Button
            icon='cancel'
            buttonStyle={disabledButton ? '' : 'danger'}
            loading={this.state.cancelInProgress}
            onClick={() => this.cancelInvoice(false)}
            disabled={disabledButton}
          >
            Cancelar Factura
          </Button>
        </React.Fragment>
      )
    }
    return renderCancelButton
  }

  renderActions () {
    const { remainingSendEmails, updatePlanModal } = this.state
    const noMoreMails = remainingSendEmails === 0
    return (
      <div>
        <div className='columns'>
          <div className='column flex-end'>
            {this.renderCancelButton()}
            <Button
              buttonStyle='primary'
              icon='email'
              onClick={
                noMoreMails
                  ? () =>
                    toggleUpdatePlanModal(
                      this,
                      updatePlanModal,
                      'correos para enviar'
                    )
                  : () => this.toggleMailModal()
              }
            >
              Enviar por Correo
            </Button>
          </div>
        </div>
      </div>
    )
  }

  getInvoiceType () {
    let type = ''
    let subtype = ''
    if (!_isNil(this.state[INVOICE_KEY][TYPE_KEY])) {
      type = ENCONTA_INVOICE_TYPE[this.state[INVOICE_KEY][TYPE_KEY]].label
    }
    if (!_isNil(this.state[INVOICE_KEY][SUBTYPE_KEY])) {
      const KEY = this.state[INVOICE_KEY][SUBTYPE_KEY]
      subtype = ENCONTA_INVOICE_SUBTYPE[KEY].label.toLowerCase()
    }
    return `${type} ${subtype}`
  }

  renderCancelledInvoice () {
    return (
      this.state[INVOICE_KEY]['cancelled_at'] &&
      this.state[INVOICE_KEY]['cancelled_at'] !== null && (
        <h2 className='kinda-notification error'>
          Factura Cancelada el{' '}
          {prettyDate(this.state[INVOICE_KEY]['cancelled_at'])}
        </h2>
      )
    )
  }

  downloadPDF = () => {
    RestrictionsActions.downloadPDF()
  }

  render () {
    const {
      requestInProgress,
      remainingPdfDownloads,
      showBanner,
      remainingSendEmails,
      showBannerEmail,
      showBannerModal,
      updatePlanModal
    } = this.state
    let invoice = this.state[INVOICE_KEY]
    const isValidVersion = invoice.version === '3.3' && invoice[TYPE_KEY] === 0
    const isExpensePayroll = invoice[TYPE_KEY] === 1 && invoice.payroll
    const isComplementVoucher = invoice.voucher_type === 'p'
    const entityId = AppStorage.getCurrentEntity().id
    const disabledButton = !invoice.digital_tax_stamp
    if (requestInProgress) {
      return <Loading />
    }
    pluralize.addPluralRule('queda', 'quedan')
    const buttonInvoicesText = `Te ${pluralize(
      'queda',
      remainingPdfDownloads
    )} ${remainingPdfDownloads} PDF para descargar.`

    const buttonEmailText = `Te ${pluralize(
      'queda',
      remainingSendEmails
    )} ${remainingSendEmails} correos para enviar.`
    const { sawPdfBanner, showEmailBanner } = windowGlobal.localStorage

    const plan = getClientsPlan()
    const showPdfCounter =
      plan === 'free' && sawPdfBanner !== 'seen' && showBanner
    const showEmailCounter =
      plan === 'free' && showEmailBanner !== 'seen' && showBannerEmail
    const noMorePDF = remainingPdfDownloads === 0
    return (
      <Fragment>
        <div className='flex-between'>
          <h1 className='headline'>
            {!isComplementVoucher
              ? this.getInvoiceType()
              : 'Complemento de pago'}
          </h1>
          <div>
            {(isValidVersion || isExpensePayroll) && (
              <Link
                style={{ marginRight: '0.5rem' }}
                to={`/${
                  isExpensePayroll ? 'facturas_de_nomina' : 'facturas'
                }/nueva?invoice=${invoice.id}`}
              >
                <Button buttonStyle='white' icon='repeat'>
                  Reemitir
                </Button>
              </Link>
            )}
            {noMorePDF ? (
              <Button
                buttonStyle='white'
                icon='file-pdf'
                disabled={disabledButton}
                onClick={() =>
                  toggleUpdatePlanModal(
                    this,
                    updatePlanModal,
                    'descargas de PDF'
                  )
                }
              >
                Descargar PDF
              </Button>
            ) : (
              <a
                style={{ marginRight: '0.5rem' }}
                href={`${process.env.API_SERVER}${process.env.API_PREFIX}/pdfs/${this.props.params.id}/${entityId}`}
                download
              >
                <Button
                  buttonStyle='white'
                  icon='file-pdf'
                  disabled={disabledButton}
                  onClick={() => this.downloadPDF()}
                >
                  Descargar PDF
                </Button>
              </a>
            )}

            <Button
              buttonStyle='white'
              icon='file-xml'
              disabled={!invoice.digital_tax_stamp}
              onClick={this.downloadFile.bind(this, 'xml')}
              loading={this.state.requestInProgress}
            >
              Descargar XML
            </Button>
          </div>
        </div>
        <Card>
          {this.renderCancelledInvoice()}
          {showPdfCounter && (
            <Banner
              color={'blue'}
              buttonText={'Mejora tu plan'}
              text={buttonInvoicesText}
              controlBanner={() =>
                toggleBanner(this, showBanner, 'sawPdfBanner')
              }
              bannerAction={() => toggleBannerModal(this, showBannerModal)}
            />
          )}
          <div className='columns'>
            <Issuer invoice={invoice} />
            <Recipient invoice={invoice} />
          </div>
          <div className='columns'>
            <Details invoice={invoice} />
            {!isComplementVoucher && (
              <Payments
                invoice={invoice}
                toggleModal={this.togglePaymentModal.bind(this)}
                getInvoice={this.getInvoice.bind(this)}
              />
            )}
          </div>
          {showEmailCounter && (
            <Banner
              color={'blue'}
              buttonText={'Mejora tu plan'}
              text={buttonEmailText}
              controlBanner={() =>
                toggleBanner(this, showBannerEmail, 'showEmailBanner')
              }
              bannerAction={() => toggleBannerModal(this, showBannerModal)}
            />
          )}
          <div className='columns'>
            {!isComplementVoucher ? (
              <div className='is-fullwidth'>
                {invoice.payroll ? (
                  <PayrollConcepts invoice={invoice} />
                ) : (
                  <Concepts invoice={invoice} />
                )}
              </div>
            ) : (
              <PaymentComplement paymentDetails={this.state.paymentDetails} />
            )}
          </div>
          <br />
          <br />
          {this.renderActions()}
        </Card>
        <PaymentModal
          isActive={this.state.paymentModalOpen}
          toggleModal={this.togglePaymentModal.bind(this)}
          invoice={invoice}
          paymentId={this.state.paymentId}
          getInvoice={this.getInvoice.bind(this)}
        />
        <MailerModal
          isActive={this.state.mailModalOpen}
          toggleModal={this.toggleMailModal.bind(this)}
          invoice={invoice}
        />
        {modalPlans(this)}
        {modalUpdatePlan(this)}
      </Fragment>
    )
  }
}

ViewInvoice.propTypes = {
  params: PropTypes.shape({
    id: PropTypes.string
  })
}

export default ViewInvoice
