import React, { Component, Fragment } from 'react'
import DocumentTitle from 'react-document-title'

import NotificationsContainer from 'shared/notifications'
import Button from 'shared/button'
import {
  handleInputs,
  toggleShowHidePassword,
  formatFile,
  handleURL
} from 'utils'
import Logo from 'shared/logo'
import SelectField from 'shared/selectField'
import { FISCAL_DIAGNOSTIC } from 'shared/catalogs'
import DropZoneFile from 'shared/credentials/components/DropzoneFile'
import TextField from 'shared/textField'
import { handleSelectField, uploadFiel, uploadCiec } from './utils'
import MenuOptions from './utils/menuOptions'

import SessionStore from 'stores/session'

import NotificationsActions from 'actions/notifications'
import SatCredentialsActions from 'actions/satCredentials'
import RegisterActions from 'actions/register'

const requiredFields = new Set()
const user = 'Usuario'
const password = 'Contrase√±a'

const getCurrentState = () => {
  return {
    user: SessionStore.getCurrentEntity()
  }
}
class FiscalDiagnostic extends Component {
  state = {
    credentials: '',
    fielPassword: '',
    ciecPassword: '',
    hidden: true,
    fiel: {
      attachedKeyFile: '',
      originalKeyFilename: '',
      contentKeyType: '',
      attachedCerFile: '',
      originalCerFilename: '',
      contentCerType: ''
    },
    requestInProgress: false,
    ...getCurrentState()
  }

  componentDidMount () {
    SessionStore.addChangeListener(this._onChange)
  }

  componentWillUnmount = () => {
    SessionStore.removeChangeListener(this._onChange)
  }

  _onChange = () => {
    this.setState(getCurrentState())
  }

  setFielCer = async fileUpload => {
    try {
      const { file64, name, contentType } = await formatFile(fileUpload)
      this.setState({
        fiel: {
          ...this.state.fiel,
          attachedCerFile: file64,
          originalCerFilename: name,
          contentCerType: contentType
        }
      })
    } catch (error) {
      NotificationsActions.error(error)
    }
  }

  setFielKey = async fileUpload => {
    try {
      const { file64, name, contentType } = await formatFile(fileUpload)
      this.setState({
        fiel: {
          ...this.state.fiel,
          attachedKeyFile: file64,
          originalKeyFilename: name,
          contentKeyType: contentType
        }
      })
    } catch (error) {
      NotificationsActions.error(error)
    }
  }

  credentialsSuccessfulMessage = credentials => {
    NotificationsActions.success(
      `${credentials.toUpperCase()} guardada correctamente`
    )
    this.setState({ requestInProgress: false })
    handleURL('/diagnostico/en-proceso')
  }

  uploadCredentials = e => {
    e.preventDefault()

    const { credentials, user } = this.state

    if (credentials === 'fiel') {
      SatCredentialsActions.updateSatCredentials(uploadFiel(this.state))
        .then(() => {
          this.credentialsSuccessfulMessage(credentials)
          RegisterActions.updateSteps(user.user_id)
        })
        .catch(error => {
          NotificationsActions.error(error)
        })
    } else {
      SatCredentialsActions.updateSatCredentials(uploadCiec(this.state))
        .then(() => {
          this.credentialsSuccessfulMessage(credentials)
          RegisterActions.updateSteps(user.user_id)
        })
        .catch(error => {
          NotificationsActions.error(error)
        })
    }
  }

  renderFiel = () => {
    const { fielPassword, hidden, fiel } = this.state
    return (
      <Fragment>
        <p className='register__form-text'>Sube los archivos de tu FIEL</p>
        <div className='register__form-container'>
          <DropZoneFile
            accept='.cer'
            addFileMessage='Arrastra tu .cer aqu√≠ o da clic para adjuntarlo'
            className='dropzone-no-margin'
            onDrop={this.setFielCer}
            file={fiel.originalCerFilename}
            icon='mdi-cloud-check'
          />
          <DropZoneFile
            accept='.key'
            addFileMessage='Arrastra tu .key aqu√≠ o da clic para adjuntarlo'
            className='dropzone-no-margin'
            onDrop={this.setFielKey}
            file={fiel.originalKeyFilename}
            icon='mdi-cloud-check'
          />
        </div>
        <p className='register__form-fiscal register__form-text'>
          Ingresa la contrase√±a de tu FIEL
        </p>

        <TextField
          placeholder={password}
          floatLabel={password}
          value={fielPassword}
          onChange={handleInputs(this, 'fielPassword')}
          type={hidden ? 'password' : 'text'}
          hideAndShowPassword
          onChangeType={() => toggleShowHidePassword(this, 'hidden')}
          ref={r => requiredFields.add(r)}
          required
        />
      </Fragment>
    )
  }

  renderCiec () {
    const { ciecPassword, hidden } = this.state
    const { rfc } = this.props.params
    return (
      <Fragment>
        <p className='register__form-text'>Configura tu ciec</p>
        <div className='register__form-container register__form-container-one-column'>
          <TextField placeholder={user} floatLabel={user} value={rfc} />

          <TextField
            placeholder={password}
            floatLabel={password}
            value={ciecPassword}
            onChange={handleInputs(this, 'ciecPassword')}
            type={hidden ? 'password' : 'text'}
            hideAndShowPassword
            onChangeType={() => toggleShowHidePassword(this, 'hidden')}
            ref={r => requiredFields.add(r)}
            required
          />
        </div>
      </Fragment>
    )
  }

  renderForm () {
    const { credentials, fiel, fielPassword, ciecPassword, user } = this.state

    const disabledButton =
      (fiel.attachedCerFile.length > 0 &&
        fiel.attachedKeyFile.length > 0 &&
        fielPassword) ||
      ciecPassword

    return (
      <Fragment>
        <div className='register--left-side'>
          <div className='register--left-side-container'>
            <Logo logoSize='normal' textColour='white' />
            <p className='register--left-side__title'>
              <i className='mdi mdi-greater-than register--left-side-icon' />
              Configuraci√≥n FIEL o CIEC
            </p>
            <p className='register--left-side__subtitle'>
              Sube tu FIEL o el usuario de tu CIEC
            </p>
            <p className='register--left-side__main-option'>Diagn√≥stico</p>
          </div>
        </div>

        <div className='register--full-height'>
          <MenuOptions email={user.email} />
          <form className='register__form register__form-fiscal-diagnostic'>
            <p className='register__form-title'>
              Diagn√≥stico{' '}
              <span className='register__form-title--highlight'>fiscal</span>
            </p>

            <p className='register__form-text'>
              Para poder realizar un an√°lisis de tu situaci√≥n fiscal es
              necesario que ingreses tu{' '}
              <span className='register__form-text--highlight'>FIEL</span> √≥{' '}
              <span className='register__form-text--highlight'>CIEC</span>
            </p>

            <div className='register__form-fiscal'>
              <SelectField
                placeholder='Selecciona que deseas configurar'
                floatLabel
                options={FISCAL_DIAGNOSTIC}
                value={credentials}
                onChange={handleSelectField(this)}
                ref={r => requiredFields.add(r)}
                required
              />
            </div>

            {credentials === 'fiel'
              ? this.renderFiel()
              : credentials === 'ciec' && this.renderCiec()}

            <div className='register__form-container register__form-container-button'>
              <Button
                buttonStyle='border-white'
                className='register__form-button'
              >
                ¬øNo los tienes?
              </Button>

              <Button
                buttonStyle='round'
                type='submit'
                className='register__form-button'
                disabled={!disabledButton}
                onClick={this.uploadCredentials}
              >
                Siguiente
              </Button>
            </div>
          </form>
        </div>
      </Fragment>
    )
  }

  render () {
    return (
      <DocumentTitle title={'Diagn√≥stico - Enconta'}>
        <div className='register'>
          {this.renderForm()}
          <div className='notification-container notification-centered'>
            <NotificationsContainer />
          </div>
        </div>
      </DocumentTitle>
    )
  }
}

export default FiscalDiagnostic
