import React from 'react'
import PropTypes from 'prop-types'
import range from 'lodash/range'

const StepIndicator = ({ selected }) => (
  <div className='wizard-indicator'>
    <i className={`mdi mdi-brightness-1 ${selected ? 'selected' : ''}`} />
  </div>
)

// Steps component
const Steps = ({ currentStep, totalSteps }) => {
  return (
    <div className='wizard-indicators-container'>
      {range(totalSteps).map(stepIndex => (
        <StepIndicator
          key={`wizard-step-${stepIndex}`}
          selected={stepIndex === currentStep}
        />
      ))}
    </div>
  )
}

StepIndicator.propTypes = {
  selected: PropTypes.bool
}

StepIndicator.defaultProps = {
  selected: false
}

Steps.propTypes = {
  currentStep: PropTypes.number,
  totalSteps: PropTypes.number
}

Steps.defaultProps = {
  currentStep: 0,
  totalSteps: 0
}

export default Steps
