import React, { Component } from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'
import _isNull from 'lodash/isNull'
import _kebabCase from 'lodash/kebabCase'

// Error messages
const ERROR_TXT = {
  REQUIRED: 'Este campo es requerido'
}

class SelectField extends Component {
  static getDerivedStateFromProps (props, state) {
    const matchSomeOption = props.options.some(option =>
      typeof option === 'object'
        ? props.value === option[props.optionsConfig.id].toString()
        : props.value === option
    )

    if (matchSomeOption || props.value === '') {
      return {
        ...state,
        value: props.value
      }
    }

    return null
  }

  constructor (props) {
    super(props)
    this.data = {
      value: props.value,
      isValid: true,
      errorText: ''
    }
    this.state = Object.assign({}, this.data)
    this.onChange = this.onChange.bind(this)
    this.validate = this.validate.bind(this)
  }

  componentDidMount () {
    this.mounted = true
  }

  componentWillUnmount () {
    this.mounted = false
  }

  // When the field changes, it updates the value locally and calls the passed callback function
  // so it can also update values on the parent component
  onChange (e) {
    if (this.mounted) this.setState({ value: e.target.value }, this.validate)
    this.props.onChange(e)
  }

  // Validates if selected value is any other but the default.
  // Sets on state the validation result and error message
  // Returns boolean with the validation result.
  validate () {
    let { required } = this.props
    let isValid = !required
    let errorText = ''
    if (required) {
      isValid = !!this.state.value
      if (!isValid) {
        errorText = ERROR_TXT.REQUIRED
        this.props.onValidationError(
          `${this.props.name} - ${ERROR_TXT.REQUIRED}`
        )
      }
    }

    if (this.mounted) this.setState({ isValid, errorText })
    return isValid
  }

  // Return only a placeholder if it's defined
  renderPlaceholder () {
    const { placeholder } = this.props
    return placeholder.length > 0 ? (
      <option value='' disabled>
        {placeholder}
      </option>
    ) : null
  }

  // Iterates over the passed options collection to generate the option tags
  // It evaluates if the passed collection is an array or an object.
  // Sets them within the select tag and adds the passed default option on top
  renderOptions (opts) {
    let {
      optionsConfig,
      className,
      disabled,
      onFocus,
      onBlur,
      required,
      showId,
      disabledOptions,
      hiddenOptions,
      label,
      testId
    } = this.props
    let { value, isValid } = this.state
    if (_isNull(value)) value = ''
    let options = opts.map(opt => {
      // Iterates over passed hidden options to not display that value in the options
      if (hiddenOptions.includes(opt[optionsConfig.id])) return
      return typeof opt === 'object' ? (
        <option
          value={opt[optionsConfig.id]}
          key={opt[optionsConfig.id]}
          disabled={disabledOptions.includes(opt[optionsConfig.id])}
        >
          {showId ? `${opt[optionsConfig.id]} â€“ ` : ''}
          {opt[optionsConfig.label]}
        </option>
      ) : (
        <option value={opt} key={opt}>
          {opt}
        </option>
      )
    })
    return (
      <select
        className={classNames(className, {
          'is-danger': !isValid,
          required: required && !value
        })}
        onChange={this.onChange.bind(this)}
        disabled={disabled}
        value={value}
        onFocus={onFocus}
        onBlur={onBlur}
        required={required}
        {...(label ? { id: _kebabCase(label) } : {})}
        data-testid={testId}
      >
        {this.renderPlaceholder()}
        {options}
      </select>
    )
  }

  render () {
    let { label, options, required, info, floatLabel } = this.props
    let { isValid, errorText } = this.state
    if (required && label) label = `${label} *`

    return (
      <div
        className={classNames({
          'control float-label': floatLabel
        })}
      >
        {' '}
        {label ? (
          <label className='caption' htmlFor={_kebabCase(label)}>
            {label}
          </label>
        ) : null}
        {floatLabel && (
          <label className='caption' htmlFor={_kebabCase(floatLabel)}>
            {floatLabel}
          </label>
        )}
        <p
          className={classNames('control', {
            'has-info': info
          })}
        >
          <span className='select is-fullwidth'>
            {this.renderOptions(options)}
          </span>
          {info ? (
            <span className='icon info-icon'>
              <span className='tooltip'>{info}</span>
              <i className='mdi mdi-information-outline' />
            </span>
          ) : null}
        </p>
        <span className={classNames('help is-danger', { 'is-hidden': isValid })}>
          {errorText || ''}
        </span>
      </div>
    )
  }
}

export default SelectField

SelectField.propTypes = {
  label: PropTypes.string,
  name: PropTypes.string,
  options: PropTypes.array.isRequired,
  className: PropTypes.string,
  placeholder: PropTypes.string,
  testId: PropTypes.string,
  children: PropTypes.string.isRequired,
  value: PropTypes.oneOfType([
    PropTypes.string,
    PropTypes.shape({
      id: PropTypes.oneOfType([PropTypes.string, PropTypes.number]),
      value: PropTypes.string
    })
  ]),
  disabled: PropTypes.bool,
  required: PropTypes.bool,
  onChange: PropTypes.func,
  onFocus: PropTypes.func,
  onBlur: PropTypes.func,
  onValidationError: PropTypes.func,
  optionsConfig: PropTypes.object,
  showId: PropTypes.bool,
  disabledOptions: PropTypes.array,
  hiddenOptions: PropTypes.array,
  info: PropTypes.string
}

SelectField.defaultProps = {
  options: [],
  className: '',
  placeholder: '',
  value: '',
  name: '',
  disabled: false,
  required: false,
  onChange: () => {},
  onFocus: () => {},
  onBlur: () => {},
  onValidationError: () => {},
  optionsConfig: { id: 'id', label: 'label' },
  showId: false,
  disabledOptions: [],
  hiddenOptions: [],
  info: '',
  testId: null
}
