import React from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

/**
 * Advertisment component
 *
 * Can customize the text by sending it through the parent component
 * @param {*} props
 */

const Advertisment = props => {
  const { icon, color, text, diagnosis } = props
  return (
    <div
      className={classNames(`app-wide-notifications--notification ${color}`, {
        'app-wide-notifications--notification--diagnosis': diagnosis
      })}
    >
      <div className='columns is-vcentered'>
        {icon && (
          <div className='column is-narrow'>
            <i className={`mdi mdi-${icon} icon`} />
          </div>
        )}
        <div className='column is-11'>{text}</div>
      </div>
    </div>
  )
}

export default Advertisment

Advertisment.propTypes = {
  icon: PropTypes.string.isRequired,
  text: PropTypes.object,
  color: PropTypes.string.isRequired
}

Advertisment.defaultProps = {
  icon: '',
  text: <p>Estamos en mantenimiento</p>,
  color: ''
}
