import React, { Component, Fragment } from 'react'
import AppSignal from 'appsignal'
import {
  injectStripe,
  CardNumberElement,
  CardExpiryElement,
  CardCVCElement
} from 'react-stripe-elements'
import { Link, browserHistory, withRouter } from 'react-router'
import classNames from 'classnames'
import TextField from 'shared/textField'
import Button from 'shared/button'
import Modal from 'shared/modal'
import CardThumbnail from 'shared/cardThumbnail'

import NotificationsActions from 'actions/notifications'
import SuscriptionActions from 'actions/suscription'
import SessionAction from 'actions/session'

import SuscriptionStore from 'stores/suscription'
import SessionStore from 'stores/session'
import { handleURL } from 'utils'

export const createtokenParams = ({ paymentInformation }) => ({
  card: {
    name: paymentInformation.nameInCard,
    address_zip: paymentInformation.zipCode
  }
})

export const createPaymentSource = cardInfo => ({
  subscription: {
    payment_source_token: cardInfo
  }
})

export const requiredFields = new Set()

const styleCardInput = () => {
  return {
    style: {
      base: {
        fontSize: '16px',
        fontWeight: '400',
        fontFamily: 'Rubik, sans-serif',
        color: '#363636',
        letterSpacing: 'normal',
        '::placeholder': {
          color: '#6F7378'
        }
      },
      invalid: {
        color: '#b71c1c'
      }
    }
  }
}

const fonts = () => {
  return {
    fonts: [
      {
        src: 'https://fonts.googleapis.com/css?family=Rubik"',
        family: 'Rubik',
        style: 'normal'
      }
    ]
  }
}

class CardForm extends Component {
  state = {
    paymentInformation: {
      nameInCard: '',
      zipCode: ''
    },
    isShownModal: false,
    lastNumbers: '',
    totalCardsCreated: SuscriptionStore.getTotalPaymentSources(),
    tokenID: '',
    requestInProgress: false
  }
  componentWillUnmount () {
    requiredFields.clear()
  }

  handleInputs = field => event => {
    const value = event && event.nativeEvent ? event.target.value : event
    this.setState(state => ({
      paymentInformation: {
        ...state.paymentInformation,
        [field]: value
      }
    }))
  }

  handleCancel = () => {
    browserHistory.goBack()
    requiredFields.clear()
  }

  handleChangeCardNumber = event => {
    if (event.brand) {
      this.setState(state => ({
        paymentInformation: {
          ...state.paymentInformation,
          cardType: event.brand
        }
      }))
    }
  }

  handlezipCode = event => {
    const value = event.target.value
    const pattern = '^[0-9]{0,5}$'
    if (value.match(pattern)) {
      this.setState(state => ({
        paymentInformation: {
          ...state.paymentInformation,
          zipCode: value
        }
      }))
    }
  }

  validateAll () {
    return [...requiredFields]
      .filter(Boolean)
      .map(form => form.validate.call(this))
      .every(e => e)
  }

  onSubmit = event => {
    const { hideForm } = this.props
    const { tokenID, lastNumbers } = this.state
    const cardInfo = { payment_source: { token: tokenID } }
    const { id } = SessionStore.getCurrentEntity()
    const { pathname } = this.props.location
    const isBuyPremium = pathname.includes('contratar-premium')
    if (isBuyPremium) {
      this.setState({ requestInProgress: true })
      SuscriptionActions.createPremium(createPaymentSource(tokenID))
        .then(() => {
          this.setState({ isShownModal: false, requestInProgress: false })
          handleURL(`/pago-exitoso/${lastNumbers}`)
        })
        .catch(error => {
          this.cancelModalConfirmation()
          NotificationsActions.error(error)
        })
    } else {
      this.setState({ requestInProgress: true })
      SuscriptionActions.createPaymentSource(cardInfo)
        .then(() => {
          this.setState({ isShownModal: false, requestInProgress: false })
          hideForm()
          if (isBuyPremium) {
            SessionAction.changeCurrentEntity({ id: id }).then(() => {
              NotificationsActions.success('Pago realizado exitosamente.')
            })
          } else {
            NotificationsActions.success(
              'Método de pago agregado exitosamente.'
            )
          }
        })
        .catch(error => {
          const { rfc } = SessionStore.getCurrentEntity()
          AppSignal.addBreadcrumb({
            category: 'Pasarela de pagos',
            action: `Error: Agregar método de pago`,
            metadata: {
              token: tokenID,
              rfc: rfc,
              url: '/mi-cuenta/suscripcion/agregar-metodo-pago'
            }
          })
          AppSignal.sendError(new Error(error))
          NotificationsActions.error(error)
          this.cancelModalConfirmation()
        })
        .finally(() => {
          this.setState({ requestInProgress: false })
        })
    }
  }

  handleOnClickNoModal = () => {
    if (this.validateAll()) {
      this.props.stripe
        .createToken(createtokenParams(this.state))
        .then(({ token, error }) => {
          if (error) {
            AppSignal.sendError(new Error(error.message))
            NotificationsActions.error(error.message)
          } else {
            this.setState({
              tokenID: token.id
            })
            this.onSubmit()
          }
        })
        .catch(error => {
          AppSignal.sendError(new Error(error.message))
          NotificationsActions.error(error.message)
        })
    }
  }

  openModalConfirmation = () => {
    if (this.validateAll()) {
      this.props.stripe
        .createToken(createtokenParams(this.state))
        .then(({ token, error }) => {
          if (error) {
            NotificationsActions.error(error.message)
          } else {
            const { last4 } = token.card
            this.setState({
              isShownModal: true,
              lastNumbers: last4,
              tokenID: token.id
            })
          }
        })
        .catch(error => {
          AppSignal.sendError(new Error(error.message))
          NotificationsActions.error(error.message)
        })
    }
  }

  cancelModalConfirmation = () => {
    this.setState(state => ({
      isShownModal: !state.isShownModal
    }))
  }

  renderModalNewPayment () {
    const { isShownModal, lastNumbers, requestInProgress } = this.state
    return (
      <Modal
        isActive={isShownModal}
        toggleModal={this.cancelModalConfirmation}
        onSubmit={this.onSubmit}
        title='Crear nuevo método de pago'
        disabled={requestInProgress}
        loading={requestInProgress}
      >
        <p className='paragraph-info paragraph-info--black'>
          {`Enconta cobrará de forma automática y mensual los servicios en la
          tarjeta terminación `}{' '}
          <span className='is-semibold'>{lastNumbers}</span>{' '}
          {`hasta que cambies de tarjeta principal
          o solicites la cancelación tu suscripción.`}
        </p>
      </Modal>
    )
  }
  render () {
    const { paymentInformation, totalCardsCreated } = this.state
    const { pathname } = this.props.location
    const { premium, controlPlans } = this.props
    const isBuyPremium = pathname.includes('contratar-premium')
    const isOnSuscription = pathname.includes('mi-cuenta/suscripcion')
    const buttonText = isBuyPremium ? 'Obtener plan' : 'Agregar tarjeta'
    const { conekta } = SessionStore.getCurrentEntity()
    const conektaValidate = isBuyPremium ? false : !conekta
    return (
      <form id='card-form'>
        {isOnSuscription ? null : isBuyPremium ? (
          <p className='subheader-green'>Estás a un paso de obtener tu plan</p>
        ) : (
          <p className='subheader-green'>Agregar método de pago</p>
        )}
        {isBuyPremium && (
          <Fragment>
            <Link
              onClick={this.handleCancel}
              className='is-uppercase link--underline link-previous'
            >
              <i className='mdi mdi-chevron-double-left icon-aligned icon-aligned--margin-left' />
              Volver
            </Link>

            <p className='paragraph-info paragraph-info--font-12px'>
              {totalCardsCreated > 1
                ? `Se han registrado ${totalCardsCreated} tarjetas.`
                : `Se ha registrado ${totalCardsCreated} tarjeta.`}
            </p>
          </Fragment>
        )}

        <fieldset className='margin-top'>
          <div className='payment-form__container-direction-column'>
            <div>
              {' '}
              <p className='caption'>Nombre en la tarjeta*</p>
              <TextField
                value={paymentInformation.nameInCard}
                type='text'
                onChange={this.handleInputs('nameInCard')}
                ref={r => requiredFields.add(r)}
                required
              />
            </div>
            <div>
              <p className='caption'>Fecha de vencimiento*</p>
              <CardExpiryElement {...styleCardInput()} options={fonts} />
            </div>
          </div>
          <div className='payment-form__container-direction-column'>
            <p className='caption'>Número tarjeta*</p>
            <CardNumberElement
              {...styleCardInput()}
              options={fonts}
              onChange={this.handleChangeCardNumber}
              placeholder={''}
            />
            <div className='payment-cards'>
              <CardThumbnail
                brand={'VISA'}
                className={classNames(
                  'image img-paymentcard img-paymentcard--width-75px',
                  {
                    'img-paymentcard--grayscale':
                      paymentInformation.cardType !== 'visa'
                  }
                )}
              />
              <CardThumbnail
                brand={'MASTERCARD'}
                className={classNames(
                  'image img-paymentcard img-paymentcard--lateralmargin img-paymentcard--width-75px',
                  {
                    'img-paymentcard--grayscale':
                      paymentInformation.cardType !== 'mastercard'
                  }
                )}
              />
              <CardThumbnail
                brand={'AMERICAN EXPRESS'}
                className={classNames(
                  'image img-paymentcard img-paymentcard--width-75px',
                  {
                    'img-paymentcard--grayscale':
                      paymentInformation.cardType !== 'amex'
                  }
                )}
              />
            </div>

            <div className='payment-form__container-third-column'>
              <div>
                <p className='caption'>Código de seguridad*</p>
                <CardCVCElement {...styleCardInput()} options={fonts} />
              </div>
              <div>
                <p className='caption'>Código Postal *</p>
                <TextField
                  value={paymentInformation.zipCode}
                  type='text'
                  onChange={this.handlezipCode}
                  ref={r => requiredFields.add(r)}
                  required
                />
              </div>
            </div>
            {isBuyPremium && (
              <div className='payment-form__details-container'>
                <p className='payment-form__details-container-total'>
                  Total a pagar:{' '}
                </p>
                <p className='payment-form__details-container-price'>
                  $499 MXN
                </p>
                <p className='payment-form__details-container-iva'>
                  más IVA Mensual
                </p>
              </div>
            )}
          </div>
        </fieldset>
        <div
          className={classNames('flex-end margin-top', {
            'flex-start': premium
          })}
        >
          <Button
            onClick={premium ? controlPlans : this.handleCancel}
            icon={`mdi ${premium ? 'mdi-eye' : 'mdi-cancel'}`}
          >
            {premium ? 'Ver planes' : 'Cancelar'}
          </Button>
          <Button
            buttonStyle='primary'
            onClick={
              Number(totalCardsCreated) > 0
                ? this.handleOnClickNoModal
                : this.openModalConfirmation
            }
            icon={`mdi ${isOnSuscription ? 'mdi-plus' : 'mdi-content-save'}`}
            disabled={conektaValidate}
          >
            {buttonText}
          </Button>
        </div>
        <p className='caption required payment-form__required'>
          *Campos obligatorios
        </p>
        {this.renderModalNewPayment()}
      </form>
    )
  }
}

export default withRouter(injectStripe(CardForm))
