import React, { Component } from 'react'
import PropTypes from 'prop-types'
import accounting from 'accounting'
import { Creatable } from 'react-select'
import SessionStore from 'stores/session'
import classNames from 'classnames'
import { browserHistory } from 'react-router'
import AppSignal from 'appsignal'

import NotificationsActions from 'actions/notifications'
import Modal from 'shared/modal'
import TextField from 'shared/textField'
import { prettyDate } from 'utils'

import InvoicesActions from 'actions/invoices'

const requiredFields = new Set()

const REPLY_KEY = 'reply_to'

class MailerModal extends Component {
  constructor () {
    super()
    this.data = {
      from: SessionStore.getCurrentEntity().email,
      to: [],
      [REPLY_KEY]: SessionStore.getCurrentEntity().email,
      message: '',
      attachments: {
        pdf: true,
        xml: true
      },
      isValid: {
        to: true,
        message: true
      }
    }
    this.state = Object.assign({}, this.data)
  }

  componentWillUnmount () {
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validateAll () {
    return (
      this.isValidTo() &&
      [...requiredFields] // Converts the Set() into an array
        .filter(Boolean) // Filters the array from any falsy value
        .map(form => form.validate.call(this)) // Calls the validate() method on each form
        .every(e => e) // Checks if all validations suceeded
    )
  }

  onChange (field, e) {
    this.setState({ [field]: e.target.value }, () => {
      if (field === 'message') this.isValidMessage()
    })
  }

  onChangeTo (value) {
    this.setState({ to: value }, this.isValidTo)
  }

  isValidTo () {
    this.setState(prevState => ({
      isValid: Object.assign({}, prevState.isValid, {
        to: prevState.to.length > 0
      })
    }))
    return this.state.to.length > 0
  }

  isValidMessage () {
    this.setState(prevState => ({
      isValid: Object.assign({}, prevState.isValid, {
        message: prevState.message.length > 0
      })
    }))
    return this.state.message.length > 0
  }

  onToggleAttachments (file) {
    this.setState(prevState => ({
      attachments: Object.assign({}, prevState.attachments, {
        [file]: !prevState.attachments[file]
      })
    }))
  }

  sendInvoice = () => {
    const { id } = this.props.invoice
    let { from, to, [REPLY_KEY]: replyTo, message, attachments } = this.state
    let data = {
      email: {
        from,
        [REPLY_KEY]: replyTo,
        message,
        attachments,
        to: to.reduce(
          (acc, curr) => `${acc}${acc ? ', ' : ''}${curr.value}`,
          ''
        )
      }
    }
    if (this.validateAll()) {
      InvoicesActions.sendEmail(id, data)
        .then(r => {
          NotificationsActions.success(
            'La factura ha sido enviada correctamente'
          )
          if (this.props.redirectOnSubmit) {
            browserHistory.push(`/facturas/ver/${this.props.invoice.id}`)
          } else {
            this.props.toggleModal()
          }
        })
        .catch(error => {
          AppSignal.sendError(error)
          NotificationsActions.error(error)
        })
    } else {
      NotificationsActions.error(
        'Es necesario llenar todos los campos requeridos.'
      )
    }
  }

  render () {
    let { isActive, toggleModal, invoice } = this.props
    let {
      from,
      to,
      [REPLY_KEY]: replyTo,
      message,
      attachments,
      isValid
    } = this.state
    return (
      <Modal
        title='Enviar por Correo'
        isActive={isActive}
        toggleModal={toggleModal}
        onSubmit={() => this.sendInvoice()}
      >
        <form onSubmit={() => this.sendInvoice()}>
          <div className='columns is-multiline'>
            <div className='column is-12 flex-column'>
              <span className='subtitle-body'>
                Emisor: {invoice.issuer ? invoice.issuer.name : ''}
              </span>
              <span className='subtitle-body'>
                Receptor: {invoice.recipient ? invoice.recipient.name : ''}
              </span>
              <span className='body'>Folio: {invoice.folio}</span>
              <span className='body'>
                Fecha de emisi칩n: {prettyDate(invoice.issue_date)}
              </span>
              <span className='body'>
                Total: {accounting.formatMoney(invoice.total_cents / 100, '$ ')}
              </span>
            </div>
            <div className='column is-12'>
              <TextField
                label='De'
                type='email'
                value={from}
                onChange={this.onChange.bind(this, 'from')}
                required
                ref={r => requiredFields.add(r)}
              />
            </div>
            <div className='column is-12'>
              <label className='caption'>Para *</label>
              <Creatable
                multi
                placeholder=''
                searchPromptText='Ingrese los correos electr칩nicos'
                noResultsText='Ingrese los correos electr칩nicos'
                value={to}
                options={[]}
                onChange={this.onChangeTo.bind(this)}
                onBlur={this.isValidTo.bind(this)}
                promptTextCreator={label => `A침adir correo "${label}"`}
              />
              <span
                className={classNames('help is-danger', {
                  'is-hidden': isValid.to
                })}
              >
                Este campo es requerido
              </span>
            </div>
            <div className='column is-12'>
              <TextField
                label='Responder a'
                type='email'
                value={replyTo}
                onChange={this.onChange.bind(this, REPLY_KEY)}
              />
            </div>
            <div className='column is-12'>
              <label className='caption'>Mensaje *</label>
              <textarea
                className={classNames('textarea', {
                  'is-danger': !isValid.message
                })}
                value={message}
                onChange={this.onChange.bind(this, 'message')}
                onBlur={this.isValidMessage.bind(this)}
                required
              />
              <span
                className={classNames('help is-danger', {
                  'is-hidden': isValid.message
                })}
              >
                Este campo es requerido
              </span>
            </div>
            <div className='column is-12'>
              <label className='caption'>Archivos adjuntos</label>
              <div className='control flex-column'>
                <div>
                  <input
                    type='checkbox'
                    onChange={this.onToggleAttachments.bind(this, 'pdf')}
                    checked={attachments.pdf}
                  />
                  <span className='body'>
                    &nbsp; <i className='mdi mdi-file-pdf' /> PDF
                  </span>
                </div>
                <div>
                  <input
                    type='checkbox'
                    onChange={this.onToggleAttachments.bind(this, 'xml')}
                    checked={attachments.xml}
                  />
                  <span className='body'>
                    &nbsp; <i className='mdi mdi-file-xml' /> XML
                  </span>
                </div>
              </div>
            </div>
            <br />
            <div className='caption required'>* Campos obligatorios</div>
            <br />
          </div>
        </form>
      </Modal>
    )
  }
}

export default MailerModal

MailerModal.propTypes = {
  invoice: PropTypes.object.isRequired,
  isActive: PropTypes.bool,
  redirectOnSubmit: PropTypes.bool,
  toggleModal: PropTypes.func
}

MailerModal.defaultProps = {
  isActive: false,
  toggleModal: () => {},
  redirectOnSubmit: false
}
