// Libraries
import React from 'react'
import PropTypes from 'prop-types'
import accounting from 'accounting'

import { TAXES } from 'shared/catalogs'

// Render Concepts for invoice with version 3.2
const render33 = invoice => {
  let concepts = invoice.line_items
  let renderConcepts = null
  let renderTotals = null
  if (concepts.length > 0) {
    renderConcepts = concepts.map((concept, index) => {
      return (
        <tr key={index}>
          <td>{concept.quantity}</td>
          <td>{concept.unit_type}</td>
          <td className='align-left'>{concept.description}</td>
          <td className='has-text-right with-border-bottom'>
            {accounting.formatMoney(concept.unit_price_cents / 100, '$ ', 2)}
          </td>
          <td className='has-text-right with-border-bottom'>
            {accounting.formatMoney(concept.total_cents / 100, '$ ', 2)}
          </td>
        </tr>
      )
    })
    renderTotals = (
      <tfoot>
        <tr className='no-border'>
          <td colSpan={3} className='with-border-top' />
          <th className='has-text-right with-border-bottom with-borer-top'>
            Subtotal
          </th>
          <td className='has-text-right with-border-bottom with-borer-top'>
            {accounting.formatMoney(
              concepts
                .map(concept => concept.total_cents)
                .reduce((acc, curr) => acc + curr, 0) / 100,
              '$ ',
              2
            )}
          </td>
        </tr>
        {invoice.tax_detail &&
          invoice.tax_detail.retained_taxes &&
          invoice.tax_detail.retained_taxes.map(tax => (
            <tr key={tax._id.$oid} className='no-border'>
              <td colSpan={3} />
              <th className='has-text-right with-border-bottom'>
                {`${
                  invoice.version === '3.2'
                    ? tax.tax_name
                    : TAXES.find(taxType => taxType.id === tax.tax_name).label
                } retenido`}
              </th>
              <td className='has-text-right with-border-bottom'>
                ({accounting.formatMoney(tax.amount_cents / 100, '$ ', 2)})
              </td>
            </tr>
          ))}
        {invoice.tax_detail &&
          invoice.tax_detail.transferred_taxes &&
          invoice.tax_detail.transferred_taxes.map(tax => (
            <tr key={tax._id.$oid} className='no-border'>
              <td colSpan={3} />
              <th className='has-text-right with-border-bottom'>
                {`${
                  invoice.version === '3.2'
                    ? tax.tax_name
                    : TAXES.find(taxType => taxType.id === tax.tax_name).label
                } trasladado`}
              </th>
              <td className='has-text-right with-border-bottom'>
                {accounting.formatMoney(tax.amount_cents / 100, '$ ', 2)}
              </td>
            </tr>
          ))}
        {invoice.discount_cents ? (
          <tr key='discount_cents' className='no-border'>
            <td colSpan={3} />
            <th className='has-text-right with-border-bottom'>Descuento</th>
            <td className='has-text-right with-border-bottom'>
              ({accounting.formatMoney(invoice.discount_cents / 100, '$ ', 2)})
            </td>
          </tr>
        ) : null}
        <tr className='no-border'>
          <td colSpan={3} />
          <th className='has-text-right is-bold'>Total</th>
          <td className='has-text-right'>
            {accounting.formatMoney(invoice.total_cents / 100, '$ ', 2)}
          </td>
        </tr>
      </tfoot>
    )
  }
  return (
    <table className='table'>
      <thead>
        <tr>
          <th style={{ width: '8rem' }}>Cantidad</th>
          <th style={{ minWidth: '8rem' }}>Unidad</th>
          <th>Descripci√≥n</th>
          <th style={{ width: '9rem' }}>Precio Unitario</th>
          <th style={{ width: '8rem' }}>Total</th>
        </tr>
      </thead>
      <tbody>{renderConcepts}</tbody>
      {renderTotals}
    </table>
  )
}

const Concepts = props => {
  // Main render
  let concepts = (
    <div className='block unselectable'>
      <p>No hay conceptos</p>
    </div>
  )
  switch (props.invoice.version) {
    case '3.2':
      concepts = render33(props.invoice)
      break
    case '3.3':
      concepts = render33(props.invoice)
      break
  }
  return (
    <div className='column'>
      <h4 className='subheader-green'>Conceptos</h4>
      <div className='columns'>
        <div className='column'>{concepts}</div>
      </div>
    </div>
  )
}

Concepts.propTypes = {
  invoice: PropTypes.shape({
    version: PropTypes.string
  })
}

export default Concepts
