import React from 'react'
import PropTypes from 'prop-types'
import accounting from 'accounting'
import classNames from 'classnames'
import _isNil from 'lodash/isNil'
import { Link, browserHistory } from 'react-router'
import AppSignal from 'appsignal'

// Components
import API from 'api'
import NotificationsActions from 'actions/notifications'
import StatusLabel from 'shared/statusLabel'
import IconButton from 'shared/iconButton'
import Button from 'shared/button'
import { prettyDate } from 'utils'

const PAYMENT_STATUS = [
  'pending_payment',
  'partially_paid',
  'cancelled',
  'paid'
]

const deletePayment = (paymentId, getInvoice) => {
  API.Payments.Delete(paymentId)
    .then(r => {
      getInvoice()
      NotificationsActions.success('El pago fue borrado correctamente')
    })
    .catch(error => {
      AppSignal.sendError(error)
      NotificationsActions.error(error)
    })
}

const showPaymentSupplement = invoiceID => {
  browserHistory.push(`/facturas/ver/${invoiceID}`)
}
const Payment = ({ payment, toggleModal, getInvoice }) => (
  <tr key={payment.id} className='hover-row'>
    <td className='flex-center'>
      {prettyDate(payment.paid_on, '', true, false, '-')}
      {!payment.invoice_payment_id && (
        <IconButton
          icon='information-outline'
          tooltip='Creado por el cliente'
          className='aligned--icon-rigth'
        />
      )}
    </td>
    <td className='has-text-right'>
      {accounting.formatMoney(payment.amount_cents / 100, '$ ')}
    </td>
    {payment.invoice_payment_id ? (
      <td
        className={payment.invoice_payment_id ? 'is-clickable' : ''}
        onClick={() => showPaymentSupplement(payment.invoice_payment_id)}
      >
        <Link
          to={`/facturas/ver/${payment.invoice_payment_id}`}
          className='hover-link'
        >
          Ver
        </Link>
      </td>
    ) : (
      <td>
        <IconButton
          icon='pencil'
          tooltip='Editar registro'
          onClick={() => {
            toggleModal(payment.id)
          }}
        />
        <IconButton
          icon='delete'
          tooltip='Eliminar registro'
          onClick={() => {
            deletePayment(payment.id, getInvoice)
          }}
        />
      </td>
    )}
  </tr>
)
const getTotalPaid = ({ payments }) => {
  const total = payments.reduce(
    (total, { amount_cents: amount }) => total + amount,
    0
  )
  return total
}

const PaymentsTable = ({ invoice, toggleModal, type, getInvoice }) => {
  let payments = (
    <tr>
      <td colSpan={4} className='caption'>
        No hay {type === 0 ? 'cobros' : 'pagos'} registrados
      </td>
    </tr>
  )
  let paidTotal = 0
  let outstandingAmount = 0
  if (invoice.payments && invoice.payments.length > 0) {
    paidTotal = getTotalPaid(invoice)
    outstandingAmount = invoice.total_cents - paidTotal
    payments = invoice.payments.map(payment => (
      <Payment
        payment={payment}
        toggleModal={toggleModal}
        getInvoice={getInvoice}
      />
    ))
  }
  return (
    <table className='paytable'>
      <thead>
        <tr>
          <th>Fecha</th>
          <th className='has-text-right'>Importe</th>
          <th className='align-center' />
        </tr>
      </thead>
      <tbody>
        {payments}
        <tr>
          <td colSpan={3} className='paid-amount-separator' />
        </tr>
        <tr>
          <td colSpan={2} className='has-text-right'>
            <p className='total-to-pay'>
              <span className='total-to-pay__amount total-to-pay__amount--normal'>
                {accounting.formatMoney(paidTotal / 100, '$ ')}
              </span>
              <span className='total-to-pay__label'>Total pagado</span>
            </p>
          </td>
        </tr>
        {outstandingAmount > 0 && (
          <tr>
            <td colSpan={2} className='outstanding-amount'>
              <p className='body body--grey-darker'>
                {`Resta por pagar: ${accounting.formatMoney(
                  outstandingAmount / 100,
                  '$ '
                )}`}
              </p>
            </td>
          </tr>
        )}
      </tbody>
    </table>
  )
}

const Payments = props => (
  <div className='column'>
    <h2 className='subheader-green'>
      Informaci√≥n de {props.type === 0 ? 'cobro' : 'pago'}
    </h2>
    <div className='columns is-multiline'>
      <div className='column'>
        <label className='caption'>
          Monto por {props.type === 0 ? 'cobrar' : 'pagar'}:
        </label>
        <br />
        <span className='body-bold'>
          {accounting.formatMoney(props.invoice.total_cents / 100, '$ ')}
        </span>
      </div>
      <div
        className={classNames('column', {
          'is-hidden': _isNil(props.invoice.currency)
        })}
      >
        <label className='caption'>Moneda:</label>
        <br />
        <span className='body'>{props.invoice.currency}</span>
      </div>
      <div
        className={classNames('column', {
          'is-hidden': _isNil(props.invoice.exchange_rate)
        })}
      >
        <label className='caption'>Tipo de cambio:</label>
        <br />
        <span className='body'>
          {accounting.formatMoney(props.invoice.exchange_rate, '')}
        </span>
      </div>
      <div
        className={classNames('column', {
          'is-hidden': _isNil(props.invoice.payment_status)
        })}
      >
        <label className='caption'>Estado:</label>
        <br />
        <span className='body'>
          <StatusLabel
            status={PAYMENT_STATUS[props.invoice.payment_status]}
            type={props.invoice.type === 0 ? 'income' : 'expense'}
          />
        </span>
      </div>
    </div>
    <br />
    <PaymentsTable {...props} />
    <Button
      className={classNames({
        'is-hidden': props.invoice.payment_status === '3'
      })}
      buttonStyle='primaryLink'
      icon='plus'
      onClick={() => {
        props.toggleModal()
      }}
    >{`Registrar ${props.type === 0 ? 'cobro' : 'pago'}`}</Button>
  </div>
)

export default Payments

Payments.propTypes = {
  invoice: PropTypes.object.isRequired,
  toggleModal: PropTypes.func,
  getInvoice: PropTypes.func,
  type: PropTypes.number
}

Payment.propTypes = {
  payment: PropTypes.object,
  toggleModal: PropTypes.func,
  getInvoice: PropTypes.func
}

Payments.defaultProps = {
  toggleModal: () => {},
  getInvoice: () => {}
}

PaymentsTable.propTypes = {
  invoice: PropTypes.object,
  toggleModal: PropTypes.func,
  type: PropTypes.number,
  getInvoice: PropTypes.func
}
