import React from 'react'
import classNames from 'classnames'
import PropTypes from 'prop-types'

/**
 *
 * Stepper for Smart-Sync process
 * @param {*} props
 */
const StepperSmartSync = props => {
  const { status } = props

  const bankSelected = status === 'firstElement'
  const registerInProgress = status === 'secondElement'
  const failed = status === 'failed'
  const registerFinished = status === 'secondElementFinished'
  const accountsSelected = status === 'thirdElementCompleted'
  const firsStepFinished =
    bankSelected ||
    registerInProgress ||
    failed ||
    registerFinished ||
    accountsSelected
  return (
    <div className='stepper-smart-sync'>
      <i className='mdi mdi-bank stepper-smart-sync__icon stepper-smart-sync__icon--active' />
      <div className='stepper-smart-sync__rectangle'>
        <div
          className={classNames(
            'stepper-smart-sync__rectangle stepper-smart-sync__rectangle--active',
            {
              'stepper-smart-sync__rectangle--completed': firsStepFinished
            }
          )}
        />
      </div>

      <i
        className={classNames(
          'mdi mdi-account-circle-outline stepper-smart-sync__icon stepper-smart-sync__icon--inactive',
          {
            'stepper-smart-sync__icon stepper-smart-sync__icon--active': firsStepFinished
          }
        )}
      />
      <div className='stepper-smart-sync__rectangle'>
        <div
          className={classNames('stepper-smart-sync__rectangle', {
            'stepper-smart-sync__rectangle--active': bankSelected,
            'stepper-smart-sync__rectangle--medium': registerInProgress,
            'stepper-smart-sync__rectangle--failed': failed,
            'stepper-smart-sync__rectangle--completed':
              registerFinished || accountsSelected
          })}
        />
      </div>

      <i
        className={classNames(
          'mdi mdi-file-document-outline stepper-smart-sync__icon stepper-smart-sync__icon--inactive',
          {
            'stepper-smart-sync__icon--active': accountsSelected
          }
        )}
      />
      <div
        className={classNames('stepper-smart-sync__rectangle', {
          'stepper-smart-sync__rectangle': bankSelected
        })}
      >
        <div
          className={classNames('stepper-smart-sync__rectangle', {
            'stepper-smart-sync__rectangle--active': registerFinished,
            'stepper-smart-sync__rectangle--completed': accountsSelected
          })}
        />
      </div>
    </div>
  )
}

export default StepperSmartSync

StepperSmartSync.propTypes = {
  status: PropTypes.string.isRequired
}

StepperSmartSync.defaultProps = {
  status: ''
}
