// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import moment from 'moment'
import update from 'immutability-helper'
import { Link } from 'react-router'
import AppSignal from 'appsignal'

// Stores - actions
import SatCredentialsStore from 'stores/satCredentials'
import SatCredentialsActions from 'actions/satCredentials'
import SessionStore from 'stores/session'

// Invoice sub-views
import CredentialsExpire from 'shared/credentials'
import SelectFile from 'shared/selectFile'
import Button from 'shared/button'
import Card from 'shared/card'
import API from 'api'
import NotificationsActions from 'actions/notifications'
import {
  prettyDate,
  FILE_TEMPLATE_URL,
  hasDifference,
  askRemainingPlanDays
} from 'utils'
import { INVOICING_STATUS } from 'shared/catalogs'

// Billing types for multiple invoicing
const BILLING_TYPE = [
  {
    id: 'regular',
    billingType: 'sales',
    label: 'Regulares',
    uploadFile: API.MultipleInvoicing.Create
  },
  {
    id: 'a_terceros',
    billingType: 'sales_third_party',
    label: 'A terceros',
    uploadFile: API.MultipleInvoicing.CreateThirdParty
  }
]

// Keys for file uploader
const FILE_KEY = 'invoice_import_file'
const FILE_DATA_KEY = 'batch_file'
const FILE_NAME_KEY = 'batch_file_file_name'
const FILE_TYPE_KEY = 'batch_file_content_type'

// Render single invoice
const renderInvoice = (invoice, tab) => {
  return (
    <tr key={invoice.id}>
      <td className='align-left'>
        {prettyDate(invoice.created_at, 'YYYY-MM-DD', true, true, '/')}
        <br />
        <small>
          {moment(invoice.created_at, 'YYYY-MM-DDTHH:mm:SS').format('HH:mm A')}
        </small>
      </td>
      <td className='align-left'>{INVOICING_STATUS[invoice.status]}</td>
      <td className='align-right'>
        <Link to={`/facturacion_masiva/proceso/${tab}/${invoice.id}`}>
          {invoice.batch_file_file_name}
        </Link>
      </td>
    </tr>
  )
}

const getCurrentState = () => ({
  credentials: SatCredentialsStore.getCredentials(),
  currentUser: SessionStore.getUser()
})

class MultipleInvoicing extends Component {
  constructor (props) {
    super(props)
    this.data = {
      showUpload: false,
      [FILE_KEY]: {
        [FILE_DATA_KEY]: '',
        [FILE_NAME_KEY]: '',
        [FILE_TYPE_KEY]: ''
      },
      invoices: [],
      tab: this.props.route.tab
    }
    this.state = { ...this.data, ...getCurrentState() }
    this._handleEscape = this._handleEscape.bind(this)
  }

  // Get invoices list on component's mount
  componentDidMount () {
    SatCredentialsStore.addChangeListener(this._onChange)

    this.loadData()
    this.loadInvoices()
    askRemainingPlanDays()
  }

  _onChange = () => {
    // When the store changes, the state goes to it and pulls the updated data
    // then sets it so the component rerenders
    const { credentials } = getCurrentState()

    if (hasDifference(this.state.credentials, credentials)) {
      this.setState({ credentials })
    }
  }

  loadData () {
    SatCredentialsActions.getSatCredentials().catch(e =>
      NotificationsActions.error(e)
    )
  }

  componentWillUnmount () {
    SatCredentialsStore.removeChangeListener(this._onChange)
  }

  // Get invoices list
  loadInvoices () {
    API.MultipleInvoicing.List({
      billing_type: BILLING_TYPE.find(type => type.id === this.state.tab)
        .billingType
    })
      .then(r => {
        this.setState({ invoices: r.invoice_imports })
      })
      .catch(error => {
        AppSignal.sendError(error)
        NotificationsActions.error(error)
      })
  }

  // Sets Diot file data (base64 string) and original filename from SelectFile component
  setUpload (doc, filename, contentType) {
    this.setState(
      {
        [FILE_KEY]: update(this.state[FILE_KEY], {
          [FILE_DATA_KEY]: { $set: doc },
          [FILE_NAME_KEY]: { $set: filename },
          [FILE_TYPE_KEY]: { $set: contentType }
        })
      },
      this.uploadFile
    )
  }

  // Listen for key presses, close the dialog if escape is true
  _handleEscape (key, b) {
    if (key.keyCode === 27) {
      window.removeEventListener('keydown', this._handleEscape, true)
      this.setState({ showUpload: false })
    }
  }

  // Toggle this.state.showUpload, enables upload file form
  toggleUpload () {
    if (!this.state.showUpload) {
      window.addEventListener('keydown', this._handleEscape, true)
    }
    this.setState({ showUpload: !this.state.showUpload })
  }

  // Clean file upload
  cleanUpload () {
    this.setState(
      {
        [FILE_KEY]: this.data[FILE_KEY],
        showUpload: false
      },
      this.loadInvoices()
    )
  }

  // Handle file upload
  uploadFile () {
    BILLING_TYPE.find(type => type.id === this.state.tab)
      .uploadFile({
        [FILE_KEY]: this.state[FILE_KEY]
      })
      .then(r => {
        NotificationsActions.success('Facturaci贸n masiva iniciada')
        this.cleanUpload()
      })
      .catch(r => {
        NotificationsActions.error('Error al procesar archivo')
        this.cleanUpload()
      })
  }

  // Set tab and load new data
  setTab (tab) {
    this.setState({ tab }, this.loadInvoices)
  }

  // Render past multiple invoice attempts
  renderInvoices () {
    let { tab } = this.state
    let renderInvoices = (
      <div className='block unselectable'>
        <p>No hay facturaci贸n masiva</p>
      </div>
    )
    if (this.state.invoices.length > 0) {
      let invoices = this.state.invoices.map(invoice =>
        renderInvoice(invoice, tab)
      )
      renderInvoices = (
        <table className='table is-fullwidth'>
          <thead>
            <tr>
              <th className='align-left'>Creado</th>
              <th className='align-left'>Estado</th>
              <th className='align-right'>Nombre del archivo</th>
            </tr>
          </thead>
          <tbody>{invoices}</tbody>
        </table>
      )
    }
    return renderInvoices
  }

  // Render view title
  renderTitle () {
    const { credentials, currentUser } = this.state
    const hasCSD = Boolean(credentials.csd)
    const { role } = currentUser
    const allowedUser = ['admin', 'account_holder', 'analyst']
    const disabled = !(hasCSD && allowedUser.includes(role))

    return (
      <div className='flex-between'>
        <h1 className='headline'>Facturaci贸n Masiva</h1>
        <div className='flex-column flex-end' style={{ display: 'inline-block' }}>
          <Button
            icon='file-excel'
            buttonStyle='white'
            onClick={this.toggleUpload.bind(this)}
            disabled={disabled}
          >
            Subir archivo
          </Button>
          <a
            href={FILE_TEMPLATE_URL}
            target='_blank'
            download
            className='encontaButton white'
          >
            <span>
              <i className='mdi mdi-download' />
              Descargar formato
            </span>
          </a>
        </div>
      </div>
    )
  }

  // Main render
  render () {
    return (
      <div>
        <CredentialsExpire csd />
        {this.renderTitle()}
        <Card
          className='is-fullheight flex-column'
          title={BILLING_TYPE}
          activeTab={this.state.tab}
          baseUrl='/facturacion_masiva'
          onTabSwitch={this.setTab.bind(this)}
        >
          {this.renderInvoices()}
        </Card>

        <SelectFile
          isActive={this.state.showUpload}
          toggleModal={this.toggleUpload.bind(this)}
          title='Subir Archivo'
          fileTypePlaceholder='archivo'
          setFile={this.setUpload.bind(this)}
        />
      </div>
    )
  }
}

MultipleInvoicing.propTypes = {
  route: PropTypes.shape({
    tab: PropTypes.number
  })
}

export default MultipleInvoicing
