// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'

// Componens
import NotificationsActions from 'actions/notifications'
import MessagesActions from 'actions/messages'
import MessagesStore from 'stores/messages'
import ReadMessage from './message'
import { generateMessage } from '../utils'
import Button from 'shared/button'
import Paginator from 'shared/paginator'

const getCurrentState = () => ({
  messages: MessagesStore.getMessages(),
  pagination: MessagesStore.getPagination()
})

class Messages extends Component {
  static propTypes = {
    period: PropTypes.string.isRequired,
    location: PropTypes.shape({
      query: PropTypes.shape({
        page: PropTypes.string
      })
    }).isRequired
  }

  static defaultProps = {
    location: {
      query: {
        page: '1'
      }
    }
  }

  state = {
    messages: [],
    pagination: {},
    loading: false,
    alert: false
  }

  componentDidMount = () => {
    MessagesStore.addChangeListener(this._onChage)
    const { period } = this.props
    this._loadData(period)
  }

  componentDidUpdate (prevProps) {
    if (prevProps.location.query.page !== this.props.location.query.page) {
      this._loadData(this.props.period)
    }
  }

  componentWillUnmount = () => {
    MessagesStore.removeChangeListener(this._onChage)
  }

  _loadData = async period => {
    const { query } = this.props.location
    MessagesActions.listMessages({ period, query }).catch(error => {
      console.error(error)
      NotificationsActions.error('No me he podido conectar con el servidor')
    })
  }

  _onChage = () => {
    this.setState(getCurrentState())
  }

  submitMessage = e => {
    e.preventDefault()
    e.stopPropagation()
    e.persist()

    if (
      this.textMessage.current.value !== '' &&
      this.textMessage.current.value.trim()
    ) {
      this.addMessage(generateMessage(this.textMessage.current.value))
    } else {
      this.setState({ alert: true })
    }

    this.textMessage.current.value = ''
  }

  addMessage = async messageData => {
    const { period } = this.props
    try {
      this.setState({ loading: true })

      await MessagesActions.createMessage({ period, messageData })

      this.setState({ loading: false, alert: false })

      this.textMessage.current.focus()
    } catch (error) {
      NotificationsActions.error(error)
      this.textMessage.current.value = messageData.message.message
      this.setState({ loading: false })
    }
  }

  textMessage = React.createRef()

  render () {
    const { messages, loading, pagination, alert } = this.state

    return (
      <div className='field espacio-medium'>
        {alert && (
          <div className='app-wide-notifications--notification danger'>
            Debes agregar un comentario
          </div>
        )}
        <br />
        <form className='control'>
          <textarea
            className='textarea textarea--margin-bottom'
            aria-labelledby='Enviar comentario sobre el periodo, presiona Enter para enviar.'
            placeholder='Enviar comentario sobre el periodo.'
            minLength='1'
            required
            maxLength='1000'
            id='comment'
            disabled={loading}
            ref={this.textMessage}
            spellCheck
            autoFocus
          />
          <div className='justify-between'>
            <Button
              buttonStyle='primary'
              icon='send'
              onClick={this.submitMessage}
              loading={loading}
            >
              Enviar
            </Button>
          </div>
        </form>
        <div>
          <p className='comment-counter'>
            Comentarios ({messages.length}
            ):
          </p>
          <ul className='clientComments'>
            {messages.map(message => {
              return <ReadMessage {...message} key={message.id} />
            })}
          </ul>
        </div>
        <Paginator
          totalPages={pagination.total_pages}
          location={this.props.location}
        />
      </div>
    )
  }
}

export default Messages
