import React, { Component, Fragment } from 'react'
import { Link } from 'react-router'
import accounting from 'accounting'
import classNames from 'classnames'
import moment from 'moment'

import DashboardActions from 'actions/dashboard'
import DashboardStore from 'stores/dashboard'
import Chart from 'shared/chart'
import Card from 'shared/card'
import Button from 'shared/button'
import StatusIcon from 'shared/statusIcon'
import CredentialsExpire from 'shared/credentials'
import Modal from 'shared/modal'
import {
  prettyDate,
  resetScroll,
  getClientsPlan,
  askRemainingPlanDays,
  handleModals
} from 'utils'
import { INVOICE_PAYMENT_STATUS, MONTHS, CHART_PERIODS } from 'shared/catalogs'
import BankStatements from './bankStatements'
import { modalPlans } from '../viewInvoice/utilsRestrictions'

import SessionStore from 'stores/session'

const CHARTS = 'chartData'
const INCOMES = 'incomes'
const EXPENSES = 'expenses'
const CHART_PERIOD_KEY = 'period'
const MONTHS_SHORT = MONTHS.map(m => m.label.substr(0, 3))

const getCurrentState = () => {
  return {
    currentEntity: SessionStore.getCurrentEntity(),
    [CHARTS]: DashboardStore.getChartData(),
    [INCOMES]: DashboardStore.getRecentIncomes(),
    [EXPENSES]: DashboardStore.getRecentExpenses()
  }
}

class Dashboard extends Component {
  constructor () {
    super()
    this.data = {
      [CHART_PERIOD_KEY]: 'semester'
    }
    this.state = {
      ...this.data,
      ...getCurrentState(),
      modalOpen: true,
      showBannerModal: true
    }
    this._onChange = this._onChange.bind(this)
  }

  componentDidMount () {
    SessionStore.addChangeListener(this._onChange)
    DashboardStore.addChangeListener(this._onChange)
    this.loadData()
    askRemainingPlanDays()
    resetScroll()
    this.setPlanModal()
  }

  componentWillUnmount () {
    SessionStore.removeChangeListener(this._onChange)
    DashboardStore.removeChangeListener(this._onChange)
    this.handleModalPlan()
  }

  _onChange () {
    const state = getCurrentState()
    this.setState(state)
  }

  loadData () {
    DashboardActions.getRecentIncomes()
    DashboardActions.getRecentExpenses()
    DashboardActions.getChartData({ period: this.state[CHART_PERIOD_KEY] })
  }

  setInfoSurvey = () => {
    const windowGlobal = typeof window !== 'undefined' && window

    windowGlobal.localStorage.setItem('answeredSurvey', true)
    this.toggleSatisfactionSurvey()
  }

  toggleSatisfactionSurvey = () => {
    this.setState(state => ({
      modalOpen: !state.modalOpen
    }))
  }

  onChange (field, e) {
    this.setState({ [field]: e.target.value }, () => {
      if (field === CHART_PERIOD_KEY) this.loadData()
    })
  }

  onChangePeriod (periodId) {
    this.setState({ [CHART_PERIOD_KEY]: periodId }, () => {
      DashboardActions.getChartData({ period: this.state[CHART_PERIOD_KEY] })
    })
  }

  setPlanModal = () => {
    const { dayBefore, sawPlanModal } = window.localStorage
    const currentDay = moment().format('YYYY-MM-DD')
    if (dayBefore) {
      if (dayBefore !== currentDay && sawPlanModal === 'viewed') {
        window.localStorage.setItem('sawPlanModal', 'not-seen')
      }
    } else {
      window.localStorage.setItem('dayBefore', currentDay)
      window.localStorage.setItem('sawPlanModal', 'not-seen')
    }
  }

  handleModalPlan = () => {
    const currentDay = moment().format('YYYY-MM-DD')
    window.localStorage.setItem('sawPlanModal', 'viewed')
    window.localStorage.setItem('dayBefore', currentDay)
    handleModals('showBannerModal', this)
  }

  renderModalSatisfactionSurvey () {
    const { modalOpen } = this.state
    const taxableEntities = SessionStore.getTaxableEntities()
    const entity = taxableEntities.find(entity => entity)

    return (
      <Modal
        isActive={modalOpen}
        toggleModal={this.toggleSatisfactionSurvey}
        noFooter
        isAlert
      >
        <div className='columns referral-promo'>
          <div className='column is-12'>
            <p className='referral-promo__title brake-line'>
              <span>En Enconta tú eres nuestra prioridad </span>
            </p>
            <div className='columns is-centered'>
              <div className='column is-6'>
                <img
                  src={`img/contabilidad.svg`}
                  alt={'Promoción de referidos'}
                  className='referral-promo__img'
                />
              </div>
            </div>
            <p className='referral-promo__content brake-line'>
              <span>
                Es por eso que te pedimos apoyo para responder nuestra breve
                encuesta, la cual no te tomará más de 1 minuto. Esto con el fin
                de mejorar nuestro servicio y tu experiencia como cliente.
              </span>
            </p>
            <Link
              to={`https://docs.google.com/forms/d/e/1FAIpQLSeXTL2M3ZwIemcZ-itGdDTdoyP5VxGE-ZmwjN9-KlXSvI6u_w/viewform?entry.1384753369=${entity &&
                entity.rfc}`}
              className='referral-promo__link'
              target={'_blank'}
              onClick={this.setInfoSurvey}
            >
              Valora nuestro servicio
            </Link>
          </div>
        </div>
      </Modal>
    )
  }

  renderTable (tableName) {
    const data = this.state[tableName]
    let lowStatusWord = ''
    let capStatusWord = ''
    if (tableName === INCOMES) {
      lowStatusWord = 'cobrar'
      capStatusWord = 'Cobrada'
    }
    if (tableName === EXPENSES) {
      lowStatusWord = 'pagar'
      capStatusWord = 'Pagada'
    }
    const tableBody = data.map((invoice, index) => {
      if (index < 5) {
        return (
          <tr key={invoice.id} className='cursor-pointer'>
            <td className='align-left'>
              <Link to={`/facturas/ver/${invoice.id}`}>
                {invoice.legal_name}
              </Link>
            </td>
            <td>{prettyDate(invoice.issue_date, false, true, false, '/')}</td>
            <td className='is-money'>
              {accounting.formatMoney(invoice.subtotal, '')}
            </td>
            <td>
              <StatusIcon
                className={INVOICE_PAYMENT_STATUS[invoice.payment_status]}
              />
            </td>
          </tr>
        )
      }
    })
    if (this.state[tableName].length > 0) {
      return (
        <div>
          <div className='flex-column'>
            <div className='caption flex-end'>
              <span>
                <StatusIcon className='warning' />
                Por {lowStatusWord}
              </span>
              <span style={{ marginLeft: '1.6rem' }}>
                <StatusIcon className='info' />
                {capStatusWord} parcialmente
              </span>
              <span style={{ marginLeft: '1.6rem' }}>
                <StatusIcon className='success' />
                {capStatusWord}
              </span>
              <span style={{ marginLeft: '1.6rem' }}>
                <StatusIcon className='danger' />
                Cancelada
              </span>
            </div>
          </div>
          <table className='table'>
            <thead>
              <tr>
                <th className='align-left'>RAZÓN SOCIAL</th>
                <th style={{ width: '6rem' }}>FECHA</th>
                <th style={{ width: '9rem' }}>SUBTOTAL</th>
                <th style={{ width: '5rem' }}>Estado</th>
              </tr>
            </thead>
            <tbody>{tableBody}</tbody>
          </table>
        </div>
      )
    }
    return (
      <div className='block unselectable'>
        <p>No hay facturas</p>
      </div>
    )
  }

  renderChart () {
    // bars prop passed to Chart must be an array of objects with:
    // dataName: the label to be shown to the user
    // dataKey: the data object key name to get the actual data for each bar
    // fill: the color for that kind of bar
    // all of this could change, this is a demo so it's to be defined
    const bars = [
      { dataName: 'Ingreso', dataKey: 'revenue', fill: '#00BCB4' },
      { dataName: 'Gasto', dataKey: 'expense', fill: '#4184F5' }
    ]

    const parsedChartData = this.state[CHARTS].map(item =>
      Object.assign({}, item, {
        month: MONTHS_SHORT[item.month],
        expense: Math.ceil(item.expense),
        revenue: Math.ceil(item.revenue)
      })
    )

    if (parsedChartData.length > 0) {
      return (
        <div>
          <div className='espacio-medium'>
            <div className='tabs is-toggle is-small'>
              <ul>
                {CHART_PERIODS.map((period, i) => (
                  <li
                    className={classNames({
                      'is-active': this.state[CHART_PERIOD_KEY] === period.id
                    })}
                    key={i}
                  >
                    <button onClick={this.onChangePeriod.bind(this, period.id)}>
                      <span>{period.label}</span>
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          </div>
          <Chart data={parsedChartData} bars={bars} />
        </div>
      )
    }
    return <p>Cargando datos...</p>
  }

  render () {
    const windowGlobal = typeof window !== 'undefined' && window
    const { answeredSurvey, sawPlanModal } = windowGlobal.localStorage
    const plan = getClientsPlan()
    const isShowedPlans =
      (plan === 'free' || plan === 'free_premium') &&
      sawPlanModal === 'not-seen'
    const showModalSurvey = moment().date() >= 23

    return (
      <Fragment>
        {isShowedPlans && modalPlans(this)}
        <CredentialsExpire ciec fiel />
        <BankStatements />
        <hr />
        <div className='columns'>
          <div className='column'>
            <Card title='Ingresos y Gastos Mensuales'>
              {this.renderChart()}
            </Card>
          </div>
        </div>
        <div className='columns'>
          <div className='column'>
            <Card
              className='is-fullheight flex-column'
              title='Últimos Ingresos'
            >
              {this.renderTable(INCOMES)}
              <div>
                <Link to='/ingresos'>
                  <Button buttonStyle='link' className='is-pulled-right'>
                    Ver todos
                  </Button>
                </Link>
              </div>
            </Card>
          </div>
          <div className='column'>
            <Card className='is-fullheight flex-column' title='Últimos Gastos'>
              {this.renderTable(EXPENSES)}
              <div>
                <Link to='/gastos'>
                  <Button buttonStyle='link' className='is-pulled-right'>
                    Ver todos
                  </Button>
                </Link>
              </div>
            </Card>
          </div>
          {showModalSurvey &&
            !answeredSurvey &&
            this.renderModalSatisfactionSurvey()}
        </div>
      </Fragment>
    )
  }
}

export default Dashboard
