import React, { Component } from 'react'
import { browserHistory, Link } from 'react-router'
import DocumentTitle from 'react-document-title'
import update from 'immutability-helper'
import BrowserDetection from 'react-browser-detection'

import SessionActions from 'actions/session'
import NotificationsActions from 'actions/notifications'
import SessionStore from 'stores/session'
import Logo from 'shared/logo'
import TextField from 'shared/textField'
import Button from 'shared/button'
import NotificationsContainer from 'shared/notifications'
import LogRocket from 'logrocket'

const requiredFields = new Set()

// Handle browser detection
const browserHandler = {
  chrome: () => null,
  firefox: () => null,
  safari: () => null,
  default: () => (
    <p className='browserHandler espacio-medium'>
      Te recomendamos utilizar{' '}
      <a
        href='https://www.google.com.mx/intl/es-419/chrome/browser/desktop/'
        target='_blank'
      >
        Google Chrome
      </a>{' '}
      para tener una mejor experiencia.
    </p>
  )
}

class Login extends Component {
  constructor (props) {
    super(props)
    this.title = 'Ingresar - Enconta'
    this.data = {
      requestInProgress: false,
      user: {
        email: '',
        password: ''
      }
    }
    this.state = Object.assign({}, this.data)
  }

  // If there's an active session send the user to dashboard
  componentDidMount () {
    if (SessionActions.hasSession()) {
      browserHistory.replace(SessionStore.getPreloginPage())
    }
  }

  componentWillUnmount () {
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  // Update User Email
  onChange (field, e) {
    this.setState({
      user: update(this.state.user, { [field]: { $set: e.target.value } })
    })
  }

  // Iterates over required fields and validates every one of them
  // Then it returns true if all validations succeedeed, else false.
  validateAll () {
    return [...requiredFields] // Converts the Set() into an array
      .filter(Boolean) // Filters the array from any falsy value
      .map(form => form.validate.call(this)) // Calls the validate() method on each form
      .every(e => e) // Checks if all validations suceeded
  }

  logRocket () {
    const user = SessionStore.getUser()
    const userId = user.id.toString()
    LogRocket.identify(userId, {
      name: user.name,
      email: user.email
    })
  }

  // Login attempt
  sendData = event => {
    event.preventDefault()
    let { user } = this.state
    if (this.validateAll() && !this.state.requestInProgress) {
      // requestInProgress disables submit button, prevents duplicate requests
      this.setState(
        {
          requestInProgress: true
        },
        () => {
          SessionActions.login(user)
            .then(response => {
              this.setState({ requestInProgress: false })

              if (response === 'success') {
                browserHistory.push(SessionStore.getPreloginPage())
                this.logRocket()
              }

              response === 'unauthorized' && browserHistory.push('/403')
            })
            .catch(err => {
              this.setState({ requestInProgress: false })
              NotificationsActions.error(err)
            })
        }
      )
    }
  }

  renderBox () {
    const { user, requestInProgress } = this.state
    const { password, email } = user
    return (
      <div className='login-box'>
        <Logo logoSize={'normal'} />
        <form className='login-box__form' onSubmit={this.sendData}>
          <BrowserDetection>{browserHandler}</BrowserDetection>
          <TextField
            className='login-box__input'
            label='Correo electrónico'
            type='email'
            value={email}
            onChange={this.onChange.bind(this, 'email')}
            ref={r => requiredFields.add(r)}
            required
          />
          <TextField
            className='login-box__input'
            label='Contraseña'
            type='password'
            value={password}
            onChange={this.onChange.bind(this, 'password')}
            ref={r => requiredFields.add(r)}
            required
          />
          <Button
            buttonStyle='primary'
            className='is-fullwidth login-box__input'
            type='submit'
            loading={requestInProgress}
          >
            Ingresar
          </Button>
          <Link
            className='encontaButton link is-centered login-box__input login-box__input--forgot-password'
            to='/recuperar-contrasena'
          >
            ¿Olvidaste tu contraseña?
          </Link>
        </form>
      </div>
    )
  }

  // Renders the main view
  render () {
    return (
      <DocumentTitle title={this.title}>
        <div className='login-page'>
          <div className='login-box__background-vectors' />
          {this.renderBox()}
          <NotificationsContainer />
        </div>
      </DocumentTitle>
    )
  }
}

export default Login
