// Libraries
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import moment from 'moment'

// Catalogs
import { MONTHS } from './../catalogs'
import SelectField from '../selectField/index'

// Constants
const requiredFields = new Set()

/*
 * The MonthPicker is designed to render 2 selects, one for months (from 0 to 12) and one for years
 * Saves the values on its state
 * By default, sets the years and months to the current time on component mount (usually on a new page render)
 */

class MonthPicker extends Component {
  constructor (props) {
    super(props)
    this.data = {
      currentYear: moment(props.selected, 'YYYY-MM-DD').format('YYYY'),
      currentMonth: moment(props.selected, 'YYYY-MM-DD').format('MM')
    }
    this.state = Object.assign({}, this.data)
  }

  componentWillUnmount () {
    // Clears the stored requiredFields
    requiredFields.clear()
  }

  // Returns an Array with objects to build the Years options for the SelectField component
  buildYears () {
    const currentYear = moment().year()
    return [...new Array(currentYear - 2011)].map((x, i) => ({
      id: `${currentYear - i}`,
      label: `${currentYear - i}`
    }))
  }

  // Saves selected year to state
  setYear (e) {
    let year = e.target.value
    this.setState({ currentYear: year })
    this.propsChange(`${year}-${this.state.currentMonth}-01`)
  }

  // Saves selected month to state
  setMonth (e) {
    let month = e.target.value
    this.setState({ currentMonth: month })
    this.propsChange(`${this.state.currentYear}-${month}-01`)
  }

  // Sends the updated data to the parent Component so it can perform its functions
  // and usually pass the updated data via props
  propsChange (date) {
    this.props.onChange(date)
  }

  // Main render
  render () {
    return (
      <div className={this.props.className}>
        <label className='caption'>{this.props.label}</label>
        <div className='columns'>
          <div className='column is-3'>
            <SelectField
              options={this.buildYears()}
              value={this.state.currentYear}
              onChange={this.setYear.bind(this)}
              placeholder='AÃ±o'
              ref={r => requiredFields.add(r)}
              required
            />
          </div>
          <div className='column is-4'>
            <SelectField
              options={MONTHS}
              onChange={this.setMonth.bind(this)}
              value={this.state.currentMonth}
              placeholder='Mes'
              ref={r => requiredFields.add(r)}
              required
            />
          </div>
        </div>
      </div>
    )
  }
}

MonthPicker.propTypes = {
  selected: PropTypes.string,
  onChange: PropTypes.func,
  className: PropTypes.string,
  label: PropTypes.string
}

MonthPicker.defaultProps = {
  selected: moment().format('YYYY-MM-DD')
}

export default MonthPicker
