import React, { Component } from 'react'
import { prettyDate, formatCurrency } from 'utils'
import pluralize from 'pluralize'

export default class Deposits extends Component {
  renderContent (calculation) {
    return (
      <tbody>
        {calculation.deposits.map(deposit => {
          return (
            <tr key={deposit.id}>
              <td className='has-text-left small-padding'>
                {formatCurrency(deposit.amount)}
              </td>
              <td className='is-uppercase small-padding'>
                {deposit.description}
              </td>
              <td className='is-uppercase small-padding'>
                {prettyDate(deposit.date, '', true, false, '-')}
              </td>
              <td className='has-text-right small-padding'>
                {`${deposit.bank_name.toUpperCase()} - ${
                  deposit.account_number
                }`}
              </td>
            </tr>
          )
        })}
      </tbody>
    )
  }

  // Main render
  render () {
    const { calculation } = this.props
    return (
      <div className='section section--is-small'>
        <div className='columns is-centered'>
          <div className='column is-11'>
            <p className='caption'>
              {`Hay ${calculation.deposits.length}
              ${pluralize('depósito', calculation.deposits.length)}`}
            </p>
            <table className='table table--calculation-table is-borderless'>
              <thead>
                <tr>
                  <th className='has-text-left padding-bottom-less'>Monto</th>
                  <th className='padding-bottom-less'>Descripción</th>
                  <th className='padding-bottom-less'>Fecha</th>
                  <th className='has-text-right padding-bottom-less'>
                    Cuenta bancaria
                  </th>
                </tr>
              </thead>
              {this.renderContent(calculation)}
            </table>
          </div>
        </div>
      </div>
    )
  }
}
