import React, { PureComponent } from 'react'
import PropTypes from 'prop-types'
import { prettyDate } from 'utils'
import classNames from 'classnames'

class ReadMessage extends PureComponent {
  static propTypes = {
    message: PropTypes.string.isRequired,
    user: PropTypes.object.isRequired,
    created_at: PropTypes.string.isRequired
  }

  getTypeUser = user => {
    const { email } = user
    const patt = /@enconta.com/
    return email.match(patt)
  }
  render () {
    const { message, user, created_at: createdAt } = this.props

    return (
      <li className='clientComments__container-comment'>
        <div>
          <p>
            <span
              className={classNames('clientComments--user', {
                'clientComments--user--client': this.getTypeUser(user)
              })}
            >
              {user.name}
            </span>{' '}
            <span className='clientComments--date'>
              {prettyDate(createdAt)}
            </span>
          </p>
          <p className='clientComments--commentText'>{message.trim()}</p>
        </div>
      </li>
    )
  }
}

export default ReadMessage
