import React, { PureComponent } from 'react'
import PropTypes from 'prop-types'
import Select from 'shared/selectField'

import { MONTHS, YEARS } from 'shared/catalogs'
import {
  RANGE_KEY,
  PERIOD_KEY,
  MONTH_KEY,
  YEAR_KEY,
  RANGE,
  PERIODS
} from '../../constants'

class RankingFilters extends PureComponent {
  render () {
    const { filters, onChangeFilter } = this.props
    return (
      <div className='rubrosChart__filters rubrosChart__filters-wrap'>
        <div className='rubrosChart__filters rubrosChart__filters-filter'>
          <Select
            label='Periodo'
            options={PERIODS}
            value={filters[PERIOD_KEY]}
            onChange={e => onChangeFilter(PERIOD_KEY, e.currentTarget.value)}
          />
        </div>

        <div className='rubrosChart__filters rubrosChart__filters-filter'>
          <Select
            label='Mes final'
            options={MONTHS}
            value={filters[MONTH_KEY]}
            onChange={e => onChangeFilter(MONTH_KEY, e.currentTarget.value)}
          />
        </div>

        <div className='rubrosChart__filters rubrosChart__filters-filter'>
          <Select
            label='AÃ±o'
            options={YEARS}
            value={filters[YEAR_KEY]}
            onChange={e => onChangeFilter(YEAR_KEY, e.currentTarget.value)}
          />
        </div>

        <div className='rubrosChart__filters rubrosChart__filters-filter'>
          <Select
            label='Comparar con'
            options={RANGE}
            value={filters[RANGE_KEY]}
            onChange={e => onChangeFilter(RANGE_KEY, e.currentTarget.value)}
          />
        </div>
      </div>
    )
  }
}

RankingFilters.propTypes = {
  filters: PropTypes.object.isRequired,
  onChangeFilter: PropTypes.func.isRequired
}

export default RankingFilters
