// Libraries
import React from 'react'
import PropTypes from 'prop-types'
import accounting from 'accounting'

// Components
import { formatCurrency } from 'utils'
import Tooltip from 'shared/tooltip'

const RenderDocument = ({ paymentDetails: { payments } }) => {
  return (
    <tbody>
      {payments &&
        payments.map(payment => {
          if (payment.related_documents) {
            return payment.related_documents.map(relatedDocument => {
              // if currency is 'MXN', exchange_rate is null and must be 1
              const exchangeRate = relatedDocument.exchange_rate
                ? relatedDocument.exchange_rate
                : 1
              return (
                <tr key={relatedDocument._id}>
                  <td className='align-left' style={{ width: '15%' }}>
                    {relatedDocument.document_id}
                  </td>
                  <td>{relatedDocument.currency}</td>
                  <td>{formatCurrency(exchangeRate)}</td>
                  <td>
                    <Tooltip tooltip='Pago en parcialidades o diferido'>
                      {relatedDocument.payment_method}
                    </Tooltip>
                  </td>
                  <td>{relatedDocument.partiality_mumber}</td>
                  <td>
                    {accounting.formatMoney(
                      relatedDocument.previous_balance_cents / 100,
                      '$ ',
                      2
                    )}
                  </td>
                  <td>
                    {accounting.formatMoney(
                      relatedDocument.amount_paid_cents / 100,
                      '$ ',
                      2
                    )}
                  </td>
                  <td className='padding-right'>
                    {accounting.formatMoney(
                      relatedDocument.amount_outstanding_cents / 100,
                      '$ ',
                      2
                    )}
                  </td>
                </tr>
              )
            })
          } else {
            return (
              <tr>
                <td colSpan={8}>No hay información de pagos</td>
              </tr>
            )
          }
        })}
    </tbody>
  )
}

export const PaymentComplement = ({ paymentDetails }) => {
  return (
    <div className='column'>
      <h4 className='subheader-green test'>Pagos</h4>
      <table className='table is-striped is-bordered'>
        <thead>
          <tr>
            <th className='align-left'>Factura Original</th>
            <th>Moneda</th>
            <th>Tipo de cambio</th>
            <th>Metodo de pago</th>
            <th>Parcialidad</th>
            <th>Saldo Anterior</th>
            <th>Cantidad pagada</th>
            <th className='padding-right'>Restante</th>
          </tr>
        </thead>
        <RenderDocument paymentDetails={paymentDetails} />
      </table>
    </div>
  )
}

export default PaymentComplement

PaymentComplement.defaultProps = {
  paymentDetail: []
}

PaymentComplement.propTypes = {
  paymentDetail: PropTypes.array.isRequired
}
