// Libraries
import React from 'react'
import PropTypes from 'prop-types'

// Render sigle item
const renderItem = (name, parent, item) => {
  let renderItem = null
  if (typeof parent[item] !== 'undefined' && parent[item] !== null) {
    renderItem = (
      <p>
        <label className='caption'>{name}</label>
        <br />
        <span className='body'>{parent[item]}</span>
      </p>
    )
  }
  return renderItem
}

// Render Recipient for invoice with version 3.2
const render32 = recipient => {
  return (
    <div className='content'>
      <p>
        <span className='title'>{recipient.name}</span>
      </p>
      {renderItem('RFC', recipient, 'rfc')}
    </div>
  )
}

// Render Recipient for invoice with version 3.3
// TODO: Agregar régimen fiscal
const render33 = recipient => {
  return (
    <div className='content'>
      <p>
        <span className='title'>{recipient.name}</span>
      </p>
      {renderItem('RFC', recipient, 'rfc')}
      {renderItem('País', recipient, 'tax_residency')}
    </div>
  )
}

const Recipient = ({ invoice }) => {
  let issuer = (
    <div className='block unselectable'>
      <p>No hay datos de emisor</p>
    </div>
  )
  switch (invoice.version) {
    case '3.2':
      issuer = render32(invoice.recipient)
      break
    case '3.3':
      issuer = render33(invoice.recipient)
      break
  }
  return (
    <div className='column is-half'>
      <h4 className='subheader-green'>Receptor</h4>
      {issuer}
    </div>
  )
}

Recipient.propTypes = {
  invoice: PropTypes.object
}

export default Recipient
